INSERT INTO `danhmuc_tour` (`id`, `name`, `name_symbol`, `name_url`, `img`, `icon`, `position`, `content_short`, `title`, `keyword`, `description`) VALUES
(1, 'Du lịch Anh', 'Đồng hồ Big Ben', 'du-lich-anh', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/anh/du-lich-anh-mixtourist.jpg', '/view/admin/Themes/kcfinder/upload/images/danhmuctour/icon-anh.png', 1, '<p style="text-align: justify;">\r\n	<span style="color:#ffffff;"><span style="font-family: Tahoma; font-size: 14px; text-align: justify;">L&agrave; một trong những trung t&acirc;m kinh tế, văn h&oacute;a lớn nhất thế giới, sở hữu nhiều c&ocirc;ng tr&igrave;nh nổi tiếng được t&ocirc;n vinh l&agrave; di sản thế giới, nước Anh l&agrave; điểm đến mơ ước của những ai y&ecirc;u kh&aacute;m ph&aacute;. H&atilde;y c&ugrave;ng</span><span style="font-family: Tahoma; font-size: 14px; text-align: justify;">&nbsp;</span><span style="box-sizing: border-box; font-weight: 700; font-family: Tahoma; font-size: 14px; text-align: justify;">Mix Tourist</span><span style="font-family: Tahoma; font-size: 14px; text-align: justify;">&nbsp;đi dạo một v&ograve;ng xứ sở sương m&ugrave; v&agrave; chi&ecirc;m ngưỡng rất nhiều vẻ đẹp hiếm c&oacute; kh&oacute; c&oacute; thể t&igrave;m kiếm được ở một nơi n&agrave;o kh&aacute;c tr&ecirc;n thế giới nh&eacute;!</span></span></p>\r\n', 'Du lịch Anh', 'du lịch anh, tour du lịch anh, du lich anh, tour du lich anh, du lịch anh quốc, du lich anh quoc', 'MixTourist chuyên tour du lịch châu âu: anh, phap, bỉ, hà lan, đức, ý, tây ban nha, thụy sĩ, scotland,...'),
(2, 'Du lịch Pháp', 'Tháp Eiffel', 'du-lich-phap', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/du-lich-phap1-mixtourist.jpg', '/view/admin/Themes/kcfinder/upload/images/danhmuctour/icon-phap.png', 2, '<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">Nước Ph&aacute;p, đất nước được nhiều người cho rằng l&agrave; nơi l&atilde;ng mạn nhất thế giới, quốc gia rất gi&agrave;u truyền thống văn h&oacute;a c&ugrave;ng bề d&agrave;y lịch sử. </span></p>\r\n<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">Kh&ocirc;ng chỉ hấp dẫn kh&aacute;ch du lịch bởi những c&ocirc;ng tr&igrave;nh kiến tr&uacute;c độc đ&aacute;o, ấn tượng c&oacute; tầm ảnh hưởng to&agrave;n thế giới như th&aacute;p Eiffel, Nh&agrave; thời Đức B&agrave; Paris nguy nga, cổ k&iacute;nh m&agrave; Ph&aacute;p c&ograve;n hội tụ đầy đủ từ những cảnh quan h&ugrave;ng vĩ như đỉnh Chamonix Mont Blanc hay hẻm n&uacute;i Verdon, Grand Canyon đến những phong cảnh thi&ecirc;n nhi&ecirc;n thơ mộng như c&aacute;nh đồng của lo&agrave;i hoa oải hương thơm ng&aacute;t, t&iacute;m biếc đầy m&ecirc; hoặc, d&ograve;ng s&ocirc;ng&nbsp;Seine y&ecirc;n b&igrave;nh v&agrave; đầy l&atilde;ng mạn. </span></p>\r\n<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">C&ograve;n g&igrave; tuyệt vời hơn nếu bạn được thưởng thức những m&oacute;n ăn ẩm thực đặc trưng nước Ph&aacute;p b&ecirc;n cạnh d&ograve;ng s&ocirc;ng&nbsp;Seine hiền h&ograve;a c&ugrave;ng với người th&acirc;n y&ecirc;u của m&igrave;nh. H&atilde;y để MixTourist gi&uacute;p bạn l&agrave;m điều đ&oacute; nh&eacute;!</span></p>\r\n', 'Du lịch Pháp', 'du lịch pháp, tour du lịch pháp, du lich pháp, tour du lich pháp', 'MixTourist chuyên tour du lịch châu âu: anh, pháp, bỉ, hà lan, đức, ý, tây ban nha, thụy sĩ, scotland,...'),
(3, 'Du lịch Đức', 'Cổng Brandenburg', 'du-lich-duc', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/canh-cong-Brandenburg-du-lich-duc-mixtourist.jpg', '/view/admin/Themes/kcfinder/upload/images/danhmuctour/icon-duc.png', 3, '<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">Cộng ho&agrave; Li&ecirc;n Bang Đức nằm ở trung t&acirc;m Ch&acirc;u &Acirc;u với 16 tiểu bang tiếp gi&aacute;p với 9 nước l&aacute;ng giềng. B&ecirc;n cạnh đ&oacute;, Đức c&ograve;n l&agrave; trung t&acirc;m kinh tế quan trọng của Ch&acirc;u &Acirc;u với nền c&ocirc;ng nghiệp v&agrave; nền kinh tế ph&aacute;t triển. Đ&acirc;y l&agrave; nơi hấp dẫn đối với sinh vi&ecirc;n quốc tế.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">Đức c&oacute; lịch sử ph&aacute;t triển l&acirc;u đời ch&iacute;nh v&igrave; thế nền văn ho&aacute; v&agrave; con người Đức lu&ocirc;n to&aacute;t l&ecirc;n vẻ lịch l&atilde;m, hiếu kh&aacute;ch. Đến với nước Đức bạn sẽ được học tập trong một m&ocirc;i trường trong s&aacute;ng, s&aacute;t với thực tế cuộc sống. Trong thời gian thực tập bạn c&oacute; nhiều cơ hội sang thăm c&aacute;c nước Ch&acirc;u &Acirc;u v&agrave; t&igrave;m hiểu về nền văn ho&aacute; Ch&acirc;u &Acirc;u.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">L&agrave; một trung t&acirc;m nhạc cổ điển với nhiều nhạc sỹ lừng danh, đ&acirc;y l&agrave; v&ugrave;ng đất của Goeth v&agrave; Beethoven. Ch&iacute;nh v&igrave; thế tại Đức thường tổ chức thường xuy&ecirc;n c&aacute;c buổi h&ograve;a nhạc hay c&aacute;c lễ hội &acirc;m nhạc, mặc d&ugrave; c&oacute; rất nhiều chương tr&igrave;nh Techno-rung Berlin v&agrave; lễ hội nhạc rock được biểu diễn h&agrave;ng năm nhưng b&ecirc;n cạnh đ&oacute; vẫn c&ograve;n c&aacute;c chương tr&igrave;nh nghệ thuật v&agrave; c&aacute;c buổi h&ograve;a nhạc cổ điển được tổ chức tại c&aacute;c nh&agrave; h&aacute;t lớn.</span></p>\r\n', 'Du lịch Đức', 'du lịch Đức, tour du lịch Đức, du lich Đức, tour du lich Đức', 'MixTourist chuyên tour du lịch châu âu: anh, pháp, bỉ, hà lan, đức, ý, tây ban nha, thụy sĩ, scotland,...'),
(4, 'Du lịch Ý', 'Tháp nghiêng Pisa', 'du-lich-y', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/du-lich-nuoc-y-mixtourist.jpg', '/view/admin/Themes/kcfinder/upload/images/danhmuctour/icon-y.png', 4, '<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">&Yacute; l&agrave; thi&ecirc;n đường nghệ thuật, ẩm thực, lịch sử, thời trang v&agrave; văn h&oacute;a. &Yacute; sở hữu bờ biển, v&ugrave;ng đồi n&uacute;i tuyệt đẹp v&agrave; c&oacute; nhiều di sản thế giới nhất tr&ecirc;n thế giới. </span></p>\r\n<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">9 th&agrave;nh phố danh tiếng của &Yacute; l&agrave; Roma trung t&acirc;m La M&atilde; cổ, Bologna, Firenze, Genova hải cảng, Milano thời trang, Napoli cổ k&iacute;nh, Pisa, Torino, Venice th&agrave;nh phố t&igrave;nh y&ecirc;u. </span></p>\r\n<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">H&atilde;y du lịch &Yacute; c&ugrave;ng MixTourist để tận hưởng c&aacute;c dịch vụ tốt nhất.</span></p>\r\n', 'Du lịch Ý', 'du lịch ý, tour du lịch ý, du lich y, tour du lich y, du lịch italia, du lich italia', 'MixTourist chuyên tour du lịch châu âu: anh, pháp, bỉ, hà lan, đức, ý, tây ban nha, thụy sĩ, scotland,...'),
(5, 'Du lịch Hà Lan', 'Cối xay gió', 'du-lich-ha-lan', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/halan/du-lich-dat-nuoc-ha-lan-mixtourist.jpg', '/view/admin/Themes/kcfinder/upload/images/danhmuctour/icon-ha-lan.png', 5, '<p>\r\n	<span style="color:#ffffff;">Nằm ở ph&iacute;a t&acirc;y bắc của Ch&acirc;u &Acirc;u, gi&aacute;p với Bỉ với Đức v&agrave; hướng ra biển, H&agrave; Lan l&agrave; đất nước c&oacute; mật độ d&acirc;n số cao v&agrave; đất nước đang ở dưới mực nước biển.</span></p>\r\n<p>\r\n	<span style="color:#ffffff;">C&oacute; hơn 50% diện t&iacute;ch ở H&agrave; Lan dưới mực nước biển, nhưng người d&acirc;n lu&ocirc;n tự h&agrave;o v&igrave; những cảnh quan xinh đẹp với hoa Tulip, những cối xay gi&oacute; khổng lồ, những con đ&ecirc;, v&agrave; niềm tự h&agrave;o l&agrave; vương quốc xinh đẹp nhất thế giới.</span></p>\r\n<p>\r\n	<span style="color:#ffffff;">Ch&iacute;nh v&igrave; l&yacute; do đ&oacute; n&ecirc;n H&agrave; Lan c&oacute; một t&ecirc;n gọi kh&aacute;c m&agrave; du kh&aacute;ch thường hay đặt đ&oacute; l&agrave; &quot;đất nước mu&ocirc;n hoa&quot;.</span></p>\r\n', 'Du lịch Hà Lan', 'Du lịch Hà Lan, du lich ha lan, tour du lịch hà lan, tour du lich ha lan', 'MixTourist chuyên tour du lịch châu âu: anh, pháp, bỉ, hà lan, đức, ý, tây ban nha, thụy sĩ, scotland,...'),
(6, 'Du lịch Bỉ', 'Cậu bé đứng tè', 'du-lich-bi', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/bi/du-lich-bi-mixtourist.jpg', '/view/admin/Themes/kcfinder/upload/images/danhmuctour/icon-bi.png', 6, '<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">Brussels - thủ đ&ocirc;&nbsp;Vương quốc Bỉ, l&agrave; một trong những th&agrave;nh phố văn ho&aacute; cổ của ch&acirc;u &Acirc;u, nơi đặt trụ sở ch&iacute;nh của c&aacute;c cơ quan của ch&acirc;u &Acirc;u (EU, EC). V&igrave; vậy, một c&aacute;ch kh&ocirc;ng ch&iacute;nh thức, người ta gọi Brussels l&agrave; thủ đ&ocirc; của ch&acirc;u &Acirc;u.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">S&ocirc; c&ocirc; la, b&aacute;nh quế, bia v&agrave; kim cương l&agrave; những sản vật l&agrave;m n&ecirc;n &quot; thương hiệu&quot; cho du lịch Bỉ.</span></p>\r\n<div style="text-align: justify;">\r\n	<span style="color:#ffffff;">Điều đ&aacute;ng ngạc nhi&ecirc;n l&agrave; những sản phẩm tr&ecirc;n đều kh&ocirc;ng c&oacute; xuất xứ từ Bỉ nhưng những t&ecirc;n tuổi lớn th&igrave; đều nằm ở nước n&agrave;y. Hơn thế nữa những ng&ocirc;i l&agrave;ng đan xen n&eacute;t đẹp giữa qu&aacute; khứ v&agrave; hiện tại, b&igrave;nh d&acirc;n v&agrave; sang trọng sẽ khiến cho bạn kh&ocirc;ng một ch&uacute;t nuối tiếc về qu&atilde;ng thời gian đ&atilde; bỏ ra ở đất nước xinh đẹp n&agrave;y.</span></div>\r\n', 'Du lịch Bỉ', 'du lịch Bỉ, tour du lịch Bỉ, du lich bi, tour du lich bi', 'MixTourist chuyên tour du lịch châu âu: anh, pháp, bỉ, hà lan, đức, ý, tây ban nha, thụy sĩ, scotland,...'),
(7, 'Du lịch Thụy Sĩ', 'Núi Matterhorn', 'du-lich-thuy-si', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/thuysi/nui-matterhorn.jpg', '/view/admin/Themes/kcfinder/upload/images/danhmuctour/icon-thuy-si.png', 7, '<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">Đất nước Thụy Sĩ&nbsp;l&agrave; đất nước nhận được số điểm thấp nhất về bất ổn ch&iacute;nh trị. N&oacute; cũng nằm trong&nbsp;top c&aacute;c quốc gia h&ograve;a b&igrave;nh nhất tr&ecirc;n thế giới&nbsp;với mức độ tội phạm, bạo lực thấp. </span></p>\r\n<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">Được biết đến với t&iacute;nh trung lập trong vấn đề ch&iacute;nh trị trong khu vực, quốc tế v&agrave; to&agrave;n cầu, nhưng đất nước n&agrave;y vẫn duy tr&igrave; quan hệ ngoại giao mạnh mẽ với c&aacute;c quốc gia kh&aacute;c nhau tr&ecirc;n thế giới.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">N&oacute;i tới Thụy Sỹ, hẳn ai cũng nghĩ tới nền c&ocirc;ng nghiệp sản xuất đồng hồ nổi tiếng cũng như hệ thống ng&acirc;n h&agrave;ng uy t&iacute;n nhất thế giới. Đặc biệt, nơi đ&acirc;y l&agrave; một cơ sở v&ocirc; c&ugrave;ng quan trọng đối với một số tổ chức quốc tế quan trọng, như Hội Chữ Thập Đỏ, Tổ chức Thương Mại Thế Giới.</span></p>\r\n', 'Du lịch Thụy Sĩ', 'du lịch Thụy Sĩ, tour du lịch Thụy Sĩ, du lich Thuy Si, tour du lich thuy si', 'MixTourist chuyên tour du lịch châu âu: anh, pháp, bỉ, hà lan, đức, ý, tây ban nha, thụy sĩ, scotland,...'),
(8, 'Du lịch Tây Ban Nha', 'Nhà thờ Sagrada Familia', 'du-lich-tay-ban-nha', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/taybannha/du-lich-tay-ban-nha-mixtourist.jpg', '/view/admin/Themes/kcfinder/upload/images/danhmuctour/icon-tay-ban-nha.png', 8, '<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">Đất nước T&acirc;y Ban Nha được biết đến với điệu nhảy Flamenco, của những trận đấu b&ograve; t&oacute;t với niềm tin y&ecirc;u v&agrave;o cuộc sống. Tuy nhi&ecirc;n, hầu hết T&acirc;y Ban Nha được biết đến như một điểm đến du lịch nổi tiếng ch&acirc;u &Acirc;u.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">L&agrave; nước lớn thứ 2 T&acirc;y &Acirc;u, hầu hết mọi n&eacute;t văn h&oacute;a trong đời sống văn h&oacute;a của người TBN đều khởi nguồn từ di sản văn h&oacute;a La M&atilde; cổ đại, ch&iacute;nh đều n&agrave;y đ&atilde; gi&uacute;p TBN trở th&agrave;nh đất nước c&oacute; nền văn h&oacute;a Latinh tại Ch&acirc;u &Acirc;u.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">Ẩm thực TBN gắn liền với nguồn hải sản dồi d&agrave;o được cung cấp từ v&ugrave;ng biển Địa Trung Hải v&agrave; Đại T&acirc;y Dương.</span></p>\r\n', 'Du lịch Tây Ban Nha', 'du lịch Tây Ban Nha, tour du lịch Tây Ban Nha, du lich tay ban nha, tour du lich tay ban nha', 'MixTourist chuyên tour du lịch châu âu: anh, pháp, bỉ, hà lan, đức, ý, tây ban nha, thụy sĩ, scotland,...'),
(9, 'Du lịch Scotland', 'Kỳ Lân', 'du-lich-scotland', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/scotland/du-lich-scotland-mixtourist.jpg', '/view/admin/Themes/kcfinder/upload/images/danhmuctour/icon-scotland.png', 9, '<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">Với những ai th&iacute;ch kỳ nghỉ năng động với nhiều hoạt động v&agrave; trải nghiệm mới mẻ, h&atilde;y t&igrave;m một điểm đến độc đ&aacute;o v&agrave; th&uacute; vị như Scotland.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">Scotland l&agrave; một nh&agrave; nước c&oacute; chủ quyền trong Vương quốc Li&ecirc;n hiệp Anh v&agrave; Bắc Ireland. Mặc d&ugrave; l&agrave; một phần thuộc chế độ qu&acirc;n chủ lập hiến của Vương quốc Anh nhưng Scotland vẫn c&oacute; nền độc lập của m&igrave;nh.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="color:#ffffff;">Scotland l&agrave; một quốc gia nổi tiếng với những truyền thống d&acirc;n tộc v&agrave; văn h&oacute;a, &acirc;m nhạc, điệu nhảy độc đ&aacute;o, trang phục v&aacute;y kẻ v&agrave; rượu whisky. Lo&agrave;i động vật biểu tượng của Scotland l&agrave; kỳ l&acirc;n v&agrave; l&agrave; biểu trưng cho sự &acirc;n sủng, tinh khiết, an l&agrave;nh v&agrave; hạnh ph&uacute;c.</span></p>\r\n', 'Du lịch Scotland', 'scotland, du lịch scotland, tour du lịch scotland, tin tức scotland, hình ảnh scotland, khách sạn scotland, video scotland', 'MixTourist - Công ty du lịch hàng đầu Việt Nam chuyên tour du lịch châu Âu: du lịch scotland, tour du lịch scotland'),
(10, 'Du lịch Áo', 'Đại bàng đen', 'du-lich-ao', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/ao/du-lich-ao-mixtourist.jpg', '/view/admin/Themes/kcfinder/upload/images/danhmuctour/icon-ao.png', 0, '', 'Du lịch Áo cùng Mix Tourist', 'du lịch áo, tour du lịch áo, tin tức nước áo, hình ảnh nước áo, video nước áo, du lịch áo cùng mix tourist', 'Cùng Mix Tourist tham gia chuyến du lịch Áo và các nước châu Âu để khám phá nét cổ kính pha nét hiện đại của lục địa già của Thế giới'),
