INSERT INTO `news` (`id`, `danhmuc_id`, `name`, `name_url`, `img`, `view`, `content`, `title`, `keyword`, `description`, `created`) VALUES
(1, 1, 'Top 5 di sản thiên nhiên thế giới tuyệt vời nhất tại châu Âu', 'top-5-di-san-thien-nhien-the-gioi-tuyet-voi-nhat-tai-chau-au', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/taybannha/du-lich-tay-ban-nha-mixtourist.jpg', 577, '<p style="text-align: justify;">\r\n	<span style="font-size:14px;">Với rất nhiều quốc gia c&oacute; nền văn minh l&acirc;u đời v&agrave; một cuộc sống ph&aacute;t triển hiện đại...&nbsp;<strong>du lịch ch&acirc;u &Acirc;u</strong>&nbsp;thực sự trở điểm đến v&ocirc; c&ugrave;ng hấp dẫn đối với những ai y&ecirc;u th&iacute;ch du lịch v&agrave; kh&aacute;m ph&aacute;.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">Ch&acirc;u &Acirc;u được coi l&agrave; tr&aacute;i tim văn minh của nh&acirc;n loại, tuy x&atilde; hội đang tr&ecirc;n đ&agrave; ph&aacute;t triển vượt bậc nhưng ch&acirc;u &Acirc;u vẫn c&ograve;n giữ trong l&ograve;ng m&igrave;nh những đường n&eacute;t cổ điển v&agrave; sang trọng. Dưới đ&acirc;y l&agrave; những di sản&nbsp;thế giới&nbsp;tuyệt đẹp ở Ch&acirc;u &Acirc;u.</span></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:14px;">1. Th&agrave;nh phố Cinque Terre, Italy</span></h2>\r\n<p style="text-align: center;">\r\n	<span style="font-size:14px;"><img alt="Thành phố Cinque Terre, Italy" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/thanh-pho-cinque-Terre-Italy-mixtourist.jpg" style="width: 70%; height: 5%;" /></span></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">&gt;&gt;<em> Click xem v&agrave; đăng k&yacute; <a href="http://dulichchauau.org/du-lich-y/"><strong>tour du lịch &Yacute; gi&aacute; rẻ 2017</strong></a> ngay h&ocirc;m nay để nhận ưu đ&atilde;i lớn của <strong>MixTourist</strong></em></span></p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;"><strong>Cinque Terre</strong> nằm ở ph&iacute;a t&acirc;y bắc nước Italia, dọc theo bờ biển Ligurian. <strong>Cinque Terre</strong> bao&nbsp;gồm 5 ng&ocirc;i l&agrave;ng nhỏ, lần lượt từ Bắc xuống Nam l&agrave; Monterosso, Vernazza, Corniglia, Manarola v&agrave; Riomaggiore. Những ng&ocirc;i l&agrave;ng ở Cinque Terre l&agrave; kết quả h&agrave;ng ng&agrave;n năm lao động của người n&ocirc;ng d&acirc;n, đ&atilde; cố gắng biến những v&aacute;ch n&uacute;i dốc đứng ven biển th&agrave;nh những ruộng bậc thang trồng nho, v&agrave; x&acirc;y n&ecirc;n những ng&ocirc;i nh&agrave; nhỏ rực rỡ sắc m&agrave;u.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">Sự c&acirc;n bằng tinh tế giữa c&aacute;c yếu tố thi&ecirc;n nhi&ecirc;n, với những t&aacute;c động của con người, đ&atilde; tạo n&ecirc;n một vẻ đẹp ho&agrave;n hảo v&agrave; độc nhất cho Cinque Terre, đưa 5 ng&ocirc;i l&agrave;ng nhỏ v&agrave;o danh s&aacute;ch di sản thế giới của UNESCO. Đ&acirc;y l&agrave; nơi thu h&uacute;t rất đ&ocirc;ng kh&aacute;ch&nbsp;<strong>du lịch &Yacute;</strong>.</span></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:14px;">2. V&ugrave;ng n&uacute;i Jungfrau, Thụy Sĩ</span></h2>\r\n<p style="text-align: center;">\r\n	<span style="font-size:14px;"><img alt="Vùng núi Jungfrau, Thụy Sĩ" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/thuysi/vung-nui-Jungfrau-thuy-si-mixtourist.jpg" style="width: 70%; height: 5%;" /></span></p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">Jungfrau l&agrave; đỉnh cao nhất trong khối n&uacute;i c&ugrave;ng t&ecirc;n, nằm tại khu vực Bernese OberlandAnpơ Bern, nh&igrave;n xuống Wengen. Đỉnh n&uacute;i n&agrave;y được chinh phục lần đầu ti&ecirc;n v&agrave;o ng&agrave;y 3 th&aacute;ng 8 năm 1811 bởi anh em Meyer ở Aarau. Từng c&oacute; thời rất kh&oacute; tiếp cận, nhưng hiện nay đường sắt c&oacute; kh&iacute;a Jungfraubahn đ&atilde; chạy b&ecirc;n trong n&uacute;i l&ecirc;n tới nh&agrave; ga đường sắt Jungfraujoch ở cao độ 3.454 m (11.332 ft), rất thuận lợi cho những&nbsp;<strong>tour du lịch ch&acirc;u &Acirc;u</strong>&nbsp;đến tham quan.</span></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:14px;">3. Thung lũng Loire, Ph&aacute;p</span></h2>\r\n<p style="text-align: center;">\r\n	<span style="font-size:14px;"><img alt="Thung lũng Loire, Pháp" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/thung-lung-Loire-phap-mixtourist.jpg" style="width: 70%; height: 5%;" /></span></p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">Tại thung lũng s&ocirc;ng Loire c&ograve;n khoảng 42 l&acirc;u đ&agrave;i, phần lớn được x&acirc;y dựng từ thời Trung Cổ đến thế kỉ 15, khi trung t&acirc;m quyền lực của nước Ph&aacute;p nằm ở đ&acirc;y. Nhiều l&acirc;u đ&agrave;i c&oacute; kiến tr&uacute;c đặc trưng của thời k&igrave; Phục hưng.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">Ch&iacute;nh nhờ gi&aacute; trị văn h&oacute;a độc đ&aacute;o, đặc biệt l&agrave; gi&aacute; trị lịch sử v&agrave; kiến tr&uacute;c, m&agrave; năm 2000 phần thung lũng s&ocirc;ng Loire từ Sully-sur-Loire (thuộc tỉnh Loiret) đến Chalonnes-sur-Loire (thuộc tỉnh Maine-et-Loire) đ&atilde; được UNESCO c&ocirc;ng nhận l&agrave; Di sản thế giới.</span></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:14px;">4. H&ograve;n đảo n&uacute;i lửa Surtsey ở ph&iacute;a nam Iceland</span></h2>\r\n<p style="text-align: center;">\r\n	<span style="font-size:14px;"><img alt="Hòn đảo núi lửa Surtsey ở phía nam Iceland" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/iceland/hon-dao-nui-lua-surtsey-o-phia-nam-ice-land.jpg" style="width: 70%; height: 5%;" /></span></p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">L&agrave; đảo mới được h&igrave;nh th&agrave;nh khoảng hơn 40 năm về trước do c&aacute;c trận phun tr&agrave;o n&uacute;i lửa xảy ra từ năm 1963 đến 1967. Đảo Surtsey nằm ở ph&iacute;a nam Iceland đ&atilde; được nước n&agrave;y c&ocirc;ng nhận l&agrave; khu bảo tồn thi&ecirc;n nhi&ecirc;n v&agrave;o năm 1965, chỉ một số lượng hạn chế c&aacute;c nh&agrave; khoa học được ph&eacute;p tới đ&acirc;y t&igrave;m hiểu v&agrave; nghi&ecirc;n cứu.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">H&ograve;n đảo n&agrave;y l&agrave; một qu&agrave; tặng bất ngờ của thi&ecirc;n nhi&ecirc;n v&igrave; chưa bao giờ c&aacute;c nh&agrave; khoa học được theo d&otilde;i ch&iacute;nh x&aacute;c động thực vật chiếm lĩnh miền đất mới ra sao.</span></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:14px;">5. V&ugrave;ng đ&aacute; thi&ecirc;ng Stonehenge ở Anh</span></h2>\r\n<p style="text-align: center;">\r\n	<span style="font-size:14px;"><img alt="Vùng đá thiêng Stonehenge ở Anh" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/anh/vung-da-thien-Stonehenge-o-anh-mixtourist.jpg" style="width: 70%; height: 5%;" /></span></p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">Stonehenge nằm ở v&ugrave;ng đồng bằng Salisbury (Nam Anh), c&ocirc;ng tr&igrave;nh đ&aacute; phức tạp n&agrave;y gồm c&aacute;c v&ograve;ng tr&ograve;n đồng t&acirc;m tạo n&ecirc;n bởi c&aacute;c khối đ&aacute; đ&ocirc;i dựng đứng, b&ecirc;n tr&ecirc;n đặt một khối đ&aacute; nằm ngang, nối kết lại với nhau. Ở giữa l&agrave; khoảng 80 khối đ&aacute; xanh (dolomite) sắp theo h&igrave;nh m&oacute;ng ngựa, v&ograve;ng tr&ograve;n b&ecirc;n ngo&agrave;i l&agrave; c&aacute;c khối sa thạch (sarsen) lớn hơn.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">C&aacute;c nh&agrave; khảo cổ cho rằng c&aacute;c cột đ&aacute; n&agrave;y được dựng l&ecirc;n từ khoảng 2500-2000 trước C&ocirc;ng nguy&ecirc;n d&ugrave; c&aacute;c v&ograve;ng đất xung quanh được x&acirc;y dựng sớm hơn, khoảng 3100 năm trước C&ocirc;ng nguy&ecirc;n.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">Khu vực n&agrave;y v&agrave; khu vực xung quanh đ&atilde; được UNESCO c&ocirc;ng nhận l&agrave; Di sản thế giới năm 1986.</span></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">Mọi th&ocirc;ng tin chi tiết về&nbsp;<a href="http://dulichchauau.org/du-lich-chau-au/"><strong>tour du lịch ch&acirc;u &Acirc;u</strong></a>&nbsp;xin qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ trực tiếp tới văn ph&ograve;ng của <strong><a href="http://mixtourist.com.vn/">MixTourist</a>&nbsp;</strong>qu&aacute; hotline 04.6281 4340 để được tư vấn miễn ph&iacute;.</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">Tham khảo nhay c&aacute;c tour du lịch ch&acirc;u &acirc;u 2017 của MixTourist:</span></p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">&gt;&gt; <a href="http://dulichchauau.org/tour/du-lich-anh/tour-du-lich-anh-quoc.html"><strong>Tour du lịch Anh Quốc</strong></a></span></p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;">&gt;&gt; <a href="http://dulichchauau.org/tour/phap/tour-phap-thuy-si-y.html"><strong>Tour Phap - Thụy Sĩ - &Yacute;</strong></a></span></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<span style="font-size:14px;"><em>Mời bạn chi&ecirc;m ngưỡng những vẻ đẹp tuyệt vời nhất của ch&acirc;u &Acirc;u được tổng hợp trong video dưới đ&acirc;y</em></span></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size:14px;"><iframe frameborder="0" height="250" scrolling="no" src="https://www.youtube.com/embed/mC9MNnDc22Y" width="50%"></iframe></span></p>\r\n', 'Top 5 di sản thiên nhiên thế giới tuyệt vời nhất tại châu Âu', 'vẻ đẹp châu âu, cảnh đẹp châu âu, di sản thế giới, du lịch châu âu', 'Châu Âu được coi là trái tim văn minh của nhân loại, tuy xã hội đang trên đà phát triển vượt bậc nhưng châu Âu vẫn còn giữ trong lòng mình những đường nét cổ điển và sang', '2017-01-13 09:09:24'),
(6, 4, 'Phát thèm với top 5 món ăn làm dạng danh ẩm thực nước Ý', 'phat-them-voi-top-5-mon-an-lam-dang-danh-am-thuc-nuoc-y', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/Lasagna-am-thuc-nuoc-y-mixtourist.jpg', 393, '<p style="text-align: justify;">\r\n	H&agrave;nh tr&igrave;nh&nbsp;<strong>du lịch Ch&acirc;u &Acirc;u&nbsp;</strong>của du kh&aacute;ch sẽ kh&ocirc;ng thể bỏ qua được đất nước &Yacute;, một đất nước với một nền ẩm thực rất phong ph&uacute; v&agrave; tuyệt vời, l&agrave; bậc thầy của nh&acirc;n loại. Khi n&oacute;i đến <strong>ẩm thực &Yacute;</strong> chắc chắn sẽ kh&ocirc;ng thiếu những lời khen ngợi d&agrave;nh cho c&aacute;c m&oacute;n ăn nơi đ&acirc;y.&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	B&acirc;y giờ,&nbsp;<strong>MixTourist</strong> sẽ c&ugrave;ng với c&aacute;c bạn đi kh&aacute;m ph&aacute; 5 m&oacute;n ăn nổi tiếng nhất v&agrave; l&agrave;m dạng danh cho nền ẩm thực của đất nước h&igrave;nh chiếc ủng n&agrave;y nh&eacute;!</p>\r\n<p style="text-align: justify;">\r\n	<em>&gt;&gt; Để thưởng thức c&aacute;c m&oacute;n ăn đặc trưng của văn h&oacute;a ẩm thực nước &Yacute; cũng như c&aacute;c quốc gia ch&acirc;u &Acirc;u nổi tiếng kh&aacute;c, h&atilde;y đăng k&yacute;&nbsp;<strong><a href="http://dulichchauau.org/tour/du-lich-phap/tour-phap-thuy-si-y.html">tour du lịch ph&aacute;p - thụy sĩ - &yacute;</a></strong>&nbsp;của MixTourist ngay h&ocirc;m nay!</em></p>\r\n<h2 style="text-align: justify;">\r\n	<strong><span style="font-size:16px;">1. Pizza</span></strong></h2>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/am-thuc-nuoc-y-du-lich-nuoc-y-mixtourist.jpg" style="width: 650px; height: 401px;" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Chắc hẳn những người Việt Nam ch&uacute;ng ta kh&ocirc;ng c&ograve;n cảm thấy xa lạ khi nhắc tới m&oacute;n Pizza gắn liền với mỗi chuyến đi&nbsp;<strong>du lịch &Yacute;</strong>&nbsp;c&ugrave;ng với sự xuất hiện của n&oacute; ở tr&ecirc;n thế giới n&agrave;y. Pizza lu&ocirc;n lu&ocirc;n l&agrave; niềm tự h&agrave;o của nước &Yacute;. Trước đ&acirc;y, Pizza vốn chỉ l&agrave; một&nbsp;loại b&aacute;nh mỳ b&igrave;nh d&acirc;n được b&aacute;n dạo ở đường phố. Nhưng sau n&agrave;y ch&iacute;nh nữ ho&agrave;ng Margherits đ&atilde; chuyển m&oacute;n b&aacute;nh mỳ n&agrave;y th&agrave;nh một m&oacute;n ăn ho&agrave;ng gia bằng c&aacute;ch cho th&ecirc;m l&aacute; hung quế, c&agrave; chua v&agrave; pho m&aacute;t Mozzarella c&ugrave;ng với oliu v&agrave;o b&aacute;nh v&agrave; đ&atilde; biến loại b&aacute;nh b&igrave;nh d&acirc;n n&agrave;y trở n&ecirc;n sặc sỡ hơn với ba m&agrave;u ch&iacute;nh của n&oacute; l&agrave; m&agrave;u xanh, trắng v&agrave; đỏ.</p>\r\n<p style="text-align: justify;">\r\n	Những chiếc Pizza n&agrave;y kh&ocirc;ng c&oacute; h&igrave;nh th&ugrave; cố định m&agrave; n&oacute; c&oacute; thể l&agrave; h&igrave;nh vu&ocirc;ng, h&igrave;nh tr&ograve;n hay l&agrave; h&igrave;nh bầu dục, cũng c&oacute; thể l&agrave; h&igrave;nh m&aacute;i ch&egrave;o nhưng phổ biến nhất vẫn l&agrave; h&igrave;nh tr&ograve;n. Những chiếc b&aacute;nh Pizza ng&agrave;y nay đ&atilde; du nhập hầu hết c&aacute;c nước tr&ecirc;n to&agrave;n thế giới v&agrave; ở mỗi v&ugrave;ng miền mỗi đất nước n&oacute; lại c&oacute; những hương vị kh&aacute;c nhau.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>2. Gelato</strong></span></h2>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Gelato - Ẩm thực nước Ý - Mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/Gelato-am-thuc-nuoc-y-mixtourist.jpg" style="width: 70%; height: 5%;" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	T&ecirc;n gọi <strong>Gelato</strong> theo như c&aacute;c ph&aacute;t &acirc;m của tiếng &Yacute;, n&oacute; c&oacute; nghĩa l&agrave; kem. Gelato l&agrave; một m&oacute;n ăn được l&agrave;m ra từ đường, sữa kem c&ugrave;ng với c&aacute;c loại hương liệu kh&aacute;c nhau như quả hạch hay l&agrave; hoa quả. Một điểm kh&aacute;c biệt ở loại kem n&agrave;y đ&oacute; ch&iacute;nh l&agrave; n&oacute; nhiều đường nhưng lại chứa rất &iacute;t chất b&eacute;o. Với c&aacute;c loại đường như đường nho, đường&nbsp;m&iacute;a hay đường khử nước th&igrave; người &Yacute; lu&ocirc;n lu&ocirc;n t&igrave;m c&aacute;ch để đảm bảo độ ngọt tự nhi&ecirc;n cho m&oacute;n ăn của m&igrave;nh.</p>\r\n<p style="text-align: justify;">\r\n	Nếu như du kh&aacute;ch chưa một lần nếm thử qua <strong>m&oacute;n Gelato</strong> th&igrave; sẽ l&agrave; mọt điều thiếu x&oacute;t trong chuyến <strong>du lịch &Yacute;</strong> của m&igrave;nh. Du kh&aacute;ch c&oacute; thể dễ d&agrave;ng nh&igrave;n thấy c&aacute;c cửa h&agrave;ng chuy&ecirc;n b&aacute;n kem Gelato hay cũng c&oacute; thể l&agrave; những cửa h&agrave;ng caf&eacute; Gelato hoặc cũng c&oacute; thể l&agrave; những cửa h&agrave;ng chỉ b&aacute;n ri&ecirc;ng kem Gelato do ch&iacute;nh m&igrave;nh sản xuất ở khắp nơi tr&ecirc;n đất nước &Yacute;.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="font-size:16px;"><strong>3. Risotto</strong></span></h2>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Risotto - Ẩm thực nước Ý - Mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/Risotto-am-thuc-nuoc-y-mixtourist.jpg" style="width: 70%; height: 5%;" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Ở ph&iacute;a Bắc của nước &Yacute; c&oacute; m&oacute;n ăn rất ngon v&agrave; rất được ưa chuộng nơi đ&acirc;y đ&oacute; ch&iacute;nh l&agrave; m&oacute;n cơm <strong>Risotto</strong>. M&oacute;n ăn độc đ&aacute;o v&agrave; hấp dẫn n&agrave;y được chia ra th&agrave;nh bốn th&agrave;nh phần ch&iacute;nh đ&oacute; ch&iacute;nh l&agrave; hỗn hợp của rau củ theo phong c&aacute;ch nước &Yacute;, nước thịt c&oacute; thể l&agrave; thịt g&agrave;, thịt b&ograve; hay l&agrave; c&aacute; c&ugrave;ng với c&aacute;c th&agrave;nh phần hương vị v&agrave; th&agrave;nh phần quan trọng nhất ch&iacute;nh l&agrave; gạo.</p>\r\n<p style="text-align: justify;">\r\n	Với cơm Risotto th&igrave; gạo phải được nấu bằng gạo c&oacute; hạt ngắn, tr&ograve;n v&agrave; dẻo hơn c&aacute;c loại gạo kh&aacute;c v&agrave; loại gạo n&agrave;y chứa một lượng tinh bột rất gi&agrave;u chất Amylopectin. Một điều đặc biệt quan trọng để tạo n&ecirc;n m&ugrave;i hương đặc trưng của m&oacute;n ăn n&agrave;y ch&iacute;nh l&agrave; người ta sử dụng rượu vang để dưới&nbsp;l&ecirc;n gạo trong qu&aacute; tr&igrave;nh nấu v&agrave; nhờ v&agrave;o rượu c&ugrave;ng với lượng nhiệt đun th&igrave; gạo sẽ đậm m&ugrave;i hơn v&agrave; mềm hơn.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="font-size:16px;"><strong>4. Spaghetti</strong></span></h2>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Spaghetti - Ẩm thực nước Ý - Mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/Spaghetti-am-thuc-nuoc-y-mixtourist.jpg" style="width: 70%; height: 5%;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Mỳ &Yacute; với dạng mỳ sợi tr&ograve;n v&agrave; to n&agrave;y rất nổi tiếng ở hầu khắp c&aacute;c nước tr&ecirc;n thế giới. Mỳ được l&agrave;m ra từ bột mỳ loại semolina c&ugrave;ng với nước. M&oacute;n mỳ n&agrave;y thực chất l&agrave; do người Ả Rập đ&atilde; du nhập v&agrave;o nước &Yacute; từ khoảng thể kỷ IX. L&uacute;c đầu m&oacute;n mỳ n&agrave;y được người Ả Rập l&agrave;m ra chỉ l&agrave; một m&oacute;n ăn kh&ocirc; v&agrave; n&oacute; d&ugrave;ng tay để bốc ăn nhưng khi tới nước &Yacute; th&igrave; c&ugrave;ng với sự kết hợp của c&agrave; chua m&oacute;n mỳ n&agrave;y đ&atilde; trở th&agrave;nh m&oacute;n ăn ướt v&agrave; phải d&ugrave;ng đến nĩa ăn.</p>\r\n<p style="text-align: justify;">\r\n	Mỳ Spaghetti được ăn với nước xốt c&agrave; chua v&agrave; loại nước xốt n&agrave;y c&oacute; thể được tạo từ nhiều loại rau v&agrave; gia vị kh&aacute;ch nhau như dầu oliu, hung hay từ thịt v&agrave; c&aacute;. Khi ăn mỳ người ta thường d&ugrave;ng với một loại&nbsp;phomat xay như Parmedan, Pecorino Romano hay l&agrave; Asiago. M&oacute;n mỳ Spaghetti l&agrave; một m&oacute;n ăn kh&ocirc;ng thể thiếu trong thực đơn của người &Yacute;.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="font-size:16px;"><strong>5. Lasagna</strong></span></h2>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Lasagna-am-thuc-nuoc-y-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/Lasagna-am-thuc-nuoc-y-mixtourist.jpg" style="width: 70%; height: 5%;" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Cũng l&agrave; một loại mỳ nhưng <strong>Lasagna </strong>l&agrave; một loại mỳ dạng l&aacute; hoặc l&agrave; dạng tấm. Đ&acirc;y ch&iacute;nh l&agrave; một loại pasta dạng nướng được tạo n&ecirc;n từ c&aacute;c nguy&ecirc;n liệu như ph&ocirc; mai, l&aacute; <strong>lasagna </strong>v&agrave; thịt sốt. C&oacute; rất nhiều c&aacute;ch kh&aacute;c nhau để chế biến m&oacute;n mỳ &Yacute; Lasgna nhưng m&agrave; m&oacute;&nbsp;phổ biến nhất vẫn l&agrave; Lasgna Bolognese.</p>\r\n<p style="text-align: justify;">\r\n	Mọi th&ocirc;ng tin chi tiết về <a href="http://dulichchauau.org/du-lich-y/"><strong>tour du lịch &yacute; 2017</strong></a> hoặc c&aacute;c&nbsp;<strong><a href="http://dulichchauau.org/du-lich-chau-au/">tour du lịch ch&acirc;u &Acirc;u</a></strong>&nbsp;kh&aacute;c xin qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ trực tiếp tới văn ph&ograve;ng của <strong>Mixtourist </strong>qua hotline<strong> 04.6281.4340</strong>&nbsp;để được hỗ trợ nhiều hơn nữa</p>\r\n', 'Phát thèm với top 5 món ăn làm dạng danh ẩm thực nước Ý', 'ẩm thực ý, món ăn ý, du lịch ý, am thuc nuoc y, văn hóa ý, tour du lịch ý', 'Khi nói đến ẩm thực Ý chắc chắn sẽ không thiếu những lời khen ngợi không ngớt dành cho các món ăn nơi đây. ', '2017-01-04 11:43:33'),
(7, 2, 'Những quê hương rượu vang tại Pháp', 'nhung-que-huong-ruou-vang-tai-phap', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/ruou-vang-phap-mixtourist.jpg', 322, '<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<span font-size:="" new="" style="margin: 0px; font-family: " times="">Ph&aacute;p nổi tiếng thế giới với những d&ograve;ng rượu vang ngon v&agrave; được chế biến độc đ&aacute;o. Nhưng kh&ocirc;ng phải chỗ n&agrave;o tr&ecirc;n đất Ph&aacute;p cũng c&oacute; thể cho ra đời một ly rượu vang ngon như lời đồn vẫn n&oacute;i. </span></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<span font-size:="" new="" style="margin: 0px; font-family: " times="">H&atilde;y c&ugrave;ng <strong>Mix Tourist</strong>&nbsp; - <strong><a href="http://dulichchauau.org/">C&ocirc;ng ty du lịch chuy&ecirc;n tour ch&acirc;u &acirc;u</a>&nbsp;</strong>t&igrave;m hiểu một v&agrave;i&nbsp;</span>qu&ecirc; hương rượu vang tại Ph&aacute;p&nbsp;<span font-size:="" new="" style="margin: 0px; font-family: " times="">nh&eacute;.</span></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</h2>\r\n<h2 style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<span font-size:="" new="" style="margin: 0px; font-family: " times=""><strong style="margin: 0px;">1. Bordeaux</strong></span></h2>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<img alt="Những quê hương rượu vang tại Pháp" height="321" src="http://demo.mixtourist.com/uploads/images/thang%206/30/Bordeaux.jpg" style="margin: 0px auto; border: 0px; max-width: 100%; display: block;" width="600" /></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<span font-size:="" new="" style="margin: 0px; font-family: " times="">Bordeaux kh&ocirc;ng chỉ l&agrave; v&ugrave;ng đất sản xuất rượu lớn nhất v&agrave; c&oacute; lịch sử l&acirc;u đời nhất tại Đại T&acirc;y Dương m&agrave; c&ograve;n đứng đầu thế giới về xuất khẩu rượu. Được mệnh danh với c&aacute;i t&ecirc;n &ldquo;thủ phủ rượu vang tr&ecirc;n thế giới&rdquo; ch&uacute;ng ta c&oacute; thể liệt k&ecirc; ra một số ng&ocirc;i l&agrave;ng rượu vang nổi tiếng như Pessac-L&eacute;ognan, Entre-Deux-Mers, Sauternes v&agrave; Barsac. Bordeau thường k&eacute;n người thưởng thức bởi lẽ n&oacute; c&oacute; vị ch&aacute;t mạnh hơn hẳn so với c&aacute;c loại kh&aacute;c.</span></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<strong font-size:="" margin:="" new="" style="font-family: " times="">2. Champagne</strong></h2>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<img alt="Những quê hương rượu vang tại Pháp" height="409" src="http://demo.mixtourist.com/uploads/images/thang%206/30/Champagne.jpg" style="margin: 0px auto; border: 0px; max-width: 100%; display: block;" width="600" /></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<span font-size:="" new="" style="margin: 0px; font-family: " times="">Nổi tiếng l&agrave; trung t&acirc;m sản xuất rượu vang của Ph&aacute;p từ thế kỷ thứ 17, Champagne hẳn l&agrave; c&aacute;i t&ecirc;n kh&aacute; quen thuộc với những ai nghiện loại rượu vang sủi tăm của v&ugrave;ng đất lạnh nhất nước Ph&aacute;p n&agrave;y. Champagne c&oacute; ba vị độc đ&aacute;o đ&oacute; l&agrave; Bruit (kh&ocirc;ng ngọt), Demi Sec (hơi ngọt) v&agrave; Sec (ngọt). Điều n&agrave;y l&agrave; do nguy&ecirc;n liệu sản xuất ch&iacute;nh của ch&uacute;ng tạo ra ba hương vị kh&aacute;c nhau đ&oacute; v&iacute; dụ như Pinot Noir đỏ, rượu Pinot Meunier đen v&agrave; Chardonnay trắng &hellip; Th&agrave;nh phố Reimis được xem l&agrave; &ldquo;thủ đ&ocirc; của xứ sở Champagne&rdquo; của Ph&aacute;p.</span></p>\r\n<h2 style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</h2>\r\n<h2 style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<strong font-size:="" margin:="" new="" style="font-family: " times="">3. Loire</strong></h2>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<img alt="Những quê hương rượu vang tại Pháp" height="360" src="http://demo.mixtourist.com/uploads/images/thang%206/30/Loire.jpg" style="margin: 0px auto; border: 0px; max-width: 100%; display: block;" width="600" /></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<span font-size:="" new="" style="margin: 0px; font-family: " times="">Được đ&aacute;nh gi&aacute; l&agrave; khu sản xuất vang hảo hạng lớn thứ 3 của Ph&aacute;p, thung lũng Loire trải d&agrave;i bạt ng&agrave;n với c&aacute;nh đồng nho l&ecirc;n tới 1125 km<sup>2&nbsp;</sup>v&agrave; cực kỳ nổi tiếng với loại rượu vang trắng h&agrave;ng đầu thế giới. C&oacute; thể kể ra những v&ugrave;ng sản xuất rượu vang t&ecirc;n tuổi ở Loire như Pouilly-Fum&eacute;, Chinon, Vouvray, Sancerre, Saumur v&agrave; Bourgueil</span></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<span font-size:="" new="" style="margin: 0px; font-family: " times=""><strong style="margin: 0px;">4. Languedoc</strong></span></h2>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<img alt="Những quê hương rượu vang tại Pháp" height="399" src="http://demo.mixtourist.com/uploads/images/thang%206/30/Languedoc.jpg" style="margin: 0px auto; border: 0px; max-width: 100%; display: block;" width="600" /></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<span font-size:="" new="" style="margin: 0px; font-family: " times="">Languedoc l&agrave; v&ugrave;ng trồng nho v&agrave; sản xuất nho lớn nhất thế giới với tổng diện t&iacute;ch l&ecirc;n tới 290.000 ha, Languedoc thuộc miền Nam nước Ph&aacute;p v&agrave; nằm b&ecirc;n bờ Địa Trung Hải xanh tươi.&nbsp;</span><span font-size:="" new="" style="font-family: " times="">C&oacute; tới 60 loại vang nổi tiếng kh&aacute;c nhau tại đ&acirc;y, mỗi năm sản xuất 40% so với tổng số rượu vang trong nước sản xuất. Nếu bạn&nbsp;</span><strong font-size:="" margin:="" new="" style="font-family: " times=""><a href="http://mixtourist.com.vn/tour/with/Tour-du-lich-Chau-au-y---Thuy-Sy---Phap---Bi---Ha-Lan/190" style="margin: 0px; text-decoration: none; color: rgb(68, 68, 255);">du lịch Ph&aacute;p</a></strong><span font-size:="" new="" style="font-family: " times="">&nbsp;m&agrave; gh&eacute; qua đ&acirc;y bạn n&ecirc;n nếm thử một loại n&agrave;o đ&oacute; để thưởng thức hương vị của n&oacute;. C&oacute; rất nhiều loại vang hảo hạng m&agrave; ta c&oacute; thể kể ra như: AOC Classification, Blanquette de Limoux v&agrave; Vins de Pays d&rsquo;Oc.</span></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<span font-size:="" new="" style="margin: 0px; font-family: " times=""><strong style="margin: 0px;">5. Burgundy</strong></span></h2>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<img alt="Những quê hương rượu vang tại Pháp" height="348" src="http://demo.mixtourist.com/uploads/images/thang%206/30/Burgundy.jpg" style="margin: 0px auto; border: 0px; max-width: 100%; display: block;" width="600" /></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<span font-size:="" new="" style="margin: 0px; font-family: " times="">Mang trong m&igrave;nh nhiều thương hiệu rượu, nhiều hơn bất cứ v&ugrave;ng đất sản xuất rượu vang n&agrave;o của Ph&aacute;p. Burgundy c&oacute; h&agrave;ng lọat những khu trồng nho như Chablis, C&ocirc;te d&rsquo;Or, C&ocirc;te Chalonnaise, M&acirc;con v&agrave; Beaujolais. C&oacute; hai loại vang đỏ v&agrave; vang trắng hảo hạng v&ugrave;ng Burgundy c&oacute; &ldquo;tuổi đời&rdquo; l&ecirc;n đến 10 đến 20 năm.</span></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<span font-size:="" new="" style="margin: 0px; font-family: " times=""><strong style="margin: 0px;">6.&nbsp;</strong></span><strong font-size:="" margin:="" new="" style="font-family: " times="">Rh&ocirc;ne</strong></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: center;">\r\n	<img alt="Những quê hương rượu vang tại Pháp" src="http://viniferavn.com/wp-content/uploads/2016/06/rhone_08_burgundy-1.jpg" style="height: 398px; width: 600px;" /></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<span font-size:="" new="" style="margin: 0px; font-family: " times="">Đ&acirc;y l&agrave; v&ugrave;ng sản xuất rượu vang đỏ chủ yếu của v&ugrave;ng Đ&ocirc;ng Nam nước Ph&aacute;p. Thung lũng s&ocirc;ng Rh&ocirc;ne như một phi&ecirc;n bản lớn hơn rất nhiều của c&aacute;c vườn nho ph&iacute;a Bắc sản xuất rượu vang đỏ từ nho Syrah v&agrave; vang trắng từ nho Viognier.</span></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<span font-size:="" new="" style="margin: 0px; font-family: " times=""><strong style="margin: 0px;">7. Alsace</strong></span></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<img alt="Những quê hương rượu vang tại Pháp" height="400" src="http://demo.mixtourist.com/uploads/images/thang%206/30/alsace-wine-route.jpg" style="margin: 0px auto; border: 0px; max-width: 100%; display: block;" width="600" /></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	<span font-size:="" new="" style="margin: 0px; font-family: " times="">Nằm ph&iacute;a Đ&ocirc;ng nước Ph&aacute;p ngo&agrave;i l&agrave; một điểm đến xinh đẹp c&ugrave;ng kh&iacute; hậu &ocirc;n đới tuyệt vời&nbsp; Alsace nổi danh với loại vang trắng được cho ra đời sớm nhất quốc gia v&ugrave;ng T&acirc;y &Acirc;u n&agrave;y. Đến với &ldquo;Xứ sở vang trắng&rdquo; Alsace ta n&ecirc;n nếm thử qua cả 4 loại rượu nho trắng mang hương vị v&ocirc; c&ugrave;ng tinh tế như vang trắng Riesling dịu nhẹ, rượu Gewiztraminer cay đậm, rượu Pinot Gris mạnh mẽ v&agrave; rượu nho xạ Muscat d&rsquo;Alsace thơm nồng ấm.</span></p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="margin: 0px; color: rgb(51, 51, 51); font-family: arial, Verdana, Helvetica, sans-serif; font-size: 13px; text-align: justify;">\r\n	&gt;&gt; H&atilde;y chọn ngay cho m&igrave;nh v&agrave; gia đ&igrave;nh một chuyến <a href="http://dulichchauau.org/du-lich-phap/"><strong>du lịch Ph&aacute;p</strong></a> gi&aacute; rẻ 2017 của Mixtourist để thưởng thức những ly rượu vang Ph&aacute;p thương hạng n&agrave;y nh&eacute;, chắc chắn bạn sẽ c&oacute; những trải nghiệm tuyệt vời nhất</p>\r\n', 'Những quê hương rượu vang tại Pháp', 'quê hương rượu vang tại Pháp, du lịch Pháp, tour du lịch Châu Âu, land tour châu âu ', 'Du lịch Pháp sẽ làm say lòng du khách với những quê hương rượu vang nổi tiếng.', '2017-01-13 08:52:50');
INSERT INTO `news` (`id`, `danhmuc_id`, `name`, `name_url`, `img`, `view`, `content`, `title`, `keyword`, `description`, `created`) VALUES
(8, 3, 'Top 10 điểm đến hấp dẫn nhất tại Đức', 'top-10-diem-den-hap-dan-nhat-tai-duc', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/canh-cong-Brandenburg-du-lich-duc-mixtourist.jpg', 482, '<p style="text-align: justify;">\r\n	Sau đ&acirc;y <strong>MixTourist</strong> xin giới thiệu <em>10 điểm đến hấp dẫn nhất đối với du kh&aacute;ch đi&nbsp;du lịch Đức</em> trong năm 2016.&nbsp;Đ&oacute; kh&ocirc;ng chỉ l&agrave; những c&ocirc;ng tr&igrave;nh kiến tr&uacute;c độc đ&aacute;o, những di t&iacute;ch lịch sử được bảo tồn m&agrave; c&ograve;n l&agrave; những cảnh đẹp thi&ecirc;n nhi&ecirc;n h&ugrave;ng vĩ, thơ mộng.</p>\r\n<p style="text-align: justify;">\r\n	C&ugrave;ng theo ch&acirc;n <strong>Mix Tourist</strong> ng&agrave;y h&ocirc;m nay để kh&aacute;m ph&aacute; nh&eacute;.</p>\r\n<p style="text-align: justify;">\r\n	<em>&gt;&gt; Xem ngay Dịch vụ l&agrave;m <a href="http://dulichchauau.org/dich-vu/visa--ho-chieu/thu-tuc-lam-visa-di-duc.html"><strong>thủ tục xin visa đi Đức thăm th&acirc;n nh&acirc;n</strong></a>, đi c&ocirc;ng t&aacute;c hoặc đi du lịch nhanh gọn, gi&aacute; rẻ của <strong>MixTourist</strong>!</em></p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><strong><span style="font-size:16px;">1. Thị trấn Rothenburg ob der Tauber</span></strong></span></h2>\r\n<p style="text-align: justify;">\r\n	Thuộc v&ugrave;ng <strong>Bavaria</strong> nước <strong>Đức</strong>, <strong>Rothenburg ob der Tauber</strong> được du kh&aacute;ch gh&eacute; về đ&ocirc;ng đ&uacute;c bởi đ&acirc;y l&agrave; thị trấn đẹp nhất nằm tr&ecirc;n con đường l&atilde;ng mạn chạy qua Bavaria.</p>\r\n<p style="text-align: center;">\r\n	<img alt="thi-tran-Rothenburg-ob-der-Tauber-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/thi-tran-Rothenburg-ob-der-Tauber-mixtourist.jpg" style="width: 600px; height: 464px;" /></p>\r\n<p style="text-align: justify;">\r\n	Nối liền <strong>th&agrave;nh phố Frankfurt</strong> với <strong>Munich</strong>. Tr&ecirc;n con đường n&agrave;y bạn kh&ocirc;ng chỉ may mắn được tới <strong>Rothenburg ob der Tauber</strong> th&ocirc;i đ&acirc;u, m&agrave; n&oacute; c&ograve;n c&oacute; thể dẫn bạn tới bảo t&agrave;ng, <strong>Market Square</strong> v&agrave; t&ograve;a thị ch&iacute;nh với 241 bậc thang nơi m&agrave; bạn c&oacute; thể quan s&aacute;t được to&agrave;n bộ thị trấn.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><strong><span style="font-size:16px;">2. Rừng Đen (Schwarzwald)</span></strong></span></h2>\r\n<p style="text-align: center;">\r\n	<img alt="rung-den-Schwarzwald-du-lich-duc-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/rung-den-Schwarzwald-du-lich-duc-mixtourist.jpg" style="width: 600px; height: 375px;" /></p>\r\n<p style="text-align: justify;">\r\n	Với diện t&iacute;ch khổng lồ, <strong>Rừng Đen</strong> bao phủ nguy&ecirc;n một v&ugrave;ng rộng lớn. C&oacute; nhiều c&aacute;ch để bạn c&oacute; thể kh&aacute;m ph&aacute; trọn vẹn những vẻ đẹp của khu rừng n&agrave;y trong đ&oacute; c&oacute; hai phương &aacute;n được nhiều người lựa chọn nhất l&agrave; l&aacute;i xe hoặc đi bộ dọc theo <strong>Schwarzwaldhochstra&szlig;e </strong>- một tuyến đường qua c&aacute;c ngọn đồi v&agrave; thung lũng, xuy&ecirc;n qua rừng c&acirc;y linh sam rậm rạp v&agrave; mặt hồ phủ sương m&ugrave;.</p>\r\n<p style="text-align: center;">\r\n	<img alt="ho-Titisee-lake-du-lich-duc-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/ho-Titisee-lake-du-lich-duc-mixtourist.jpg" style="width: 600px; height: 320px;" /></p>\r\n<p style="text-align: justify;">\r\n	Bạn cũng n&ecirc;n t&igrave;m tới <strong>hồ&nbsp;Titisee</strong> - hồ nước đẹp nhất trong khu vực hoặc kh&aacute;m ph&aacute; nơi đ&acirc;y t&ugrave;y &yacute; để tận hưởng một kh&ocirc;ng gian trong l&agrave;nh.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;"><strong>3. L&acirc;u đ&agrave;i Neuschwanstein</strong></span></span></h2>\r\n<p style="text-align: justify;">\r\n	L&acirc;u đ&agrave;i <strong>Neuschwanstein </strong>nằm tại miền Nam nước Đức, l&agrave; l&acirc;u đ&agrave;i của vua Ludwig được x&acirc;y dựng từ thế kỷ thứ 19. Đ&acirc;y l&agrave; một c&ocirc;ng tr&igrave;nh kiến tr&uacute;c ti&ecirc;u biểu cho phong c&aacute;ch <strong>Romanesque</strong>.</p>\r\n<p style="text-align: center;">\r\n	<img alt="lau-dai-Neuschwanstein-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/lau-dai-Neuschwanstein-mixtourist.jpg" style="width: 600px; height: 432px;" /></p>\r\n<p style="text-align: justify;">\r\n	Tuy đ&atilde; trải qua một thời gian d&agrave;i nhưng t&ograve;a l&acirc;u đ&agrave;i hầu như vẫn c&ograve;n nguy&ecirc;n bản với những căn ph&ograve;ng xa hoa, lộng lẫy được người Đức bảo tồn rất tốt.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;"><strong>4. Th&agrave;nh phố Heidelberg</strong></span></span></h2>\r\n<p style="text-align: justify;">\r\n	Nằm ở trung t&acirc;m <strong>thung lũng Neckar</strong>, <strong>th&agrave;nh phố Heidelberg</strong> bị ảnh hưởng nặng nề sau thế chiến thứ 2. Đ&acirc;y được xem như một v&iacute; dụ điển h&igrave;nh về phong c&aacute;ch <strong>kiến tr&uacute;c baroque</strong>.</p>\r\n<p style="text-align: center;">\r\n	<img alt="thanh-pho-Heidelberg-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/thanh-pho-Heidelberg-mixtourist.jpg" style="width: 600px; height: 401px;" /></p>\r\n<p style="text-align: justify;">\r\n	Tuy bị chiến tranh t&agrave;n ph&aacute; nhưng nơi n&agrave;y vẫn lưu giữ được những địa danh lịch sử như l&acirc;u đ&agrave;i Heidelberg, khu phố cổ.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;"><strong>5. Cung điện Sanssouci</strong></span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>Cung điện Sanssouci </strong>hay cung điện m&ugrave;a h&egrave; tọa lạc tr&ecirc;n một ngọn đồi nhỏ tại <strong>th&agrave;nh phố Potsdam</strong> c&aacute;ch <strong>thủ đ&ocirc; Berlin</strong> kh&ocirc;ng xa.</p>\r\n<p style="text-align: center;">\r\n	<img alt="cung-dien-Sanssouci-du-lich-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/cung-dien-Sanssouci-du-lich-mixtourist.jpg" style="width: 600px; height: 412px;" /></p>\r\n<p style="text-align: justify;">\r\n	Đ&acirc;y l&agrave; c&ocirc;ng tr&igrave;nh được x&acirc;y dựng cho <strong>Frederick Đại đế</strong> v&agrave; được bao bọc bởi những khu vườn bậc thang tuyệt đẹp.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;"><strong>6. Cung đường Rheinsteig</strong></span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>Rheinsteig </strong>l&agrave; một cung đường m&ograve;n nổi tiếng chạy dọc <strong>bờ s&ocirc;ng Rhine</strong> nối liền <strong>th&agrave;nh phố Bonn</strong> v&agrave; <strong>Wiesbaden</strong>. <strong>Rheinsteig&nbsp;</strong>được nhiều người biết đến với những khu rừng, những ng&ocirc;i l&agrave;ng v&agrave; những vườn nho đẹp mơ m&agrave;ng như trong một bức tranh vậy.</p>\r\n<p style="text-align: center;">\r\n	<img alt="cung-duong-Rheinsteig-du-lich-duc-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/cung-duong-Rheinsteig-du-lich-duc-mixtourist.JPG" style="width: 600px; height: 450px;" /></p>\r\n<p style="text-align: justify;">\r\n	Đến với <strong>Rheinsteig</strong>, bạn sẽ c&oacute; cơ hội chi&ecirc;m ngưỡng những khung cảnh đồi n&uacute;i h&ugrave;ng vĩ hay những l&acirc;u đ&agrave;i tuyệt đẹp. Khu vực từ <strong>Bingen </strong>đến <strong>Koblenz </strong>đ&atilde; được c&ocirc;ng nhận l&agrave; di sản thế giới với 40 l&acirc;u đ&agrave;i v&agrave; c&ocirc;ng tr&igrave;nh l&acirc;u đời kh&aacute;c.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;"><strong>7. Cảng Bremen</strong></span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>Th&agrave;nh phố cảng Bremen</strong> nằm dọc b&ecirc;n <strong>d&ograve;ng s&ocirc;ng Weser</strong> c&oacute; ni&ecirc;n đại l&ecirc;n tới 1.200 năm v&agrave; l&agrave; th&agrave;nh phố c&oacute; tuổi đời l&acirc;u nhất nước Đức. Với lịch sử l&acirc;u năm của m&igrave;nh, Bremen hấp dẫn du kh&aacute;ch bằng những con hẻm nhỏ quanh co rải sỏi bao lấy những ng&ocirc;i nh&agrave; cổ k&iacute;nh được x&acirc;y dựng từ thời trung cổ.</p>\r\n<p style="text-align: center;">\r\n	<img alt="cang-Bremen-du-lich-duc-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/cang-Bremen-du-lich-duc-mixtourist.jpg" style="width: 600px; height: 397px;" /></p>\r\n<p style="text-align: justify;">\r\n	Ngo&agrave;i ra bạn cũng c&oacute; thể tới tham quan những trang trại lớn ở v&ugrave;ng n&ocirc;ng th&ocirc;n c&aacute;ch trung t&acirc;m th&agrave;nh phố một khoảng kh&ocirc;ng xa cho lắm.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;"><strong>8. H&ograve;n đảo Sylt</strong></span></span></h2>\r\n<p style="text-align: justify;">\r\n	Nhiều người từ nước ngo&agrave;i tới thường &iacute;t biết về h&ograve;n đảo n&agrave;y. N&eacute;t đẹp độc đ&aacute;o của h&ograve;n đảo n&agrave;y được tạo n&ecirc;n từ những khu nghỉ dưỡng, 40km dọc bờ biển, những c&aacute;nh đồng hoa, những ng&ocirc;i nh&agrave; sặc sỡ v&agrave; đặc biệt l&agrave; những v&aacute;ch đ&aacute; dọc bờ biển được nước x&oacute;i m&ograve;n tạo n&ecirc;n những h&igrave;nh th&ugrave; kỳ dị.</p>\r\n<p style="text-align: center;">\r\n	<img alt="hon-dao-Sylt-du-lich-duc-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/hon-dao-Sylt-du-lich-duc-mixtourist.jpg" style="width: 600px; height: 381px;" /></p>\r\n<p style="text-align: justify;">\r\n	Nh&igrave;n từ tr&ecirc;n cao, <strong>h&ograve;n đảo Sylt </strong>như một tấm vải quyến rũ ẩn m&igrave;nh giữa biển khơi vậy.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;"><strong>9. Vườn Quốc gia Harz</strong></span></span></h2>\r\n<p style="text-align: justify;">\r\n	H&ograve;n n&uacute;i cao nhất miền bắc nước Đức l&uacute;c n&agrave;o cũng lơ lửng giữa m&agrave;n sương bao phủ nằm trong <strong>vườn quốc gia Harz</strong> n&agrave;y. Người ta đ&atilde; kể cho nhau nghe về nhiều sinh vật huyền b&iacute; tr&ecirc;n đỉnh n&uacute;i kỳ lạ ấy. Điều đ&oacute; c&agrave;ng người ta t&ograve; m&ograve; v&agrave; n&oacute; c&agrave;ng ng&agrave;y c&agrave;ng nổi tiếng, thu h&uacute;t nhiều du kh&aacute;ch t&igrave;m về.</p>\r\n<p style="text-align: center;">\r\n	<img alt="vuon-quoc-gia-Harz-du-lich-duc-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/vuon-quoc-gia-Harz-du-lich-duc-mixtourist.jpg" style="width: 600px; height: 399px;" /></p>\r\n<p style="text-align: justify;">\r\n	Bạn c&oacute; thể đi t&agrave;u thẳng l&ecirc;n đỉnh n&uacute;i để chi&ecirc;m ngưỡng khung cảnh ngoạn mục của&nbsp;<strong>Harz</strong>.</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;"><strong>10. Th&agrave;nh phố Bamberg</strong></span></span></h2>\r\n<p style="text-align: justify;">\r\n	Th&agrave;nh phố di sản thế giới <strong>Bamberg </strong>giữ trong m&igrave;nh những tuyến đường nhỏ hẹp đẹp mắt c&ugrave;ng những phong c&aacute;ch kiến tr&uacute;c đa dạng v&agrave; độc đ&aacute;o. N&oacute; nổi tiếng c&ograve;n nhờ v&agrave;o truyền thống sản xuất bia l&acirc;u đời với rất nhiều loại bia kh&aacute;c nhau.</p>\r\n<p style="text-align: center;">\r\n	<img alt="thanh-pho-Bamberg-du-lich-duc-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/thanh-pho-Bamberg-du-lich-duc-mixtourist.jpg" style="width: 600px; height: 375px;" /></p>\r\n<p style="text-align: justify;">\r\n	Đến với <strong>Bamberg </strong>bạn n&ecirc;n gh&eacute; tới t&ograve;a nh&agrave; thị ch&iacute;nh hay thả m&igrave;nh tr&ocirc;i tr&ecirc;n những nh&aacute;nh s&ocirc;ng uốn lượn để chi&ecirc;m ngưỡng vẻ đẹp của th&agrave;nh phố n&agrave;y. V&agrave; tất nhi&ecirc;n rồi h&atilde;y thử nếm một cốc bia tại một khu vườn bia ngo&agrave;i trời n&agrave;o đ&oacute;.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<em>&gt;&gt; H&atilde;y đăng k&yacute; ngay <strong>tour du lịch Đức</strong> được nhiều du kh&aacute;ch lựa chọn nhất <a href="http://dulichchauau.org/du-lich-duc/"><strong>tại đ&acirc;y</strong></a> để c&oacute; những trải nghiệm mới mẻ v&agrave; tuyệt vời c&ugrave;ng người th&acirc;n trong năm 2017 nh&eacute;!</em></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<strong>MixTourist</strong> - <a href="http://dulichchauau.org/"><strong>C&ocirc;ng ty du lịch chuy&ecirc;n tour ch&acirc;u &Acirc;u</strong></a>&nbsp;h&agrave;ng đầu Việt Nam rất h&acirc;n hạnh được đồng h&agrave;nh c&ugrave;ng với qu&yacute; kh&aacute;ch!</p>\r\n<p style="text-align: justify;">\r\n	Mọi th&ocirc;ng tin chi tiết, xin truy cập&nbsp;<strong><a href="http://dulichchauau.org/">http://dulichchauau.org/</a></strong> hoặc gọi tới hotline <strong>04.62814340</strong>.</p>\r\n<p style="text-align: justify;">\r\n	Ngo&agrave;i ra, qu&yacute; kh&aacute;ch cũng c&oacute; thể trực tiếp tới c&ocirc;ng ty tại địa chỉ b&ecirc;n dưới:&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<iframe frameborder="0" height="500" longdesc="Địa chỉ Công ty du lịch Mix" name="Địa chỉ Công ty du lịch Mix" scrolling="no" src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3724.549416996156!2d105.8072683143864!3d21.010691486008167!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3135ac9e24c47d8f%3A0x79c883b18e8635af!2zQ8O0bmcgdHkgQ-G7lSBwaOG6p24gROG7i2NoIHbhu6UgVGjGsMahbmcgbeG6oWkgdsOgIER1IGzhu4tjaCBNaXg!5e0!3m2!1svi!2sus!4v1482305776162" title="Địa chỉ Công ty du lịch Mix" width="700"></iframe></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<em><strong>MixTourist rất h&acirc;n hạnh được đồng h&agrave;nh c&ugrave;ng bạn kh&aacute;m ph&aacute; thế giới!</strong></em></p>\r\n', 'Top 10 điểm đến hấp dẫn nhất tại Đức', 'du lịch châu âu, tour du lịch châu âu, du lịch đức, tour du lịch đức', 'Sau đây MixTourist xin giới thiệu 10 điểm đến hấp dẫn nhất đối với du khách đi du lịch Đức trong năm 2016', '2017-02-28 09:31:41'),
(9, 8, 'Tổng hợp kinh nghiệm du lịch Tây Ban Nha chi tiết nhất 2017', 'tong-hop-kinh-nghiem-du-lich-tay-ban-nha-chi-tiet-nhat-2017', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/taybannha/kinh-nghiem-du-lich-tay-ban-nha-mixtourist.jpg', 1688, '<p style="text-align: justify;">\r\n	Xứ sở b&ograve; t&oacute;t - T&acirc;y Ban Nha lu&ocirc;n hấp dẫn du kh&aacute;ch với bao điều kỳ b&iacute;.&nbsp;<strong>Mix Tourist </strong>xin chia sẻ một ch&uacute;t&nbsp;<strong><a href="http://dulichchauau.org/tin-tuc/du-lich-tay-ban-nha/tong-hop-kinh-nghiem-du-lich-tay-ban-nha-chi-tiet-nhat-2017.html">kinh nghiệm du lịch T&acirc;y Ban Nha</a></strong>&nbsp;gi&uacute;p bạn c&oacute; thể chủ động hơn trong h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; đất nước xinh đẹp n&agrave;y. Tham khảo ngay nh&eacute;!</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="du lịch Tây Ban Nha - MixTourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/taybannha/du-lich-tay-ban-nha-mixtourist.jpg" style="width: 700px; height: 437px;" /></p>\r\n<p style="text-align: center;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<strong><span style="font-size:16px;">1. Du lịch T&acirc;y Ban Nha m&ugrave;a n&agrave;o đẹp nhất?</span></strong></h2>\r\n<p>\r\n	Với diện t&iacute;ch quốc gia lớn thứ hai của ch&acirc;u &Acirc;u, c&ugrave;ng với địa h&igrave;nh dạng n&ecirc;n kh&iacute; hậu T&acirc;y Ban Nha c&oacute; kh&aacute; nhiều kiểu kh&aacute;c nhau.</p>\r\n<p>\r\n	- Khu vực ven biển ph&iacute;a Đ&ocirc;ng v&agrave; Nam do gần biển Địa Trung hải n&ecirc;n chịu ảnh hưởng kh&aacute; lớn với kiểu thời tiết quanh năm v&ocirc; c&ugrave;ng dễ chịu.</p>\r\n<p>\r\n	- Khu vực ph&iacute;a Bắc c&oacute; nhiệt độ lạnh hơn ph&iacute;a nam trong khi khu vực trung t&acirc;m T&acirc;y Bạn rất kh&ocirc; v&agrave; n&oacute;ng.</p>\r\n<p>\r\n	Nếu bạn muốn tận hượng một kỳ nghỉ tr&ecirc;n b&atilde;i biển ngập nắng th&igrave; khoảng thời gian th&iacute;ch hợp nhất cho bạn l&agrave; từ th&aacute;ng 6 đến th&aacute;ng 8. Tuy nhi&ecirc;n đ&oacute; cũng l&agrave; l&uacute;c lượng kh&aacute;ch du lịch đổ về đ&acirc;y đ&ocirc;ng nhất, bởi vậy c&oacute; thể bạn sẽ phải đối mặt với gi&aacute; cả tăng cao hơn b&igrave;nh thường.</p>\r\n<p>\r\n	C&ograve;n c&aacute;c khoảng thời gian cuối m&ugrave;a xu&acirc;n hoặc cuối m&ugrave;a thu, tức l&agrave; v&agrave;o khoảng th&aacute;ng 4 hay th&aacute;ng 10 h&agrave;ng năm l&agrave; m&ugrave;a trung b&igrave;nh, kh&aacute;ch du lịch c&oacute; giảm tuy nhi&ecirc;n thời tiết vẫn r&acirc;t dễ chịu, phong cảnh T&acirc;y Ban Nha thời điểm n&agrave;y vẫn rất đẹp.</p>\r\n<p style="text-align: justify;">\r\n	<em>&gt;&gt; C&ugrave;ng lướt qua một v&agrave;i n&eacute;t nổi bật về kh&iacute; hậu v&agrave; cảnh quan tuyệt vời tại T&acirc;y B&acirc;n Nha trong video dưới đ&acirc;y nh&eacute;!</em></p>\r\n<p style="text-align: center;">\r\n	<iframe frameborder="0" height="250" longdesc="Khám phá Tây Ban Nha cùng MixTourist" name="Khám phá Tây Ban Nha cùng MixTourist" scrolling="no" src="https://www.youtube.com/embed/ul6Grif_Qw4" title="Khám phá Tây Ban Nha cùng MixTourist" width="70%"></iframe></p>\r\n<p style="text-align: center;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<strong><span style="font-size:16px;">2. H&agrave;nh l&yacute; mang theo</span></strong></h2>\r\n<p style="text-align: justify;">\r\n	<em>N&ecirc;n chuẩn bị những g&igrave; khi đi&nbsp;du lịch T&acirc;y Ban Nha?</em> Nếu kh&ocirc;ng t&igrave;m hiểu kỹ c&agrave;ng một ch&uacute;t th&igrave; c&acirc;u hỏi n&agrave;y sẽ khiến bạn loay hoay trong chuyến du h&agrave;nh lần đầu t&igrave;m tới với đất nước xinh đẹp n&agrave;y đấy.&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Khi sắp xếp h&agrave;nh l&yacute; của m&igrave;nh bạn n&ecirc;n mang theo những vật dụng cần thiết cho chuyến đi của m&igrave;nh: khăn mặt, b&agrave;n chải, dao cạo r&acirc;u, thuốc dự ph&ograve;ng&hellip; quần &aacute;o chỉ n&ecirc;n mang vừa phải v&agrave; nhớ rằng ch&uacute;ng phải thật sự k&iacute;n đ&aacute;o.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Du lịch Tây Ban Nha nên mang gì?" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/taybannha/du-lich-tay-ban-nha-nen-mang-gi-mixtourist.jpg" style="width: 700px; height: 465px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Trong h&agrave;nh l&yacute; x&aacute;ch tay khi mang tới <strong>T&acirc;y Ban Nha</strong> bạn kh&ocirc;ng n&ecirc;n mang theo c&aacute;c thứ đồ sắc nhọn như dao, k&eacute;o hay cắt m&oacute;ng tay&hellip; Với c&aacute;c sản phẩm như cream, nướng uống, dầu gội, nước hoa, sữa tắm cũng vậy.</p>\r\n<p style="text-align: justify;">\r\n	C&aacute;c vật dụng c&oacute; gi&aacute; trị kh&aacute;c như v&agrave;ng, kim cương, m&aacute;y ảnh, laptop bạn cũng kh&ocirc;ng n&ecirc;n mang theo người v&agrave; cũng kh&ocirc;ng gửi v&agrave;o h&agrave;nh l&yacute; k&yacute; gửi (m&aacute;y t&iacute;nh x&aacute;ch tay v&agrave; m&aacute;y ảnh sẽ kh&ocirc;ng t&iacute;nh v&agrave;o h&agrave;nh l&yacute; x&aacute;ch tay của qu&yacute; kh&aacute;ch).</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<em>&gt;&gt; Ngo&agrave;i ra, bạn h&atilde;y xem ngay <a href="http://dulichaustralia.com.vn/news/415/225/10-sai-lam-nghiem-trong-ma-moi-du-khach-khi-di-du-lich-uc-deu-mac-phai.html"><strong>những sai l&agrave;m m&agrave; mọi du kh&aacute;ch đều mắc phải khi đi du lịch nước nước ngo&agrave;i</strong></a> tại đ&acirc;y để hạn chế thấp nhất những rủi ro trong chuyến du h&agrave;nh của bạn!&nbsp;</em></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<strong><span style="font-size:16px;">3. Ăn uống ở T&acirc;y Ban Nha</span></strong></h2>\r\n<p style="text-align: justify;">\r\n	C&oacute; một số thứ bạn phải thử qua khi theo ch&acirc;n một&nbsp;<strong>tour du lịch T&acirc;y Ban Nha</strong>&nbsp;m&agrave; t&igrave;m đến với đất nước b&ograve; t&oacute;t xa x&ocirc;i n&agrave;y:</p>\r\n<p style="text-align: justify;">\r\n	- Sangria &ndash; đ&acirc;y l&agrave; loại rượu đặc trưng T&acirc;y Ban Nha c&oacute; độ cồn rất cao, n&oacute; được l&agrave;m tr&aacute;i c&acirc;y tươi ng&acirc;m như d&acirc;u, cam, đ&agrave;o, mơ...</p>\r\n<p style="text-align: justify;">\r\n	- Thịt giăm b&ocirc;ng Jamon Serrano</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="ẩm thực tây ban nha - mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/taybannha/am-thuc-tay-ban-nha-mixtourist.jpg" style="width: 700px; height: 466px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	- Bia chanh (Shandy) &ndash; l&agrave; loại bia giải kh&aacute;t trong những ng&agrave;y h&egrave; n&oacute;ng oi ả ở T&acirc;y Ban Nha</p>\r\n<p style="text-align: justify;">\r\n	- Rượu vang Rioja: Đ&acirc;y l&agrave; loại rượu được ưa th&iacute;ch nhất tại T&acirc;y Ban Nha bởi hương vị tuyệt vời, độc đ&aacute;o một điều l&agrave; n&oacute; đượ đựng trong những chiếc t&uacute;i da thật.</p>\r\n<p style="text-align: justify;">\r\n	- Rượu vang Bồ Đ&agrave;o Nha, đặc biệt l&agrave; loại rượu vang ngọt porto.</p>\r\n<p style="text-align: justify;">\r\n	Được du kh&aacute;ch đặc biệt y&ecirc;u th&iacute;ch khi <strong>du lịch đến T&acirc;y Ban Nha</strong> l&agrave; nh&agrave; h&agrave;ng Casa Bot&iacute;n, đ&acirc;y l&agrave; nh&agrave; h&agrave;ng l&acirc;u đời nhất li&ecirc;n tục hoạt động từ năm 1725 tại th&agrave;nh phố Madrid, T&acirc;y Ban Nha. Đại văn h&agrave;o nổi tiếng to&agrave;n thế giới Earnest Hemmingway lu&ocirc;n gh&eacute; lại đ&acirc;y bởi đ&acirc;y l&agrave; nơi y&ecirc;u th&iacute;ch của &ocirc;ng, m&oacute;n ăn ngon nhất v&agrave; nổi tiếng nhất tại đ&acirc;y l&agrave; <em>m&oacute;n heo sữa quay</em>.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>4. Mua g&igrave; ở T&acirc;y Ban Nha?</strong></span></h2>\r\n<p>\r\n	T&acirc;y Ban Nha sử dụng loại đồng tiền chung của ch&acirc;u &Acirc;u l&agrave; EUR, 1 EUR = 1.2 USD.</p>\r\n<p style="text-align: justify;">\r\n	Những vật dụng đặc biệt dưới đ&acirc;y c&oacute; thể sẽ khiến bạn th&iacute;ch th&uacute; khi tới T&acirc;y Ban Nha:</p>\r\n<p style="text-align: justify;">\r\n	- Đĩa Flamenco của những nghệ sĩ t&ecirc;n tuổi của T&acirc;y Ban Nha như Paco de Lucia</p>\r\n<p style="text-align: justify;">\r\n	- Cửa h&agrave;ng Capas Sese&ntilde;a (Madrid): nh&atilde;n hiệu b&aacute;n lẻ duy nhất chỉ sản xuất &aacute;o cho&agrave;ng. Giới nh&agrave; gi&agrave;u v&agrave; người nổi tiếng tr&ecirc;n thế giới đều l&agrave; fan ruột của nh&atilde;n hiệu n&agrave;y.</p>\r\n<p style="text-align: justify;">\r\n	- &Aacute;o, quả b&oacute;ng c&oacute; chữ k&yacute; của c&aacute;c cầu thủ hoặc qu&agrave; lưu niệm của c&aacute;c CLB nổi tiếng của Real Madrid hay Barcelona tại c&aacute;c bảo t&agrave;ng trong s&acirc;n vận động</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="mua gì khi đi du lịch tây ban nha - mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/taybannha/mua-sam-khi-di-du-lich-o-tay-ban-nha.jpg" style="width: 700px; height: 350px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<em>&gt;&gt; Xem ngay c&aacute;c <a href="http://dulichchauau.org/du-lich-tay-ban-nha/"><strong>tour du lịch T&acirc;y Ban Nha</strong></a> hấp dẫn <a href="http://dulichchauau.org/du-lich-tay-ban-nha/"><strong>tại đ&acirc;y</strong></a> hoặc c&aacute;c <a href="http://dulichchauau.org/du-lich-chau-au/"><strong>tour du lịch ch&acirc;u &Acirc;u</strong></a> được y&ecirc;u th&iacute;ch nhất của MixTourist&nbsp;<strong><a href="http://dulichchauau.org/du-lich-chau-au/">tại đ&acirc;y</a>&nbsp;</strong>để nhận được nhiều ưu đ&atilde;i lớn của ch&uacute;ng t&ocirc;i trong năm 2017</em></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>5. Nắm r&otilde; th&ocirc;ng tin Đại sứ qu&aacute;n Việt Nam</strong></span></h2>\r\n<p>\r\n	Kh&ocirc;ng ai c&oacute; thể chắc chắn rằng trong chuyến du lịch của bạn sẽ kh&ocirc;ng gặp phải những vấn đề ph&aacute;t sinh bất ngờ như đ&aacute;nh mất giấy tờ hoặc mất h&agrave;nh l&yacute; hay bất cứ vấn đề cần thiết kh&aacute;c.&nbsp;</p>\r\n<p>\r\n	Vậy nơi đầu ti&ecirc;n ch&uacute;ng ta cần phải t&igrave;m đến ch&iacute;nh l&agrave; Đại sứ qu&aacute;n Việt nam tại TBN. H&atilde;y &quot;note&quot; ngay th&ocirc;ng tin dưới đ&acirc;y v&agrave;o &quot;bộ nhớ&quot; của bạn nh&eacute;!</p>\r\n<p>\r\n	Đại sứ qu&aacute;n Việt Nam tại Madrid, T&acirc;y Ban Nha.</p>\r\n<p>\r\n	<strong>Địa chỉ:</strong> C/ Arturo Soria 201, 1-AyB 28043 - Madrid, SPAIN</p>\r\n<p>\r\n	<strong>Điện thoại: </strong>(3491) 510 2867</p>\r\n<p>\r\n	<strong>Fax:</strong> (3491) 415 7067</p>\r\n<p>\r\n	<strong>Email: </strong>claudiomes@yahoo.com<br />\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Nếu bạn c&oacute; bất cứ thắc mắc g&igrave; về <strong>th&ocirc;ng tin du lịch T&acirc;y Ban Nha</strong> hoặc thủ tục l&agrave;m <a href="http://dulichchauau.org/dich-vu/visa--ho-chieu/thu-tuc-xin-visa-di-tay-ban-nha.html"><strong>visa đi T&acirc;y Ban Nha</strong></a> th&igrave; h&atilde;y li&ecirc;n hệ ngay tới văn ph&ograve;ng của <strong><a href="http://dulichchauau.org/">C&ocirc;ng ty du lịch chuy&ecirc;n tour ch&acirc;u &acirc;u</a> MixTourist</strong> qua hotline <strong>04.62814340</strong> để được tư vấn ho&agrave;n to&agrave;n miễn ph&iacute; nh&eacute;!</p>\r\n', 'Tổng hợp kinh nghiệm du lịch Tây Ban Nha chi tiết nhất 2017', 'kinh nghiệm du lịch tây ban nha, du lịch tây ban nha mua gì, du lịch tây ban nha mùa nào đẹp nhất', 'Xứ sở bò tót - Tây Ban Nha luôn hấp dẫn du khách với bao điều kỳ bí. Mix Tourist xin chia sẻ một chút kinh nghiệm du lịch Tây Ban Nha giúp bạn có thể chủ động hơn trong h', '2017-01-14 08:40:29'),
(10, 1, 'Rợn gáy trước những điểm đến hoang sơ mà vẫn hút khách du lịch ở Anh', 'ron-gay-truoc-nhung-diem-den-hoang-so-ma-van-hut-khach-du-lich-o-anh', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/anh/New-Forest-Dorset-Anh-diem-den-hoang-so-o-anh-mixtourist.jpg', 541, '<p style="text-align: justify;">\r\n	Đa phần kh&aacute;ch&nbsp;<strong><a href="http://demo.mixtourist.com/tour/with/Tour-du-lich-Anh-London---Liverpool---Manchester/116">du lịch Anh</a></strong>&nbsp;thường chọn tới những th&agrave;nh phố lớn nơi c&oacute; những c&ocirc;ng tr&igrave;nh đồ sộ in hằng thời gian, những con đường, g&oacute;c phố cổ k&iacute;nh v&agrave; cả những n&eacute;t văn h&oacute;a từ truyền thống tới hiện đại của cong người nơi đ&acirc;y.</p>\r\n<p style="text-align: justify;">\r\n	Thế nhưng bạn c&oacute; biết rằng nước Anh cũng ch&iacute;nh l&agrave; nơi sở hữu rất nhiều những điểm đến hoang d&atilde; đến r&ugrave;ng rợn với c&aacute;c c&acirc;u chuyện b&iacute; ẩn xoay quanh khiến người nghe cảm thấy bị &aacute;m ảnh.</p>\r\n<p style="text-align: justify;">\r\n	Nếu bạn mang trong m&igrave;nh d&ograve;ng m&aacute;u ưa th&aacute;m hiểm v&agrave; kh&aacute;m ph&aacute; th&igrave; b&agrave;i viết n&agrave;y sẽ chỉ ra cho bạn v&agrave;i điểm đến c&oacute; thể thỏa m&atilde;n gi&uacute;p bạn c&aacute;i niềm đam m&ecirc; đ&oacute; đấy.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<em>&gt;&gt; Chi&ecirc;m ngưỡng ngay top 5 <a href="http://dulichchauau.org/tin-tuc/du-lich-anh/top-5-di-san-thien-nhien-the-gioi-tuyet-voi-nhat-tai-chau-au.html"><strong>di sản thi&ecirc;n nhi&ecirc;n thế giới tại ch&acirc;u &Acirc;u</strong></a> tuyệt vời nhất</em></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>1. G&ograve; đất The Rumps, Polzeath, Cornwall</strong></span></h2>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/anh/the-Rumps-Polzeath-Cornwall-du-lich-anh-mixtourist.jpg" style="width: 600px; height: 480px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<strong>The Rumps</strong> tựa m&igrave;nh ven Đại T&acirc;y Dương thuộc v&ugrave;ng biến ph&iacute;a bắc xứ Cornwall. Nơi đ&acirc;y đ&iacute;ch thực l&agrave; một khu vực hoang d&atilde; theo đ&uacute;ng nghĩa của n&oacute; với nhiều loại động vật sinh sống.</p>\r\n<p style="text-align: justify;">\r\n	Cuộc h&agrave;nh tr&igrave;nh trinh phục tuyệt vời n&agrave;y sẽ khởi đầu từ thị trấn Polzeath v&agrave; đi dọc theo vệt vẽ quanh co của đường bờ biển, sau đ&oacute; sẽ đưa bạn đến với Pentire v&agrave; điểm cuối c&ugrave;ng ch&iacute;nh l&agrave; The Rumps, bạn sẽ tự m&igrave;nh leo l&ecirc;n những g&ograve; đất đu m&igrave;nh ra đại dương v&agrave; tha hồ chi&ecirc;m ngưỡng khung cảnh h&ugrave;ng vĩ của nơi n&agrave;y.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<strong><span style="font-size:16px;">2. Th&agrave;nh phố Oban v&agrave; Isles, Argyllshire, Scotland</span></strong></h2>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Thành phố Oban và Isles, Argyllshire, Scotland" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/scotland/thanh-pho-Oban-va-Isles-Argyllshire-Scotland-du-lich-sctoland-mixtourist.jpg" style="width: 600px; height: 402px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Phải n&oacute;i rằng <strong>th&agrave;nh phố Oban</strong> ch&iacute;nh l&agrave; nơi tập chung nhiều thắng cảnh đẹp mắt nhất của đất nước Scotland v&agrave; một phần c&ograve;n x&oacute;t lại của vương quốc Anh. C&oacute; kh&aacute; nhiều điều để bạn c&oacute; thể trải nghiệm tại đ&acirc;y, v&iacute; như tham quan những ngọn n&uacute;i, trekking trong rừng hay thong dong ngắm c&aacute;i nhộn nhịp ở những h&ograve;n đảo rồi thả lỏng m&igrave;nh thư gi&atilde;n tại c&aacute;c bờ biển gồ ghề&hellip;</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<em>&gt;&gt; Đăng k&yacute; ngay <a href="http://dulichchauau.org/du-lich-scotland/"><strong>tour du lịch Scotland</strong></a> để kh&aacute;m ph&aacute; v&agrave; tận hưởng cuộc sống trong <strong>th&agrave;nh phố Oban</strong> đẹp nhất Scotland<strong> <a href="http://dulichchauau.org/du-lich-scotland/">tại đ&acirc;y</a></strong> nh&eacute;!</em></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>3. Vịnh Bullslaughter, Pembrokeshire, Wales</strong></span></h2>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Vịnh Bullslaughter, Pembrokeshire, Wales" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/anh/vinh-Bullslaughter-Pembrokeshire-Wales-du-lich-anh-mixtourist.jpg" style="width: 600px; height: 419px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Mang trong m&igrave;nh những mỏm đ&aacute; treo leo c&ugrave;ng những b&atilde;i biển khiến h&igrave;nh ảnh n&agrave;y tạo n&ecirc;n sự thu h&uacute;t với kh&aacute;ch du lịch cũng như l&agrave; n&eacute;t đặc trưng cho <strong>vịnh Bullslaughter</strong>.</p>\r\n<p style="text-align: justify;">\r\n	Những mỏm đ&aacute; tại đ&acirc;y ho&agrave;n to&agrave;n được h&igrave;nh th&agrave;nh do tự nhi&ecirc;n v&igrave; thế n&ecirc;n ngắm nh&igrave;n ch&uacute;ng c&ugrave;ng những đợt s&oacute;ng cuộn m&igrave;nh mạnh mẽ k&eacute;o d&agrave;i ra xa t&iacute;t bạn sẽ c&oacute; một cảm gi&aacute;c v&ocirc; c&ugrave;ng kỳ vĩ. Điều m&agrave; c&aacute;c vị kh&aacute;ch&nbsp;<strong>du lịch Ch&acirc;u &Acirc;u</strong>&nbsp;ấn tượng với nơi n&agrave;y l&agrave; khung cảnh vịnh khi được bao phủ bởi &aacute;nh ho&agrave;ng h&ocirc;n loang lổ b&oacute;ng, v&ocirc; c&ugrave;ng n&ecirc;n thơ v&agrave; l&atilde;ng mạn</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>4. Yorkshire Dales, Anh</strong></span></h2>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/anh/Yorkshire-Dales-Anh-diem-den-hoang-so-o-anh-mixtourist.jpg" style="width: 600px; height: 480px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	C&aacute;c c&aacute;c th&agrave;nh phố lớn của miền Bắc kh&ocirc;ng xa l&agrave; mấy nhưng <strong>Yorkshire Dales</strong> vẫn c&ograve;n mang trong m&igrave;nh những n&eacute;t đẹp y&ecirc;n b&igrave;nh đặc trưng.</p>\r\n<p style="text-align: justify;">\r\n	Ở đ&acirc;y đa phần l&agrave; c&aacute;c v&ugrave;ng n&ocirc;ng th&ocirc;n, nếu đi dọc theo tuyến đường từ&nbsp; Skipton về Bolton Abbey v&agrave; l&ecirc;n v&ugrave;ng Wharfdale, ngo&agrave;i c&aacute;i kh&ocirc;ng gian y&ecirc;n tĩnh, tho&aacute;ng đ&atilde;ng bạn sẽ c&oacute; cơ hội chi&ecirc;m ngưỡng v&agrave; kh&aacute;m ph&aacute; những phong cảnh đẹp nhất của xứ sở duy&ecirc;n d&aacute;ng n&agrave;y.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<strong><span style="font-size:16px;">5. New Forest, Dorset, Anh</span></strong></h2>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="New Forest, Dorset, Anh" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/anh/New-Forest-Dorset-Anh-diem-den-hoang-so-o-anh-mixtourist.jpg" style="width: 600px; height: 350px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Phủ k&iacute;n hơn 500km2 rừng ph&iacute;a Đ&ocirc;ng Nam nước Anh, <strong>New Forest</strong> l&agrave; nơi cư ngụ của rất nhiều loại động vật hoang d&atilde; sinh sống, nhiều nhất l&agrave; ngựa hoang, cũng c&oacute; một số lo&agrave;i qu&yacute; hiếm xem đ&acirc;y l&agrave; nơi an to&agrave;n của m&igrave;nh m&agrave; t&igrave;m về như thằn lằn c&aacute;t, lo&agrave;i động vật gần như tuyệt chủng v&agrave; đ&atilde; được liệt v&agrave;o s&aacute;ch Đỏ thế giới.</p>\r\n<p style="text-align: justify;">\r\n	Bạn c&oacute; thể ngồi tr&ecirc;n lưng ngựa, băng xuy&ecirc;n qua những cong đường m&ograve;n v&ocirc; c&ugrave;ng đẹp ao quanh khu rừng v&agrave; trước những cảnh đẹp bạn đi qua t&ocirc;i chắc hẳn bạn sẽ phải ngạc nhi&ecirc;n trước vẻ đẹp ch&uacute;ng.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<em>&gt;&gt; Bạn c&oacute; thấy ngỡ ng&agrave;ng trước những cảnh đẹp hoang sơ, b&iacute; ẩn nhưng lại v&ocirc; c&ugrave;ng hấp dẫn đ&oacute; kh&ocirc;ng? H&atilde;y đăng k&yacute; ngay một chuyến đi <a href="http://dulichchauau.org/du-lich-anh/"><strong>du lịch nước Anh</strong></a> để tận mắt chứng kiến ch&uacute;ng nh&eacute;! Chắc chắn bạn sẽ c&oacute; những trải nghiệm v&ocirc; c&ugrave;ng th&uacute; vị đấy.</em></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<strong>Mọi th&ocirc;ng tin chi tiết về c&aacute;c&nbsp;<a href="http://dulichchauau.org/">tour du lịch Ch&acirc;u &Acirc;u</a>&nbsp;kh&aacute;c xin qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ tới văn ph&ograve;ng c&ocirc;ng ty du lịch chuy&ecirc;n tour ch&acirc;u &Acirc;u MixTourist &nbsp;qua hotline 04.6281 4340 để được hỗ trợ miễn ph&iacute;.</strong></p>\r\n', 'Rợn gáy trước những điểm đến hoang sơ mà vẫn hút khách du lịch ở Anh', 'điểm đến hoang sơ ở anh, cảnh đẹp nước anh, du lịch nước anh', 'Bạn có biết rằng nước Anh là nơi sở hữu rất nhiều những điểm đến hoang dã đến rùng rợn với các câu chuyện bí ẩn xoay quanh khiến người nghe cảm thấy bị ám ảnh.', '2017-01-04 11:45:38'),
(11, 4, 'Thật đáng tiếc nếu đi du lịch Ý mà không ghé thăm những địa danh này', 'that-dang-tiec-neu-di-du-lich-y-ma-khong-ghe-tham-nhung-dia-danh-nay', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/thap-nghieng-pisa-du-lich-y-mixtourist.jpg', 510, '<p style="text-align: justify;">\r\n	<strong>Du lịch &Yacute; n&ecirc;n đi đ&acirc;u</strong>? N&oacute;i đến nước &Yacute; l&agrave; n&oacute;i đến những m&oacute;n ẩm thực hấp dẫn những loại rượu nho hảo hạng v&agrave; cả những c&ocirc;ng tr&igrave;nh kiến tr&uacute;c nghệ thuật v&ocirc; c&ugrave;ng đặc sắc. Thật vậy, &nbsp;&Yacute; l&agrave; một trong những quốc gia được UNESSCO c&ocirc;ng nhận rất nhiều những di sản văn h&oacute;a thế giới m&agrave; nổi bật l&agrave; 5 c&ocirc;ng tr&igrave;nh ti&ecirc;u biểu dưới đ&acirc;y.</p>\r\n<p style="text-align: justify;">\r\n	Nếu bạn c&oacute; cơ hội đi <strong>du lịch nước &Yacute;</strong> th&igrave; h&atilde;y gh&eacute; thăm những cảnh đẹp nổi tiếng n&agrave;y nh&eacute; kh&ocirc;ng th&igrave; sẽ đang tiếc lắm đ&oacute;!</p>\r\n<p>\r\n	<em>&gt;&gt; Xem th&ecirc;m: <a href="http://dulichchauau.org/tin-tuc/du-lich-y/phat-them-voi-top-5-mon-an-lam-dang-danh-am-thuc-nuoc-y.html"><strong>Ph&aacute;t th&egrave;m với top 5 m&oacute;n ăn l&agrave;m rạng danh ẩm thực nước &Yacute;</strong></a></em></p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="font-size:16px;"><strong>Những t&ograve;a th&aacute;p San Gimignano</strong></span></h2>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Những tòa tháp San Gimignano" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/nhung-toa-thap-cua-San-Gimignano-mixtourist.jpg" style="width: 600px; height: 399px;" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	San Gimignano l&agrave; một ng&ocirc;i l&agrave;ng thuộc Manhatten, từ l&acirc;u San Gimignano nổi tiếng với 14 t&ograve;a th&aacute;p được x&acirc;y dựng bằng đ&aacute; tượng trưng cho thịnh vượng v&agrave; sức mạnh. C&oacute; khoảng hơn 70 t&ograve;a th&aacute;p được dựng l&ecirc;n ở đ&acirc;y để bảo vệ thị trấn chống lại sự tấn c&ocirc;ng của kẻ th&ugrave;.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="font-size:16px;"><strong>Th&agrave;nh phố Manarola</strong></span></h2>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Thành phố Manarola" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/thanh-pho-cinque-Terre-Italy-mixtourist.jpg" style="width: 600px; height: 450px;" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Mararola l&agrave; một th&agrave;nh phố cổ xưa. Từ rất l&acirc;u Mararola nổi tiếng với vẻ đẹp lung linh, sặc sỡ của những t&ograve;a nh&agrave; đầy m&agrave;u sắc. Được đặt tr&ecirc;n tr&ecirc;n một v&ugrave;ng n&uacute;i đ&aacute; hung vĩ với rất nhiều phong cảnh đẹp thu h&uacute;t rất đ&ocirc;ng kh&aacute;ch&nbsp;<strong><a href="http://demo.mixtourist.com/tour/with/Tour-du-lich-Ha-Noi---y---Tay-Ban-Nha---Phap/116">du lịch &Yacute;</a></strong>&nbsp;đến tham quan v&agrave; chụp ảnh.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="font-size:16px;"><strong>Th&aacute;p Pisa</strong></span></h2>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Tháp Nghiêng Pisa - Du lịch Ý - MixTourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/thap-nghieng-pisa-du-lich-y-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Ngọn th&aacute;p nổi tiếng thế giới, được x&acirc;y dựng trong khoảng thời gian hơn 177 năm. Năm 1173, ngọn th&aacute;p bắt đầu l&uacute;n xuống do nền m&oacute;ng bị xuống cấp. Người ta đ&atilde; kh&ocirc;ng tu sửa hay x&acirc;y dựng trong gần một thế kỷ, sau đ&oacute; t&ograve;a th&aacute;p được c&aacute;c kỹ sư x&acirc;y dựng lại cao hơn v&agrave; ho&agrave;n th&agrave;nh v&agrave;o giữa thế kỷ 14. Năm 2001, t&ograve;a th&aacute;p nổi tiếng n&agrave;y được mở cửa trở lại để ch&agrave;o đ&oacute;n c&aacute;c du kh&aacute;ch đến tham quan.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="font-size:16px;"><strong>Hồ Como</strong></span></h2>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Hồ Como - hồ nước đẹp nhất nước Ý - MixTourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/ho-como-dep-nhat-nuoc-y-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	L&agrave; một trong những hồ nước đẹp nhất nước &Yacute;. Nơi đ&acirc;y nổi tiếng bởi phong cảnh hữu t&igrave;nh v&agrave; kh&ocirc;ng kh&iacute; trong l&agrave;nh cho n&ecirc;n thu h&uacute;t rất đ&ocirc;ng kh&aacute;ch du lịch đến để thư gi&atilde;n, ngắm cảnh. Từ l&acirc;u điểm đến Hồ Como đ&atilde; l&agrave; một nơi kh&ocirc;ng thể thiếu trong mỗi&nbsp;<strong>tour du lịch &Yacute;</strong>&nbsp;của mọi du kh&aacute;ch tr&ecirc;n thế giới.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="font-size:16px;"><strong>Đ&agrave;i v&ograve;ng Colosseum</strong></span></h2>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Đài vòng Colosseum - Cảnh đẹp nước Ý - MixTourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/dai-vong-Colosseum-du-lich-y-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<strong>Colosseum </strong>l&agrave; đ&agrave;i v&ograve;ng rộng nhất v&agrave; nổi tiếng nhất trong số c&aacute;c đ&agrave;i v&ograve;ng của La M&atilde;. N&oacute; được x&acirc;y dựng bởi ho&agrave;ng đế Vespasian thuộc triều đại Flavian v&agrave;o năm 72 sau C&ocirc;ng nguy&ecirc;n v&agrave; được ho&agrave;n th&agrave;nh bởi con trai của &ocirc;ng t&ecirc;n l&agrave; Titus v&agrave;o năm 80 sau C&ocirc;ng nguy&ecirc;n. Colosseum c&oacute; &iacute;t hơn 80 lối v&agrave;o v&agrave; c&oacute; sức chứa 50.000 kh&aacute;n giả.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Bạn c&oacute; thấy m&ecirc; mẩn trước những <strong>cảnh đẹp nước &Yacute;</strong> đ&oacute; kh&ocirc;ng? Tại sao kh&ocirc;ng đến đ&acirc;y thưởng ngoạn v&agrave; nghỉ dưỡng trong những ng&agrave;y đầu xu&acirc;n n&agrave;y nhỉ?</p>\r\n<p style="text-align: justify;">\r\n	<em>&gt;&gt; Đăng k&yacute; ngay <a href="http://dulichchauau.org/du-lich-y/"><strong>tour du lịch &Yacute;</strong></a> đầu năm mới để nhận ngay lỳ x&igrave; 2.000.000 đồng c&ugrave;ng những m&oacute;n qu&agrave; &yacute; nghĩa của <strong><a href="https://www.google.com/maps/place/C%C3%B4ng+ty+C%E1%BB%95+ph%E1%BA%A7n+D%E1%BB%8Bch+v%E1%BB%A5+Th%C6%B0%C6%A1ng+m%E1%BA%A1i+v%C3%A0+Du+l%E1%BB%8Bch+Mix/@21.0104597,105.8096925,18z/data=!4m8!1m2!2m1!1zQ8O0bmcgdHkgQ-G7lSBwaOG6p24gROG7i2NoIHbhu6UgVGjGsMahbmcgbeG6oWkgdsOgIER1IGzhu4tjaCBNaXgsIE5ndXnhu4VuIE5n4buNYyBWxaksIFRydW5nIEhvw6AsIEPhuqd1IEdp4bqleSwgSMOgIE7hu5lpLCBWaeG7h3QgTmFt!3m4!1s0x3135ac9e24c47d8f:0x79c883b18e8635af!8m2!3d21.0106915!4d105.809457">MixTourist</a>&nbsp;</strong>nh&eacute;!</em></p>\r\n', 'Thật đáng tiếc nếu đi du lịch Ý mà không ghé thăm những địa danh này', 'cảnh đẹp nước Ý, vẻ đẹp nước Ý, cảnh đẹp ở Ý, du lịch Ý nên đi đâu, ', 'Nếu bạn có cơ hội đi du lịch nước Ý thì hãy ghé thăm những cảnh đẹp nổi tiếng này nhé không thì sẽ đang tiếc lắm đó!', '2017-01-04 11:06:58');
INSERT INTO `news` (`id`, `danhmuc_id`, `name`, `name_url`, `img`, `view`, `content`, `title`, `keyword`, `description`, `created`) VALUES
(12, 2, '10 bí quyết giúp bạn tận hưởng một kỳ nghỉ hè trọn vẹn tại châu Âu', '10-bi-quyet-giup-ban-tan-huong-mot-ky-nghi-he-tron-ven-tai-chau-au', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/du-lich-phap1-mixtourist.jpg', 556, '<p style="text-align: justify;">\r\n	Bạn muốn <a href="http://dulichchauau.org/"><strong>du lịch ch&acirc;u &Acirc;u</strong></a> trong kỳ nghỉ h&egrave; năm sau? Vậy b&acirc;y giờ ch&iacute;nh l&agrave; l&uacute;c để bạn t&igrave;m hiểu một v&agrave;i th&ocirc;ng tin để chuẩn bị cho chuyến nghỉ đ&oacute;. Khi m&ugrave;a h&egrave; sang l&agrave; thời gian th&iacute;ch hợp cho những kỳ nghỉ dưỡng d&agrave;i ng&agrave;y. Ch&iacute;nh v&igrave; vậy thời điểm n&agrave;y l&agrave; l&uacute;c kh&aacute;ch du lịch dồn về <strong>ch&acirc;u &Acirc;u</strong> l&agrave; đ&ocirc;ng nhất.&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Tuy nhi&ecirc;n bạn cvề đ&aacute;m đ&ocirc;ng du kh&aacute;ch &ugrave;n &ugrave;n t&igrave;m về, vẫn c&oacute; nhiều c&aacute;ch để bạn c&oacute; thể tr&aacute;nh điều đ&oacute;. 10 b&iacute; quyết dưới đ&acirc;y sẽ gi&uacute;p bạn tận hưởng kỳ nghỉ, <strong>kh&aacute;m ph&aacute; Ch&acirc;u &Acirc;u</strong> một c&aacute;ch trọn vẹn nhất</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<em>&gt;&gt; B&agrave;i viết li&ecirc;n quan:&nbsp;<strong><a href="http://dulichchauau.org/tin-tuc/du-lich-anh/top-5-di-san-thien-nhien-the-gioi-tuyet-voi-nhat-tai-chau-au.html">Top 5 di sản thi&ecirc;n nhi&ecirc;n thế giới tuyệt vời nhất tại ch&acirc;u &Acirc;u</a></strong></em></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Du lịch nước Pháp - MixTourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/du-lich-phap1-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>1. Thăm c&aacute;c điểm du lịch v&agrave;o cuối ng&agrave;y</strong></span></h2>\r\n<p style="text-align: justify;">\r\n	Thay v&igrave; những tranh c&atilde;i c&oacute; thể xảy ra trong khi đứng xếp h&agrave;ng mua v&eacute; tham quan trước giờ mở cửa h&atilde;y bắt đầu chuyến tham quan của m&igrave;nh một v&agrave;i tiếng trước giờ đ&oacute;ng cửa. Khi ấy c&aacute;c khu bảo t&agrave;ng, ph&ograve;ng trưng bầy kh&ocirc;ng c&ograve;n nhiều kh&aacute;ch n&ecirc;n bạn tha hồ c&oacute; kh&ocirc;ng gian cho việc ngắm nh&igrave;n mọi thứ.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Bảo tàng Louvre, Pháp - Du lịch châu âu - MixTourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/bao-tang-Louvre-du-lich-phap-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: center;">\r\n	Bảo t&agrave;ng <strong>Louvre</strong> mở cửa tới 22h đ&ecirc;m</p>\r\n<p style="text-align: center;">\r\n	<em>&gt;&gt; Mời bạn tham khảo <a href="http://dulichchauau.org/du-lich-phap/"><strong>tour du lịch Ph&aacute;p</strong></a> tham quan bảo t&agrave;ng Louvre tại đ&acirc;y</em></p>\r\n<p style="text-align: justify;">\r\n	Bạn cũng n&ecirc;n để &yacute; tới lịch mở cửa v&agrave;o ban đ&ecirc;m của một số điểm, nh&igrave;n chung những khu n&agrave;y kh&aacute; y&ecirc;n tĩnh, bạn c&oacute; thể sẽ tr&aacute;nh được những đ&aacute;m kh&aacute;ch đo&agrave;n ồn &agrave;o. Chẳng hạn như bảo t&agrave;ng Louvre (Ph&aacute;p) mở cửa tới tận 22 giờ đ&ecirc;m c&aacute;c ng&agrave;y thứ tư với thứ 6. Bảo t&agrave;ng Anh: 22h30 c&aacute;c ng&agrave;y thứ 5 v&agrave; thứ 6, ph&ograve;ng trưng bầy quốc gia London (Anh): 21h c&aacute;c ng&agrave;y thứ 6.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>2. Tr&aacute;nh n&oacute;ng</strong></span></h2>\r\n<p style="text-align: justify;">\r\n	Ở c&aacute;c nước rất n&oacute;ng, khoảng thời gian giữa ng&agrave;y c&oacute; thể l&agrave; l&uacute;c y&ecirc;n tĩnh nhất. V&iacute; dụ, hầu hết nh&oacute;m kh&aacute;ch tr&aacute;nh đến Thung lũng c&aacute;c vị vua (Ai Cập) suốt đầu buổi trưa oi bức. Nhưng nếu bạn đến đ&acirc;y l&uacute;c đ&oacute;, bạn sẽ thấy nơi đ&acirc;y hầu như kh&ocirc;ng c&oacute; kh&aacute;ch, v&igrave; thế bạn c&oacute; thể ở rất l&acirc;u trong c&aacute;c lăng mộ, kh&ocirc;ng phải chịu đựng &aacute;nh nắng mặt trời ch&oacute;i chang.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>3. Đừng tiếc một v&agrave;i xu cho v&eacute; v&agrave;o cửa miễn ph&iacute;</strong></span></h2>\r\n<p style="text-align: justify;">\r\n	C&oacute; một v&agrave;i bảo t&agrave;ng tại ch&acirc;u &Acirc;u kh&ocirc;ng thu ph&iacute; v&agrave;o cửa v&agrave;o một số ng&agrave;y trong tuần.V&iacute; như <strong>bảo t&agrave;ng Louvre</strong> v&agrave; thứ 6 đầu ti&ecirc;n h&agrave;ng th&aacute;ng miễn ph&iacute; v&eacute; v&agrave;o. Nhưng để tiết kiệm một v&agrave;i EUR th&igrave; bạn lại phải đối mặt với ng&agrave;y đ&ocirc;ng kh&aacute;ch nhất trong th&aacute;ng.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>4. Đặt trước v&eacute; v&agrave;o cửa</strong></span></h2>\r\n<p style="text-align: justify;">\r\n	Một số bảo t&agrave;ng như <strong>ph&ograve;ng trưng b&agrave;y Uffizi ở TP Florence (&Yacute;)</strong> cho ph&eacute;p đặt v&eacute; trước để tham quan sau. Nhờ vậy du kh&aacute;ch sẽ kh&ocirc;ng phải xếp h&agrave;ng mua v&eacute; ở lối v&agrave;o m&agrave; nhiều khi việc n&agrave;y c&oacute; thể l&agrave;m tốn của bạn h&agrave;ng mấy tiếng đồng hồ.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="phong-trung-bay-Uffizi-Florence-italia-du-lich-y-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/phong-trung-bay-Uffizi-Florence-italia-du-lich-y-mixtourist.jpg" style="width: 600px; height: 337px;" /></p>\r\n<p style="text-align: center;">\r\n	<strong>Ph&ograve;ng trưng b&agrave;y Uffizi ở TP Florence (&Yacute;)</strong> cho ph&eacute;p đặt v&eacute; trước</p>\r\n<p style="text-align: center;">\r\n	<em>&gt;&gt; Để tham quan ph&ograve;ng trưng b&agrave;i Uffizi v&agrave; c&aacute;c địa danh tuyệt đẹp kh&aacute;c của nước &Yacute;, h&atilde;y k&yacute; <a href="http://dulichchauau.org/du-lich-y/"><strong>tour du lịch &Yacute;</strong></a> tại đ&acirc;y nh&eacute;!</em></p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>5. Đi cửa sau hoặc cửa b&ecirc;n</strong></span></h2>\r\n<p style="text-align: justify;">\r\n	Việc n&agrave;y sẽ gi&uacute;p du kh&aacute;ch tr&aacute;nh được đ&aacute;m đ&ocirc;ng chen ch&uacute;c hay những h&agrave;ng d&agrave;i người xếp nối đu&ocirc;i nhau ở sảnh ch&iacute;nh. Bạn c&oacute; thể đi theo một lối kh&aacute;c quanh ph&ograve;ng trưng b&agrave;y hay bảo t&agrave;ng m&agrave; kh&ocirc;ng nhất thiết phải v&agrave;o từ cửa ch&iacute;nh.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>6. Tham dự một buổi lễ nh&agrave; thờ</strong></span></h2>\r\n<p style="text-align: justify;">\r\n	Đến thăm c&aacute;c nh&agrave; thờ v&agrave;o lễ ban th&aacute;nh thể, bạn sẽ được trải nghiệm kh&ocirc;ng kh&iacute; đặc biệt ở đ&acirc;y, nhất l&agrave; c&aacute;c nh&agrave; thờ như Notre Dame ở Paris (Ph&aacute;p), San Marco Basilica ở Venice (&Yacute;) hay tu viện Westminster Abbey (Anh). Điều n&agrave;y đ&aacute;ng gi&aacute; hơn việc bạn đi thơ thẩn quanh c&aacute;c gian b&ecirc;n trong gi&aacute;o đường với v&ocirc; số kh&aacute;ch du lịch kh&aacute;c l&uacute;c giữa ng&agrave;y.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Nhà thờ Notre Dame ở Paris (Pháp)" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/nha-tho-Notre%20Dame-phap-mixtourist.jpg" style="width: 600px; height: 386px;" /></p>\r\n<p style="text-align: center;">\r\n	<span style="text-align: justify;">Nh&agrave; thờ Notre Dame ở Paris (Ph&aacute;p)</span></p>\r\n<p style="text-align: center;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>7. Tr&aacute;nh c&aacute;c ng&agrave;y cuối tuần</strong></span></h2>\r\n<p style="text-align: justify;">\r\n	V&agrave;o ng&agrave;y thường &iacute;t nhất bạn sẽ chỉ phải cạnh tranh kh&ocirc;ng gian c&ugrave;ng những du kh&aacute;ch kh&aacute;c. C&ograve;n những ng&agrave;y cuối tuần sẽ c&oacute; nhiều người d&acirc;n địa phương đến tham quan hơn. Hơn nữa, du kh&aacute;ch cần biết rằng ở ch&acirc;u &Acirc;u nhiều bảo t&agrave;ng nh&agrave; nước đ&oacute;ng cửa v&agrave;o c&aacute;c ng&agrave;y thứ hai.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>8. Đi xem những điểm một sao</strong></span></h2>\r\n<p style="text-align: justify;">\r\n	Tất nhi&ecirc;n l&agrave; ai cũng th&iacute;ch ở, du lịch, tham quan những địa điểm 5 sao. Nhưng bạn thử nghĩ xem, sẽ ra sao nếu bạn&nbsp;<strong>du lịch Ph&aacute;p</strong>&nbsp;đến viện bảo t&agrave;ng Louvre lại kh&ocirc;ng được tận mắt ngắm n&agrave;ng Mona Lisa, hay đến Rome m&agrave; bỏ lỡ <strong>nh&agrave; thờ Sistine</strong> nhỏ?</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="nhà thờ Sistine, Pháp - Du lịch pháp - mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/nha-tho-Sistine-du-lich-chau-au-mixtourist.jpg" style="width: 600px; height: 337px;" /></p>\r\n<p style="text-align: center;">\r\n	<span style="text-align: justify;">Nh&agrave; thờ Sistine, Ph&aacute;p</span></p>\r\n<p style="text-align: justify;">\r\n	Nếu bạn đ&atilde; từng thăm những điểm &quot;năm sao&quot; n&agrave;y hoặc bạn th&iacute;ch thưởng thức nghệ thuật đỉnh cao trong kh&ocirc;ng kh&iacute; tĩnh lặng, c&aacute;c ph&ograve;ng trưng b&agrave;y nhỏ hơn c&oacute; thể l&agrave; những gợi &yacute; hấp dẫn hơn nhiều.</p>\r\n<p style="text-align: justify;">\r\n	Chẳng hạn như nếu ngưỡng mộ họa sĩ nổi tiếng người Ph&aacute;p Claude Monet - người s&aacute;ng lập trường ph&aacute;i ấn tượng, du kh&aacute;ch c&oacute; thể t&igrave;m thấy một bộ sưu tập đẹp hơn những bức tranh của &ocirc;ng ở <strong>bảo t&agrave;ng Marmottan Monet</strong> vốn &iacute;t được du kh&aacute;ch đến thăm so với những ph&ograve;ng trưng b&agrave;y đ&ocirc;ng đ&uacute;c của <strong>bảo t&agrave;ng d&#39;Orsay</strong>.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="bảo tàng Marmottan Monet, Pháp - MixTourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/bao-tang-Marmottan%20Monet-phap-mixtourist.jpg" style="width: 600px; height: 436px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	V&agrave; ở Venice, c&aacute;c bức tranh v&agrave; c&ocirc;ng tr&igrave;nh đi&ecirc;u khắc ở Bảo t&agrave;ng vắng kh&aacute;ch Correr cũng th&uacute; vị v&agrave; quan trọng kh&ocirc;ng k&eacute;m c&aacute;c t&aacute;c phẩm tại những Ph&ograve;ng trưng b&agrave;y Accademia.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>9. Thu&ecirc; hướng dẫn vi&ecirc;n</strong></span></h2>\r\n<p style="text-align: justify;">\r\n	Hướng dẫn vi&ecirc;n kh&ocirc;ng chỉ gi&uacute;p bạn cảm nhận trọn vẹn hơn về điểm đến th&ocirc;ng qua việ chỉ bạn đ&acirc;u l&agrave; thời điểm tốt nhất để thăm điểm đ&oacute; m&agrave; c&ograve;n l&agrave; người c&oacute; thể gi&uacute;p bạn tho&aacute;t khỏi những d&ograve;ng người đ&ocirc;ng nghịt chen ch&uacute;c. Việc t&igrave;m ra một hướng dẫn vi&ecirc;n giởi kh&ocirc;ng hề dễ, tốt nhất bạn n&ecirc;n tr&aacute;nh những tay &ldquo;c&ograve; tour&rdquo; b&ecirc;n ngo&agrave;i điểm tham quan. Hầu như c&aacute;c văn ph&ograve;ng du lịch c&aacute;c th&agrave;nh phố sẽ c&oacute; danh s&aacute;ch v&agrave; địa chỉ li&ecirc;n hệ những hướng dẫn vi&ecirc;n tuyệt nhất, bạn c&oacute; thể ho&agrave;n to&agrave;n tin tưởng ở họ v&agrave; thu&ecirc; họ muộn nhất l&agrave; trước 24 giờ.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><strong>10. Hạn chế sử dụng thiết bị hướng dẫn bằng &acirc;m thanh ở nơi đ&ocirc;ng người</strong></span></h2>\r\n<p style="text-align: justify;">\r\n	Bạn c&oacute; thể y&ecirc;n t&acirc;m v&agrave; tin tưởng 100% v&agrave;o c&aacute;c thiết bị hướng dẫn bằng &acirc;m thanh bởi ch&uacute;ng cho ph&eacute;p bạn tham quan v&agrave; thu nạp th&ocirc;ng tin thực tế c&ugrave;ng l&uacute;c. Nhưng bạn lại kh&oacute; tr&aacute;nh khỏi cảnh đ&aacute;m đ&ocirc;ng chen lấn v&agrave;o khoảng thời gian cao điểm bỏi lẽ tất cả mọi người sẽ c&ugrave;ng tụ tập quanh một điểm triển l&atilde;m để nghe thuyết minh. Như vậy bạn sẽ rất kh&oacute; tập trung v&agrave; điều đ&oacute; sẽ ảnh hương đến chuyến du lịch của bạn.</p>\r\n<p style="text-align: center;">\r\n	<em>&gt;&gt; Mọi th&ocirc;ng tin chi tiết về&nbsp;<strong><a href="http://dulichchauau.org/">tour du lịch ch&acirc;u &Acirc;u</a></strong>&nbsp;hay thủ tục <strong><a href="http://dulichchauau.org/news/218/118/Thu-tuc-xin-visa-du-lich-chau-au-Visa-Schengen">visa xin du lịch ch&acirc;u &acirc;u</a></strong>&nbsp;xin qu&yacute; kh&aacute;ch vui l&ograve;ng li&ecirc;n hệ trực tiếp tới văn ph&ograve;ng c&ocirc;ng ty ch&uacute;ng t&ocirc;i &nbsp;qua hotline 04.6281 4340 để được hỗ trợ.</em></p>\r\n<p style="text-align: justify;">\r\n	<strong>Video nổi bật:</strong></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<iframe frameborder="0" height="500" scrolling="no" src="https://www.youtube.com/embed/BkUFjaJ6rD4" width="500"></iframe></p>\r\n', '10 bí quyết giúp bạn tận hưởng một kỳ nghỉ hè trọn vẹn tại châu Âu', 'du lịch châu âu, kinh nghiệm du lịch châu âu, ', 'Bạn muốn du lịch châu Âu trong kỳ nghỉ hè năm sau? Vậy bây giờ chính là lúc để bạn tìm hiểu một vài thông tin để chuẩn bị cho chuyến nghỉ đó', '2017-01-13 08:52:59'),
(13, 1, 'Kinh nghiệm du lịch châu Âu tự túc cực tiết kiệm chi phí', 'kinh-nghiem-du-lich-chau-au-tu-tuc-cuc-tiet-kiem-chi-phi', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/kinh-nghiem-du-lich-chau-au-tu-tuc-mixtourist.jpg', 4359, '<p style="text-align: justify;">\r\n	Kh&ocirc;ng chỉ đối với&nbsp;<strong>du lịch Ch&acirc;u &Acirc;u</strong>&nbsp;m&agrave; du lịch ở đ&acirc;u cũng vậy, vấn đề chi ph&iacute; lu&ocirc;n l&agrave; mối quan t&acirc;m h&agrave;ng đầu cho mọi du kh&aacute;ch đặc biệt l&agrave; những bạn sinh vi&ecirc;n.&nbsp; Vậy l&agrave;m thế n&agrave;o để gh&eacute; thăm &quot;lục địa gi&agrave;&quot; vi vu đ&acirc;y đ&oacute; m&agrave; số tiền bạn bỏ ra thật hợp l&yacute;? L&agrave;m thế n&agrave;o để bạn vẫn cảm thấy thoải m&aacute;i trong suốt cuộc h&agrave;nh tr&igrave;nh m&agrave; chi ph&iacute; kh&ocirc;ng khiến bạn qu&aacute; &ldquo;x&oacute;t xa&rdquo;?</p>\r\n<p style="text-align: center;">\r\n	<img alt="Kinh nghiệm đi du lịch châu âu tự túc - MixTourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/kinh-nghiem-du-lich-chau-au-tu-tuc-mixtourist.jpg" style="width: 600px; height: 399px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Để <strong>Mix Tourist</strong> chia sẻ cho bạn một v&agrave;i <strong>kinh nghiệm du lịch ch&acirc;u &Acirc;u tự t&uacute;c với gi&aacute; rẻ nhất</strong> c&oacute; thể nh&eacute;!</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;"><strong>1. Kinh nghiệm xin visa du lịch ch&acirc;u &Acirc;u tự t&uacute;c</strong></span></span></h2>\r\n<p style="text-align: justify;">\r\n	Kh&ocirc;ng đơn giản như bạn đi thăm c&aacute;c nước thuộc khối Đ&ocirc;ng Nam &Aacute;, c&aacute;c quốc gia tại Ch&acirc;u &Acirc;u hầu như y&ecirc;u cầu Visa kh&aacute; chặt chẽ đối với mọi người c&oacute; &yacute; định v&agrave;o quốc gia của họ.</p>\r\n<p style="text-align: justify;">\r\n	V&igrave; thế điều đầu ti&ecirc;n bạn phải l&agrave;m đ&oacute; l&agrave; liệt k&ecirc; thứ tự c&aacute;c quốc gia bạn muốn đi qua v&agrave; sắp xếp ch&uacute;ng lần lượt theo đ&uacute;ng h&agrave;nh tr&igrave;nh của bạn đ&atilde; dự định, quốc gia n&agrave;o tới trước, quốc gia n&agrave;o đi sau. Điều n&agrave;y rất thuận lợi cho việc bạn xin visa.</p>\r\n<p style="text-align: justify;">\r\n	<em>&gt;&gt; Đặc biệt những quốc gia n&agrave;o c&oacute; d&acirc;n số &iacute;t th&igrave; việc xin visa lại c&agrave;ng dễ v&iacute; dụ như nước Bỉ. Bạn c&oacute; thể xem chi tiết&nbsp;<a href="http://dulichchauau.org/dich-vu/visa--ho-chieu/thu-tuc-lam-ho-so-xin-visa-di-bi.html"><strong>visa&nbsp;du lịch Bỉ</strong></a>&nbsp;tại đ&acirc;y.</em></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Visa du lịch châu âu tự túc - mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/visa-du-lich-chau-au-tu-tuc-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Ngo&agrave;i ra, bạn h&atilde;y tham khảo thật kỹ c&aacute;c <a href="http://dulichchauau.org/dich-vu/visa--ho-chieu/thu-tuc-xin-visa-du-lich-chau-au-visa-schengen.html"><strong>thủ tục xin visa du lịch ch&acirc;u &Acirc;u</strong></a> tại đ&acirc;y để năm r&otilde; c&aacute;c giấy tờ cần thiết, gi&uacute;p cho việc l&agrave;m hồ sơ nhanh hơn hoặc bạn c&oacute; thể li&ecirc;n hệ tới <strong>MixTourist </strong>- <a href="http://dulichchauau.org/"><strong>c&ocirc;ng ty du lịch chuy&ecirc;n tour ch&acirc;u &Acirc;u</strong></a>&nbsp;qua hotline <strong>04 6281 4340</strong>&nbsp;để được hỗ trợ tận t&igrave;nh nhất.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><strong><span style="font-size:16px;">2. Kinh nghiệm di chuyển khi đi du lịch ch&acirc;u &Acirc;u</span></strong></span></h2>\r\n<p style="text-align: justify;">\r\n	Kh&ocirc;ng c&oacute; con đường n&agrave;o nhanh bằng đường chim bay. &Agrave;, &yacute; t&ocirc;i c&oacute; nghĩa l&agrave; đường h&agrave;ng kh&ocirc;ng. V&agrave; một điều dễ hiểu hơn l&agrave; nhanh th&igrave; đi k&egrave;m với đắt v&agrave; điều bạn n&ecirc;n ch&uacute; &yacute; về lời khuy&ecirc;n của ch&uacute;ng t&ocirc;i trong mục n&agrave;y l&agrave; &gt;&gt;<strong>săn <a href="http://www.azbooking.vn">v&eacute; m&aacute;y bay gi&aacute; rẻ</a>&lt;&lt;</strong></p>\r\n<p style="text-align: justify;">\r\n	Bạn n&ecirc;n t&igrave;m những h&atilde;ng h&agrave;ng kh&ocirc;ng kh&ocirc;ng chuy&ecirc;n với gi&aacute; v&eacute; phải chăng. Dưới đ&acirc;y l&agrave; một số website tham khảo d&agrave;nh cho bạn&nbsp;như:</p>\r\n<p style="text-align: justify;">\r\n	&gt;&gt; <a href="http://vemaybay.azbooking.vn">www.vemaybay.azbooking.vn</a></p>\r\n<p style="text-align: justify;">\r\n	&gt;&gt;&nbsp;<a href="http://www.raynair.co.uk">www.raynair.co.uk</a></p>\r\n<p style="text-align: justify;">\r\n	&gt;&gt; <a href="http://www.easyjet.com">www.easyjet.com</a></p>\r\n<p style="text-align: justify;">\r\n	&gt;&gt; <a href="http://www.airberlin.com">www.airberlin.com</a></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Kinh nghiệm di chuyển khi đi du lịch châu âu - mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/kinh-nghiem-di-chuyen-khi-di-du-lich-chau-au-mixtourist.jpg" style="width: 600px; height: 450px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<u><strong>Lưu &yacute;:</strong></u></p>\r\n<p style="text-align: justify;">\r\n	- Phải đặt v&eacute; trước ng&agrave;y khởi h&agrave;nh &iacute;t nhất 2 tuần.</p>\r\n<p style="text-align: justify;">\r\n	- Sẽ th&ecirc;m chi ph&iacute; vận chuyển nếu bạn đặt gi&aacute; v&eacute; rẻ bởi s&acirc;n bay v&agrave; trung t&acirc;m th&agrave;nh phố sẽ ở c&aacute;ch xa nhau.</p>\r\n<p style="text-align: justify;">\r\n	- Chi ph&iacute; h&agrave;nh l&yacute; qu&aacute; cước rất cao, trong khi khối lượng h&agrave;nh l&yacute; được ph&eacute;p mang kh&aacute; &iacute;t n&ecirc;n h&agrave;nh l&yacute; bạn mang theo c&agrave;ng &iacute;t c&agrave;ng tốt.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Kinh nghiệm đi xe lửa khi đi du lịch châu Âu - MixTourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/kinh-nghiem-di-chuyen-bang-xe-lua-khi-di-du-lich-chau-au-mixtourist.jpg" style="width: 600px; height: 376px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Ngo&agrave;i ra, nếu muốn chi ph&iacute; thấp hơn, bạn c&oacute; thể sử dụng xe lửa. Sẽ mất kh&aacute; nhiều thời gian cho mấy loại h&igrave;nh n&agrave;y đấy nhưng b&ugrave; lại bạn c&oacute; thể ngắm nh&igrave;n to&agrave;n bộ khung cảnh tr&ecirc;n suốt qu&atilde;ng đường bạn đi. C&ograve;n g&igrave; tuyệt vời hơn thế đ&uacute;ng kh&ocirc;ng?</p>\r\n<p style="text-align: justify;">\r\n	Một số địa chỉ Website của c&aacute;c h&atilde;ng vận chuyển đường sắt v&agrave; Coach d&agrave;nh cho bạn như:</p>\r\n<p style="text-align: justify;">\r\n	&gt;&gt; Coach: <a href="http://www.eurolines.com">www.eurolines.com</a></p>\r\n<p style="text-align: justify;">\r\n	&gt;&gt; Xe lửa: <a href="http://www.raileurope.com">www.raileurope.com</a></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><strong><span style="font-size:16px;">3. Kinh nghiệm lưu tr&uacute; tại ch&acirc;u &Acirc;u</span></strong></span></h2>\r\n<h3 style="text-align: justify;">\r\n	<span style="font-size:14px;">- Nhờ người quen hoặc người bạn của bạn:</span></h3>\r\n<p style="text-align: justify;">\r\n	Sẽ thật tuyệt khi c&oacute; một người quen tại đất nước m&agrave; bạn sẽ đến, điều đ&oacute; gi&uacute;p bạn tiết kiệm được số tiền kh&ocirc;ng phải l&agrave; nhỏ đ&acirc;u, mặt kh&aacute;c mọi thứ đối với bạn cũng dễ d&agrave;ng hơn nhiều. C&ograve;n kh&ocirc;ng việc nhờ bạn b&egrave; của bạn b&egrave; m&igrave;nh cũng l&agrave; một giải ph&aacute;p hay. Đừng ngại, biết đ&acirc;u đấy ngo&agrave;i việc c&oacute; th&ecirc;m một v&agrave;i người bạn mới th&igrave; bạn c&ograve;n c&oacute; một hướng dẫn vi&ecirc;n miễn ph&iacute;. Kh&aacute; tuyệt phải kh&ocirc;ng&nbsp;?</p>\r\n<h3 style="text-align: justify;">\r\n	<span style="font-size:14px;">- &nbsp;Ở tại c&aacute;c ph&ograve;ng Information Desk ở s&acirc;n bay:</span></h3>\r\n<p style="text-align: justify;">\r\n	Ngo&agrave;i ra nếu bạn kh&ocirc;ng thể t&igrave;m hiểu từ trước đ&oacute; được th&igrave; bạn cũng n&ecirc;n hỏi về gi&aacute; cả về nh&agrave; trọ, kh&aacute;ch sạn sao cho hợp l&yacute;, hay c&aacute;ch li&ecirc;n lạc với Đại Sứ Qu&aacute;n, L&atilde;nh Sự Qu&aacute;n tại quốc gia bạn đến tại c&aacute;c ph&ograve;ng Information Desk ở s&acirc;n bay. Nếu kh&ocirc;ng bạn c&oacute; thể lướt qua một v&agrave;i th&ocirc;ng tin tại website&nbsp;: <a href="http://www.s-h-system.co.uk">www.s-h-system.co.uk</a></p>\r\n<h3 style="text-align: justify;">\r\n	<span style="font-size:14px;">- Ở ph&ograve;ng tập thể:</span></h3>\r\n<p style="text-align: justify;">\r\n	C&ograve;n bạn m&agrave; đi theo đo&agrave;n th&igrave; c&agrave;ng đơn giản, bạn c&oacute; thể thu&ecirc; ph&ograve;ng tập thể từ 6 tới 12 người. Y&ecirc;n t&acirc;m, kh&ocirc;ng giống Việt Nam ch&uacute;ng ta l&agrave; th&ecirc;m người th&ecirc;m tiền đ&acirc;u, Ch&acirc;u &Acirc;u c&agrave;ng đ&ocirc;ng th&igrave; số tiền phải bỏ ra c&agrave;ng rẻ.</p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="Kinh nghiệm lưu trú khi đi du lịch châu âu - mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/kinh-nghiem-lu-tru-o-chau-au-mixtourist.jpg" style="width: 600px; height: 337px;" /></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><strong><span style="font-size:16px;">4. Kinh nghiệm mang h&agrave;nh l&yacute;</span></strong></span></h2>\r\n<p style="text-align: justify;">\r\n	Một lời khuy&ecirc;n nếu bạn kh&ocirc;ng muốn mất nhiều tiền v&agrave;o khoản n&agrave;y nh&eacute; th&igrave; n&ecirc;n &laquo;<strong>C&agrave;ng gọn nhẹ c&agrave;ng tốt</strong>&raquo;. Ngo&agrave;i những vật dụng cần thiết ra th&igrave; bạn cần mang theo một t&uacute;i ngủ đề ph&ograve;ng kh&ocirc;ng thu&ecirc; được nh&agrave; trọ, một chiếc d&ugrave;&nbsp; trong m&ugrave;a mưa, v&agrave; một v&agrave;i loại thuốc th&ocirc;ng thường như&nbsp;: thuốc đau bụng, thuốc cảm c&uacute;m, ho, nhức đầu&hellip; đề ph&ograve;ng kh&ocirc;ng hợp thời tiết hoặc những m&oacute;n ăn tại nước bạn&hellip;</p>\r\n<p style="text-align: justify;">\r\n	Bạn c&oacute; thể tham khảo kinh nghiệm đ&oacute;ng g&oacute;i h&agrave;nh l&yacute; th&ocirc;ng minh tại video dưới đ&acirc;y nh&eacute;!</p>\r\n<p style="text-align: center;">\r\n	<iframe frameborder="0" height="250" longdesc="Kinh nghiệm đóng dói hành lý khi đi du lịch" scrolling="no" src="https://www.youtube.com/embed/P5WzKVXVkB4" width="70%"></iframe></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	&gt;&gt; Hoặc để tiết kiệm chi ph&iacute; hơn nữa, h&atilde;y tham gia tour du lịch ch&acirc;u &acirc;u theo phong c&aacute;ch của <strong>MixTourist</strong>. Tự thiết kế tour ri&ecirc;ng theo &yacute; m&igrave;nh chỉ với gi&aacute; <span style="color:#0000ff;"><strong>73$/ng&agrave;y</strong></span>. Tham khảo ngay <a href="http://dulichchauau.org/tour-ghep.html"><strong>tour ch&acirc;u &acirc;u tự chọn</strong></a> tại đ&acirc;y.</p>\r\n<p style="text-align: justify;">\r\n	<em><strong><a href="https://www.google.com/maps/place/C%C3%B4ng+ty+C%E1%BB%95+ph%E1%BA%A7n+D%E1%BB%8Bch+v%E1%BB%A5+Th%C6%B0%C6%A1ng+m%E1%BA%A1i+v%C3%A0+Du+l%E1%BB%8Bch+Mix/@21.0132978,105.8043247,17z/data=!4m12!1m6!3m5!1s0x3135ab606c5a62a3:0x9a1b056e9300aedf!2zQ8O0bmcgdHkgQ-G7lSBwaOG6p24gROG7i2NoIHbhu6UgVGjGsMahbmcgbeG6oWkgdsOgIER1IGzhu4tjaCBNaXg!8m2!3d21.0132928!4d105.8065134!3m4!1s0x0:0x79c883b18e8635af!8m2!3d21.0106921!4d105.8094569">MixTourist</a> - C&ocirc;ng ty du lịch chuy&ecirc;n tour ch&acirc;u &Acirc;u rất h&acirc;n hạnh được đồng h&agrave;nh c&ugrave;ng qu&yacute; kh&aacute;ch!</strong></em></p>\r\n', 'Kinh nghiệm du lịch châu Âu tự túc cực tiết kiệm chi phí', 'kinh nghiệm du lịch châu âu giá rẻ, kinh nghiệm du lịch châu âu tự túc, xin visa du lịch châu âu tự túc', 'Để Mix Tourist chia sẻ cho bạn một vài kinh nghiệm du lịch châu Âu tự túc với giá rẻ nhất có thể nhé!', '2017-10-18 08:29:54'),
(14, 3, 'Du lịch bụi châu Âu và những điều cần biết', 'du-lich-bui-chau-au-va-nhung-dieu-can-biet', '/view/admin/Themes/kcfinder/upload/images/mat-na-y.jpg', 585, '<p style="text-align: justify;">\r\n	<strong>Du lịch bụi </strong>đang l&agrave; phong tr&agrave;o kh&ocirc;ng chỉ trong giới trẻ ng&agrave;y nay m&agrave; cả những bậc trung ni&ecirc;n cũng bị n&oacute; cuốn h&uacute;t. Ch&acirc;u &Acirc;u đẹp đẽ với ao sắc m&agrave;u lại c&agrave;ng hấp dẫn hơn với những t&iacute;n đồ n&agrave;y. Nhưng thay v&igrave; nơi n&agrave;o bạn cũng muốn tới v&agrave; c&aacute;i g&igrave; bạn cũng muốn trải nghiệm th&igrave; h&atilde;y ngồi xuống b&igrave;nh tĩnh v&agrave; lập kế hoạch cụ thể cho một chuyến đi ho&agrave;n hảo nhất.</p>\r\n<p style="text-align: justify;">\r\n	Dưới đ&acirc;y l&agrave; một v&agrave;i lời khuy&ecirc;n m&agrave; <strong>Mix Tourist</strong> d&agrave;nh cho bạn để bạn c&oacute; thể tận hưởng một <strong>tour du lịch bụi Ch&acirc;u &Acirc;u</strong> tuyệt vời nhất.</p>\r\n<h2 style="text-align: justify;">\r\n	<strong><span style="color:#ff0000;"><span style="font-size:16px;">1. Biết chọn lựa đất nước bạn muốn tới</span></span></strong></h2>\r\n<p style="text-align: justify;">\r\n	Nếu bạn l&agrave; một người kh&ocirc;ng mấy quan t&acirc;m tới vấn đề t&agrave;i ch&iacute;nh v&agrave; thời gian th&igrave; lời khuy&ecirc;n n&agrave;y kh&ocirc;ng mấy th&uacute; vị nhưng n&oacute; sẽ rất bổ &iacute;ch cho những người muốn <strong>phượt Ch&acirc;u &Acirc;u</strong> với t&uacute;i tiền kh&ocirc;ng mấy rủng rỉnh.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/kinh-nghiem-du-lich-chau-au-tu-tuc-mixtourist.jpg" style="width: 600px; height: 399px;" /></p>\r\n<p style="text-align: justify;">\r\n	Vốn l&agrave; lục địa với sự đa dạng v&agrave; ph&aacute;t triển về văn h&oacute;a cũng như kinh tế, lục địa gi&agrave; n&agrave;y bao gồm những quốc gia c&oacute; sự kết nối kh&aacute; chặt chẽ v&igrave; thế việc bạn muốn đi hết tất cả quốc gia trong một tour th&igrave; quả l&agrave; kh&aacute; kh&oacute; khăn. C&ograve;n nếu bạn vẫn cố muốn đi th&igrave; qu&atilde;ng thời gian chờ t&agrave;u, bus, m&aacute;y bay (thời gian chết) sẽ c&agrave;ng khiến cuộc h&agrave;nh tr&igrave;nh của bạn ng&agrave;y c&agrave;ng ngắn đi th&ocirc;i.</p>\r\n<p style="text-align: justify;">\r\n	Vậy bạn phải l&agrave;m g&igrave;??? H&atilde;y lựa chọn <strong>5 điểm đến kh&ocirc;ng n&ecirc;n bỏ qua tại Ch&acirc;u &Acirc;u</strong> để trải nghiệm. Đ&oacute; c&oacute; thể l&agrave; tham gia <em>lễ hội đường phố tại Anh</em>, <em>uống bia thỏa th&iacute;ch tại Đức</em>&nbsp;hay kh&aacute;m ph&aacute; những <em>c&ocirc;ng tr&igrave;nh cổ đại của &Yacute;</em>&hellip; Mỗi điểm bạn đến bạn n&ecirc;n đ&aacute;nh dấu điểm quan trọng nhất v&agrave; dần dần mở rộng ra xung quanh. Khi đ&atilde; chọn được đất nước bạn muốn tới, căn ke quỹ thời gian hạn hẹp của m&igrave;nh v&agrave; cứ thế hướng tới th&ocirc;i.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/anh/Light-Big-Ben-London-United-Kingdom-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: center;">\r\n	Đồng hồ Big Ben, Anh</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/canh-cong-Brandenburg-du-lich-duc-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: center;">\r\n	C&aacute;nh cổng&nbsp;Brandenburg, Đức</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/du-lich-phap1-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: center;">\r\n	Th&aacute;p Eiffel, Ph&aacute;p</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/taybannha/city-of-rome-tay-ban-nha-mixtourist.jpg" style="width: 600px; height: 480px;" /></p>\r\n<p style="text-align: center;">\r\n	Th&agrave;nh phố Rome, T&acirc;y Ban Nha</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/halan/Halan1.jpg" style="width: 600px; height: 480px;" /></p>\r\n<p style="text-align: center;">\r\n	C&aacute;nh đồng hoa Tulips, H&agrave; Lan</p>\r\n<p style="text-align: justify;">\r\n	Tất nhi&ecirc;n những điểm đến nổi tiếng như <strong>Th&aacute;p Eiffel</strong>, <strong>b&aacute;nh xe thi&ecirc;n ni&ecirc;n kỷ</strong>, <strong>đấu trường La M&atilde; cổ đại</strong> hay <strong>Th&aacute;p chu&ocirc;ng đồng hồ Big Ben</strong> th&igrave; bạn kh&ocirc;ng n&ecirc;n bỏ qua ch&uacute;t n&agrave;o. Bạn h&atilde;y tự cảm nhận ch&uacute;ng bằng ch&iacute;nh con mắt của bạn chứ kh&ocirc;ng n&ecirc;n dập khu&ocirc;n theo mấy lời ca ngợi tr&ecirc;n một v&agrave;i b&agrave;i b&aacute;o hoặc c&aacute;c diễn đ&agrave;n. Cũng kh&ocirc;ng nhất thiết l&agrave; phải l&agrave;m theo hướng dẫn trong s&aacute;ch chỉ dẫn du lịch. C&oacute; những nơi kh&ocirc;ng c&oacute; trong guide book nhưng biết đ&acirc;u đấy n&oacute; lại tạo cho bạn những th&uacute; vị ri&ecirc;ng th&igrave; h&atilde;y cứ thế m&agrave; tự kh&aacute;m ph&aacute; ra th&ocirc;i.</p>\r\n<p style="text-align: justify;">\r\n	D&acirc;n phượt n&oacute;i chung th&igrave; tầm 10 ng&agrave;y đổ lại l&agrave; ph&ugrave; hợp với t&uacute;i tiền v&agrave; những trải nghiệm. Du lịch độc h&agrave;nh tại lục địa n&agrave;y đơn giản hơn rất nhiều so với những lục địa kh&aacute;c v&igrave; nhiều quốc gia tại đ&acirc;y sử dụng ng&ocirc;n ngữ bằng tiếng Anh hoặc d&acirc;n ch&uacute;ng c&oacute; khả năng n&oacute;i tiếng Anh. Vấn đề chỗ ở hay l&agrave; v&eacute; t&agrave;u, v&eacute; m&aacute;y bay bạn n&ecirc;n đặt online tr&ecirc;n mạng từ trước chuyến đi tầm v&agrave;i th&aacute;ng, khảo s&aacute;t gi&aacute; kỹ c&agrave;ng trước khi mua. Tới Ch&acirc;u &Acirc;u, sử dụng xe Bus li&ecirc;n th&agrave;nh phố thậm ch&iacute; cả li&ecirc;n quốc gia sẽ rẻ hơn m&aacute;y bay hặc t&agrave;u.</p>\r\n<p style="text-align: justify;">\r\n	Bắt đầu từ th&aacute;ng 1/2017, <strong>MixTourist </strong>triển khai chương tr&igrave;nh &quot;<span style="color:#ff0000;"><strong>Du lịch ch&acirc;u &Acirc;u theo phong c&aacute;ch MixTourist</strong></span>&quot;. Theo đ&oacute;, du kh&aacute;ch sẽ &quot;<span style="color:#0000ff;">Thiết kế tour ch&acirc;u &Acirc;u ri&ecirc;ng cho m&igrave;nh&nbsp;đồng gi&aacute; <strong><u>73$/ng&agrave;y</u></strong></span>&quot; , du kh&aacute;ch c&oacute; thể tự chọn tới bất kỳ đất nước n&agrave;o trong khu vực ch&acirc;u &Acirc;u m&agrave; kh&ocirc;ng lo về gi&aacute;, chỉ 73$/ng&agrave;y. Thật qu&aacute; tuyệt vời phải kh&ocirc;ng n&agrave;o!</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/hanhtrinh/map-hanh-trinh-du-lich-chau-au-mixtourist.jpg" style="width: 800px; height: 650px;" /></p>\r\n<p style="text-align: center;">\r\n	H&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; Ch&acirc;u &Acirc;u theo ph&ograve;ng c&aacute;ch MixTourist cho du kh&aacute;ch tham khảo</p>\r\n<p style="text-align: justify;">\r\n	<em>&gt;&gt; Để biết chi tiết hơn về chương tr&igrave;nh n&agrave;y, mời bạn click <a href="http://dulichchauau.org/tour-ghep.html"><strong>Tour tự chọn ch&acirc;u &Acirc;u MixTourist</strong></a></em></p>\r\n<h2 style="text-align: justify;">\r\n	<strong><span style="color:#ff0000;"><span style="font-size:16px;">2. Đi du lịch ch&acirc;u &Acirc;u m&ugrave;a n&agrave;o đẹp nhất?</span></span></strong></h2>\r\n<p style="text-align: justify;">\r\n	Thời gian cao điểm của m&ugrave;a <strong>du lịch Ch&acirc;u &Acirc;u</strong> l&agrave; v&agrave;o th&aacute;ng 7 đến th&aacute;ng 9, đ&acirc;y l&agrave; qu&atilde;ng thời gian m&agrave; bạn phải chia sẻ với nhiều người bạn đồng h&agrave;nh kh&aacute;c. V&agrave; v&igrave; thế n&ecirc;n việc qu&aacute; tải về c&aacute;c dịch vụ như kh&aacute;ch sạn, v&eacute; xe, v&eacute; t&agrave;u&hellip;cũng l&agrave; điều b&igrave;nh thường. &nbsp;V&igrave; thế lời khuy&ecirc;n d&agrave;nh cho bạn l&agrave; <em>h&atilde;y đi du lịch ch&acirc;u &acirc;u m&ugrave;a xu&acirc;n</em> hoặc <em>m&ugrave;a thu</em> để đi l&agrave; hợp l&yacute; nhất. Cả Ch&acirc;u &Acirc;u l&uacute;c n&agrave;y thanh b&igrave;nh v&agrave; như d&agrave;nh cho ri&ecirc;ng m&igrave;nh bạn vậy.</p>\r\n<p style="text-align: justify;">\r\n	C&ograve;n kh&ocirc;ng bạn cũng n&ecirc;n chọn du lịch v&agrave;o thời điểm diễn ra những lễ hội đặc sắc nhất Ch&acirc;u &Acirc;u như <strong>lễ hội bia tại Oktoberfest</strong> của Đức, <strong>lễ hội c&agrave; chua tại Exit</strong>, T&acirc;y Ban Nha hay <strong>lễ hội tranh nghệ thuật đương đại tại Biennale</strong>, &Yacute;&hellip;</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/le-hoi-bia-Oktoberfest-mixtourist.jpg" style="width: 600px; height: 386px;" /></p>\r\n<p style="text-align: center;">\r\n	Tưng bừng <strong>lễ hội bia&nbsp;Oktoberfest</strong><strong style="text-align: justify;"> </strong>tại <strong>Đức </strong>diễn ra v&agrave;o th&aacute;ng 10 h&agrave;ng năm</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/taybannha/le-hoi-ca-chua-tai-Exit.jpg" style="width: 600px; height: 375px;" /></p>\r\n<p style="text-align: center;">\r\n	<strong>Lễ hội c&agrave; chua tại Exit</strong>, T&acirc;y Ban Nha được tổ chức v&agrave;o n<span style="color: rgb(84, 84, 84); font-family: arial, sans-serif; font-size: small;">g&agrave;y thứ Tư cuối c&ugrave;ng trong th&aacute;ng 8 h&agrave;ng năm</span></p>\r\n<p style="text-align: center;">\r\n	<span style="color: rgb(84, 84, 84); font-family: arial, sans-serif; font-size: small;"><img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/le-hoi-nghe-thuat-tranh-duong-dai-tai-Biennale-y-mixtourist.jpg" style="width: 600px; height: 401px;" /></span></p>\r\n<p style="text-align: center;">\r\n	<span style="color: rgb(84, 84, 84); font-family: arial, sans-serif; font-size: small;">Một t&aacute;c phẩm được trưng b&agrave;y trong&nbsp;</span><strong style="text-align: justify;">lễ hội tranh nghệ thuật đương đại tại Biennale, &Yacute;</strong> được tổ chức v&agrave;o th&aacute;ng 5 h&agrave;ng năm</p>\r\n<p style="text-align: justify;">\r\n	Đ&acirc;y sẽ l&agrave; những trải nghiệm kh&oacute; qu&ecirc;n trong cuộc đời của bạn. Nhưng bạn cần lưu &yacute; rằng mọi vấn đề về chi ph&iacute; cần c&acirc;n nhắc kỹ v&agrave; những dịch vụ tại những điểm như thế n&agrave;y bạn n&ecirc;n đặt trước từ 3 th&aacute;ng trở l&ecirc;n.</p>\r\n<h2 style="text-align: justify;">\r\n	<strong><span style="color:#ff0000;"><span style="font-size:16px;">3. Biết c&aacute;ch bảo vệ bản th&acirc;n</span></span></strong></h2>\r\n<p style="text-align: justify;">\r\n	Kh&ocirc;ng chỉ ri&ecirc;ng<strong>&nbsp;<a href="http://mixtourist.com.vn/du-lich-chau-au">du lịch Ch&acirc;u &Acirc;u</a></strong>&nbsp;m&agrave; điểm đến n&agrave;o cũng thế th&ocirc;i bạn phải cẩn thận nếu kh&ocirc;ng muốn bị lừa gạt. Đặc biệt l&agrave; những lời đề nghị về gi&aacute; cả v&ocirc; c&ugrave;ng rẻ v&agrave; những lời dụ dỗ c&oacute; c&aacute;nh.</p>\r\n<p style="text-align: justify;">\r\n	Cần tỉnh t&aacute;o với một c&aacute;i đầu lạnh th&igrave; bạn mới c&oacute; thể tho&aacute;t ra khỏi được <em>những người b&aacute;n h&agrave;ng rong tại Paris</em> hay <em>những c&ocirc; n&agrave;ng gợi cảm tại Amsterdam</em>. Nếu kh&ocirc;ng thấy an to&agrave;n bạn h&atilde;y bỏ đi, linh cảm của một tay du lịch bụi thực thụ sẽ gi&uacute;p &iacute;ch cho bạn trong những thời điểm n&agrave;y.</p>\r\n<p style="text-align: justify;">\r\n	Ngo&agrave;i việc gh&eacute; thăm những th&agrave;nh phố ch&iacute;nh, nếu c&ograve;n thời gian bạn cũng n&ecirc;n gh&eacute; qua những đ&ocirc; thị kiểu như <strong>Budapest</strong>, <strong>Krakow </strong>hay <strong>Lisbon</strong>, chắc chắn những điểm đến n&agrave;y cũng tạo ra cho bạn kh&ocirc;ng &iacute;t điều th&uacute; vị đ&acirc;u.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/italia/do-thi-budapest-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: center;">\r\n	Vẻ đẹp T&ograve;a nh&agrave; Quốc Hội tại th&agrave;nh phố <strong>Budapest&nbsp;</strong>- Thủ đổ của <strong>Hungary</strong>, lung linh huyền ảo về đ&ecirc;m</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/ba-lan/thanh-pho-Krakow-ba-lan-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: center;">\r\n	Th&agrave;nh phố <strong>Krakow </strong>- Thủ đ&ocirc; <strong>Ba Lan</strong></p>\r\n<p style="text-align: center;">\r\n	<strong><img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/bo-dao-nha/thanh-pho-lisbon-thu-do-cua-bo-dao-nha.jpg" style="width: 600px; height: 400px;" /></strong></p>\r\n<p style="text-align: center;">\r\n	Th&agrave;nh phố sắc m&agrave;u <strong>Lisbon </strong>- Th&agrave;nh phố lớn nhất v&agrave; l&agrave; thủ đ&ocirc; của <strong>Bồ Đ&agrave;o Nha</strong></p>\r\n<p style="text-align: justify;">\r\n	Học một v&agrave;i ba c&acirc;u đơn giản trong ng&ocirc;n ngữ bản địa hoặc kh&ocirc;ng th&igrave; bạn cũng n&ecirc;n biết hỏi kiểu như &ldquo;bạn c&oacute; thể n&oacute;i được tiếng Anh kh&ocirc;ng?&rdquo; bằng tiếng địa phương. Việc n&agrave;y kh&aacute; l&agrave; gi&uacute;p &iacute;ch cho bạn.</p>\r\n<p style="text-align: justify;">\r\n	H&agrave;nh l&yacute; th&igrave; c&agrave;ng nhẹ nh&agrave;ng c&agrave;ng tốt thay v&igrave; những thư cồng kềnh th&igrave; bạn n&ecirc;n chọn những vật dụng nhỏ gọn hoặc bỏ những bộ đồ kh&ocirc;ng cần thiết ở nh&agrave; v&agrave; mang theo một v&agrave;i bộ hợp với thời tiết nước sở tại th&ocirc;i. Du lịch bụi m&agrave;.</p>\r\n<p style="text-align: justify;">\r\n	M&aacute;ch bạn một v&agrave;i mẹo nhỏ về c&aacute;ch sắp xếp h&agrave;nh l&yacute; du lịch trong video dưới đ&acirc;y nha:</p>\r\n<p style="text-align: center;">\r\n	<iframe frameborder="0" height="25%" longdesc="cach-xep-hanh-ly-khi-di-du-lich" name="Cách sắp xếp hành lý khi đi du lịch" scrolling="no" src="https://www.youtube.com/embed/vcEYLXSYG_o" width="60%"></iframe></p>\r\n<h2 style="text-align: justify;">\r\n	<strong><span style="color:#ff0000;"><span style="font-size:16px;">4.Ti&ecirc;u tiền đ&uacute;ng chỗ</span></span></strong></h2>\r\n<p style="text-align: justify;">\r\n	Tuy chỉ l&agrave; backpacker nhưng v&igrave; l&agrave; đi để trải nghiệm n&ecirc;n bạn cũng n&ecirc;n chi ra một số tiền gọi l&agrave; kha kh&aacute; để trải nghiệm những đặc trưng của điểm đến m&igrave;nh chọn, v&iacute; dụ như thưởng thức <strong>tr&agrave; hiều London</strong>,<strong> đi thuyền gondola tại Venice</strong>, <strong>uống bia Đức</strong>&hellip;</p>\r\n<p style="text-align: justify;">\r\n	Mang muộn khoản tiền kha kh&aacute; c&ugrave;ng với thẻ t&iacute;n dụng, traveller check (một loại ng&acirc;n phiếu d&ugrave;ng cho kh&aacute;ch du lịch c&oacute; thể đổi th&agrave;nh tiền) để bạn c&oacute; thể thưởng thức c&aacute;c m&oacute;n ăn đặc sản, tham gia một v&agrave;i tr&ograve; chơi th&uacute; vị,... Thẻ t&iacute;n dụng phải đủ để mua v&eacute; m&aacute;y bay trở về nước&nbsp; v&agrave; n&ecirc;n để ở nhiều nơi kh&aacute;c nhau đề ph&ograve;ng kẻ gian. Đặt an to&agrave;n bản th&acirc;n l&ecirc;n h&agrave;ng đầu.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/mang-tien-khi-di-du-lich-mixtourist.jpg" style="width: 600px; height: 450px;" /></p>\r\n<p style="text-align: justify;">\r\n	Thật buồn cười v&igrave; c&oacute; một v&agrave;i người chọn một v&agrave;i qu&aacute;n bia hay bar ngồi nh&acirc;m nhi những đồ uống c&oacute; cồn v&agrave; xem đ&oacute; l&agrave; th&uacute; vui. Nhưng bạn đ&atilde; vượt h&agrave;ng ng&agrave;n km để đến đ&acirc;y chỉ uống bia hay uống rượu th&igrave; quả thật l&agrave; nh&agrave;m ch&aacute;n, h&atilde;y tận dụng thời gian của m&igrave;nh để trải nghiệm. C&oacute; v&ocirc; v&agrave;n điều chờ đ&oacute;n bạn b&ecirc;n ngo&agrave;i chứ kh&ocirc;ng chỉ v&agrave;i đồ uống lạ miệng đ&acirc;u.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/vui-choi-o-chau-au-mixtourist.jpg" style="width: 600px; height: 344px;" /></p>\r\n<p style="text-align: justify;">\r\n	L&agrave;m việc chăm chỉ v&agrave; h&ugrave;n một khoản kha kh&aacute; cho chuyến đi c&oacute; thể được gọi l&agrave; lịch sử n&agrave;y. Keo kiệt một t&yacute; khi ở nh&agrave; để rồi h&agrave;o ph&oacute;ng hơn với bản th&acirc;n khi sang b&ecirc;n kia. Chả sao cả phải kh&ocirc;ng?</p>\r\n<p style="text-align: justify;">\r\n	Cuối c&ugrave;ng h&atilde;y tận hưởng từng khoảnh khắc tr&ecirc;n chuyến h&agrave;nh tr&igrave;nh của bạn. Ch&uacute;c bạn c&oacute; một chuyến du lịch thật th&uacute; vị v&agrave; an to&agrave;n!</p>\r\n<p style="text-align: justify;">\r\n	Nhưng trước ti&ecirc;n, để c&oacute; thể vi vu ở phương trời ch&acirc;u &Acirc;u cổ k&iacute;nh v&agrave; tuyệt vời đ&oacute;, bạn phải c&oacute; trong tay một tấm <strong>visa du lịch ch&acirc;u &Acirc;u</strong> hay được gọi l&agrave; <strong>VISA SCHENGEN. </strong></p>\r\n<p style="text-align: center;">\r\n	<strong><img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/visa-du-lich-chau-au-tu-tuc-mixtourist.jpg" style="width: 600px; height: 400px;" /></strong></p>\r\n<p style="text-align: justify;">\r\n	<em><strong>&gt;&gt; </strong>Click ngay <a href="http://dulichchauau.org/dich-vu/visa--ho-chieu/thu-tuc-xin-visa-du-lich-chau-au-visa-schengen.html"><strong>Thủ tục xin visa du lịch ch&acirc;u &acirc;u</strong></a> để biết th&ecirc;m th&ocirc;ng tin chi tiết nh&eacute;!</em></p>\r\n<p style="text-align: justify;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	C&oacute; một c&aacute;ch kh&aacute;c để bạn vẫn c&oacute; thể trải nghiệm cuộc sống tại ch&acirc;u &Acirc;u với gi&aacute; si&ecirc;u tiết kiệm m&agrave; kh&ocirc;ng phải lo lắng về dịch vụ đặt v&eacute; m&aacute;y bay hay ph&ograve;ng kh&aacute;ch sạn... Tất cả những việc đ&oacute; h&atilde;y để cho <strong>MixTourist </strong>gi&uacute;p bạn. C&ocirc;ng việc của bạn l&agrave; chỉ việc li&ecirc;n hệ ngay tới hotline&nbsp;<em><strong>04.6281 4340</strong>&nbsp;</em>để đặt ngay cho m&igrave;nh một<em> </em><strong><a href="http://dulichchauau.org/du-lich-chau-au/">tour du lịch ch&acirc;u &Acirc;u</a>&nbsp;</strong>m&agrave; bạn mong muốn nh&eacute;!</p>\r\n', 'Du lịch bụi châu Âu | Kinh nghiệm du lịch bụi châu Âu | MixTourist', 'du lịch bụi châu âu, kinh nghiệm du lịch bụi châu âu', 'Dưới đây là một vài lời khuyên mà Mix Tourist dành cho bạn để bạn có thể tận hưởng một tour du lịch bụi Châu Âu tuyệt vời nhất.', '2017-04-12 14:01:03');
INSERT INTO `news` (`id`, `danhmuc_id`, `name`, `name_url`, `img`, `view`, `content`, `title`, `keyword`, `description`, `created`) VALUES
(15, 2, '16 điều thú vị về tháp Eiffel mà hầu hết khách du lịch chưa bao giờ quan tâm đến', '16-dieu-thu-vi-ve-thap-eiffel-ma-hau-het-khach-du-lich-chua-bao-gio-quan-tam-den', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/du-lich-phap-cung-mixtourist.jpg', 742, '<p>\r\n	Bạn đ&atilde; từng đi&nbsp;<a href="http://dulichchauau.org/du-lich-phap/"><strong>du lịch Ph&aacute;p</strong></a>&nbsp;v&agrave; tận mắt chi&ecirc;m ngưỡng&nbsp;<strong>th&aacute;p Eiffel</strong>, nhưng bạn đ&atilde; biết đủ th&ocirc;ng tin về t&ograve;a th&aacute;p một thời cao nhất thế giới n&agrave;y chưa?&nbsp;</p>\r\n<p>\r\n	<strong>MixTourist&nbsp;</strong>sẽ bật m&iacute; cho bạn&nbsp;16 điều th&uacute; vị về th&aacute;p Eiffel m&agrave; hầu hết kh&aacute;ch du lịch chưa bao giờ quan t&acirc;m đến ngay sau đ&acirc;y nh&eacute;!</p>\r\n<div>\r\n	<p>\r\n		1. Th&aacute;p Eiffel được x&acirc;y dựng với mục đ&iacute;ch kỷ niệm 100 năm ng&agrave;y c&aacute;ch mạng Ph&aacute;p, bạn đầu chỉ kỳ vọng n&oacute; tồn tại trong 20 năm.</p>\r\n	<p style="text-align: center;">\r\n		<img alt="thap-eiffel-du-lich-phap-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/thap-eiffel-du-lich-phap-mixtourist-3.jpg" style="width: 600px; height: 450px;" /></p>\r\n	<p>\r\n		2.Th&aacute;p Eiffel ch&iacute;nh thức ho&agrave;n th&agrave;nh v&agrave;o ng&agrave;y 31/03/1889 v&agrave; mất 2 năm, 2 th&aacute;ng, 5 ng&agrave;y kể từ ng&agrave;y thi c&ocirc;ng để người Ph&aacute;p ho&agrave;n th&agrave;nh n&oacute;. C&aacute;c đ&acirc;y kh&ocirc;ng l&acirc;u người ta vừa tổ chức kỷ niệm sinh nhật tr&ograve;n 125 tuổi cho t&ograve;a th&aacute;p.</p>\r\n	<p style="text-align: center;">\r\n		<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/thap-eiffel-du-lich-phap-mixtourist-7.jpg" style="width: 600px; height: 395px;" /></p>\r\n	<p>\r\n		3.Sau khi ho&agrave;n th&agrave;nh theo thống k&ecirc; t&ograve;a th&aacute;p ti&ecirc;u tốn 18038 miếng sắt non, 2,5 triệu đinh t&aacute;n với tổng khối lượng 10000 tấn v&agrave; chiều cao t&iacute;nh cả ăngten l&agrave; 324m</p>\r\n	<p style="text-align: center;">\r\n		<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/du-lich-phap1-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n	<p>\r\n		4. Năm 1889 Eiffel ra mắt mọi người với lớp sơn mầu v&agrave;ng chứ kh&ocirc;ng phải m&agrave;u n&acirc;u như b&acirc;y giờ. Từ năm 1954 đến 1961 l&agrave; m&agrave;u n&acirc;u đỏ. Cứ 7 năm th&igrave; t&ograve;a th&aacute;p sẽ được sơn lại một lần để tr&aacute;nh gỉ v&agrave; mỗi lần như thế phải mất 60 tấn sơn để kho&aacute;c cho n&oacute; một tấm &aacute;o mới.</p>\r\n	<p style="text-align: center;">\r\n		<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/thap-eiffel-du-lich-phap-mixtourist-2.jpg" style="width: 600px; height: 450px;" /></p>\r\n	<p>\r\n		5. Gustav Eiffel người tưởng chừng như l&agrave; cha đẻ của Eiffel lại kh&ocirc;ng phải l&agrave; người thiết kế ra t&ograve;a th&aacute;p n&agrave;y. Gustav Eiffel thực ra chỉ l&agrave; người mua bằng s&aacute;ng chế v&agrave; chỉ đạo thi c&ocirc;ng.</p>\r\n	<p style="text-align: center;">\r\n		<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/gustaveeiffel-du-lich-phap-mixtourist.jpg" style="width: 600px; height: 868px;" /></p>\r\n	<p style="text-align: center;">\r\n		Alexandre Gustave Eiffel (1832 &ndash; 1923)</p>\r\n	<p>\r\n		Những người ph&ocirc;i thai v&agrave; ho&agrave;n th&agrave;nh Eiffel bằng những bản vẻ cuối c&ugrave;ng l&agrave; kiến tr&uacute;c sư Maurice Koechlin v&agrave; Emile Nouguier.&nbsp;</p>\r\n	<p>\r\n		6. Đ&atilde; c&oacute; một thời gian (1920-1930) th&aacute;p được sử dụng với mục đ&iacute;ch quảng c&aacute;o. C&aacute;c cạnh của n&oacute; được người ta chưng dụng để treo c&aacute;c tấm biển quảng c&aacute;o v&iacute; dụ như h&atilde;ng &ocirc; t&ocirc; Citro&euml;n chẳng hạn.</p>\r\n	<p style="text-align: center;">\r\n		<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/thap-eiffel-du-lich-phap-mixtourist-1.jpg" style="width: 600px; height: 400px;" /></p>\r\n	<p>\r\n		7. Ban đầu, năm 1889, tờ b&aacute;o Ph&aacute;p Le Figaro thiết lập một m&aacute;y in tr&ecirc;n tầng hai của th&aacute;p Eiffel</p>\r\n	<p>\r\n		8. Điều n&agrave;y hẳn sẽ l&agrave;m bạn bất ngờ, kh&ocirc;ng &iacute;t người Paris bạn đầu rất gh&eacute;t t&ograve;a th&aacute;p n&agrave;y, thậm ch&iacute; họ coi n&oacute; như c&aacute;i gai trong mắt. Đ&atilde; c&oacute; nhiều kiến tr&uacute;c sư v&agrave; người d&acirc;n từng k&yacute; một bản kiến nghị phản đối việc thi c&ocirc;ng th&aacute;p. Người ta cho rằng việc dựng t&ograve;a th&aacute;p n&agrave;y l&ecirc;n sẽ l&agrave;m mất đi vẻ tương xứng với quang cảnh xung quanh. Trong số những người gh&eacute;t t&ograve;a th&aacute;p n&agrave;y c&oacute; nh&agrave; văn nổi tiếng Guy De Maupassant, &ocirc;ng gh&eacute;t Eiffel tới mức &ocirc;ng chỉ chọn thưởng thức bữa trưa tại nh&agrave; h&agrave;ng b&ecirc;n trong th&aacute;p v&igrave; theo &ocirc;ng đ&acirc;y l&agrave; nơi duy nhất &ocirc;ng kh&ocirc;ng phải nh&igrave;n thấy t&ograve;a th&aacute;p đ&aacute;ng gh&eacute;t ấy.&nbsp;</p>\r\n	<p style="text-align: center;">\r\n		<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/thap-eiffel-du-lich-phap-mixtourist-6.jpg" style="width: 600px; height: 336px;" /></p>\r\n	<p style="text-align: justify;">\r\n		Nhưng điều n&agrave;y lại lại khiến cho những du kh&aacute;ch chưa một lần tới&nbsp;<strong><a href="http://mixtourist.com.vn/tour/with/Tour-du-lich-Chau-au-y---Thuy-Sy---Phap---Bi---Ha-Lan/190">du lịch Ph&aacute;p</a></strong>&nbsp;được h&eacute; lộ rằng &ldquo;bất cứ nơi n&agrave;o tại Paris bạn cũng &nbsp;c&oacute; thể nh&igrave;n thấy Eiffel&rdquo;</p>\r\n	<p>\r\n		9. Th&aacute;p Eiffel sẽ cao hơn 20 cm v&agrave;o m&ugrave;a h&egrave; v&igrave; tiếp x&uacute;c với &aacute;nh s&aacute;ng mặt trời khiến th&eacute;p nở ra.</p>\r\n	<p style="text-align: center;">\r\n		<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/thap-eiffel-du-lich-phap-mixtourist-5.jpg" style="width: 600px; height: 450px;" /></p>\r\n	<p>\r\n		10. Sau ng&agrave;y mở cửa 20 ng&agrave;y thang m&aacute;y mới ch&iacute;nh thức đi v&agrave;o hoạt độn. Ng&agrave;y đầu mở cửa 30000 du kh&aacute;ch đ&atilde; phải đi bộ qua 1710 bậc thang để l&ecirc;n tới đỉnh th&aacute;p.</p>\r\n	<p>\r\n		11.Th&aacute;p Eiffel đ&atilde; từng nắm giữ ng&ocirc;i vị c&ocirc;ng tr&igrave;nh cao nhất thế giới trong v&ograve;ng 40 năm sau khi ho&agrave;n th&agrave;nh. Sau đ&oacute; n&oacute; bị t&ograve;a nh&agrave; Chrysler tại New York đ&aacute;nh bại. &nbsp;&nbsp;.</p>\r\n	<p style="text-align: center;">\r\n		<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/toa-nha-Chrysler-New-York-mixtourist.jpg" style="width: 600px; height: 338px;" /></p>\r\n	<p>\r\n		12.Th&aacute;p Eiffel h&agrave;ng năm đ&oacute;n tầm 7 triệu du kh&aacute;ch gh&eacute; thăm, l&agrave; điểm đến thứ 2 tr&ecirc;n nước ph&aacute;p đ&oacute;n nhiều lượt kh&aacute;ch nhất, điểm thứ nhất l&agrave; nh&agrave; thờ đức b&agrave; Paris (13,6 triệu lượt kh&aacute;ch)</p>\r\n	<p style="text-align: center;">\r\n		<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/thap-eiffel-du-lich-phap-mixtourist-9.jpg" style="width: 600px; height: 400px;" /></p>\r\n	<p>\r\n		13.V&agrave;o ban đ&ecirc;m, cứ 5 ph&uacute;t mỗi giờ t&ograve;a th&aacute;p lại được thắp s&aacute;ng lung linh bằng những &aacute;nh đ&egrave;n v&agrave;ng. Bạn c&oacute; thể ngắm m&agrave;n tr&igrave;nh diễn ban đ&ecirc;m n&agrave;y tại Place du Trocad&eacute;ro.</p>\r\n	<p style="text-align: center;">\r\n		<img alt="thap-eiffel-du-lich-phap-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/thap-eiffel-du-lich-phap-mixtourist.jpg" style="width: 600px; height: 375px;" /></p>\r\n	<p>\r\n		14.Trong thế chiến thứ I t&ograve;a th&aacute;p đ&oacute;ng vai tr&ograve; kh&ocirc;ng hề nhỏ đặc biệt l&agrave; ở trận Marne năm 1914. T&iacute;n hiểu gửi đi từ đỉnh th&aacute;p đ&atilde; chỉ đạo qu&acirc;n đội Ph&aacute;p chiến đấu ngo&agrave;i tiền tuyến.</p>\r\n	<p>\r\n		15. Kh&ocirc;ng chỉ l&agrave; một điểm du lịch hấp dẫn th&aacute;p Eiffel c&ograve;n c&oacute; nhiều c&ocirc;ng tr&igrave;nh nhỏ ngay b&ecirc;n trong n&oacute;. Đ&oacute; c&oacute; thể l&agrave; c&aacute;c văn phong, bưu điện, t&ograve;a b&aacute;o, nh&agrave; h&aacute;t, s&acirc;n trượt băng&hellip;</p>\r\n	<p style="text-align: center;">\r\n		<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/phap/thap-eiffel-du-lich-phap-mixtourist-8.jpg" style="width: 600px; height: 404px;" /></p>\r\n	<p>\r\n		16. C&oacute; nhiều phi&ecirc;n bản của Th&aacute;p Eiffel tr&ecirc;n khắp thế giới c&ugrave;ng chiều cao kh&aacute;c nhau như: tại Las Vegas (165m - Mỹ), H&agrave;ng Ch&acirc;u (108m), Th&acirc;m Quyến (100m - Trung Quốc), G&oacute;mez Palacio (68,5m - Mexico), Slobozia (54m - Roumania), Parij (50m - Nga), Krakow (8,5m - Ba Lan).</p>\r\n</div>\r\n<div>\r\n	&gt;&gt; Nếu bạn chưa được tận mắt ngắm th&aacute;p Eiffel - C&ocirc;ng tr&igrave;nh vĩ đại bậc nhất ch&acirc;u &Acirc;u n&agrave;y th&igrave; h&atilde;y tham khảo ngay <a href="http://dulichchauau.org/du-lich-phap/"><strong>tour du lịch Ph&aacute;p 2017</strong></a>&nbsp;của <strong>MixTourist.</strong></div>\r\n<div>\r\n	Li&ecirc;n hệ ng&agrave;y hotline <span style="color:#ff0000;"><strong>04.6281 4340</strong></span> để được tư vấn về <a href="http://dulichchauau.org/dich-vu/visa--ho-chieu/thu-tuc-xin-visa-di-phap.html"><strong>visa du lịch ph&aacute;p</strong></a> hoặc truy cập <strong><a href="http://dulichchauau.org/dich-vu/visa--ho-chieu/thu-tuc-xin-visa-du-lich-chau-au-visa-schengen.html">thủ tục xin visa du lịch ch&acirc;u &Acirc;u (Visa Schengen)</a></strong>&nbsp;để c&oacute; thể đi du lịch tất cả c&aacute;c nước trong khối li&ecirc;n minh ch&acirc;u &Acirc;u EU nh&eacute;!</div>\r\n', '16 điều thú vị về tháp Eiffel mà hầu hết khách du lịch chưa bao giờ qu', 'du lịch pháp, tháp eiffel, tour du lịch pháp, ', 'Nếu bạn chưa được tận mắt ngắm tháp Eiffel - Công trình vĩ đại bậc nhất châu Âu này thì hãy tham khảo ngay tour du lịch Pháp 2017 của MixTourist.', '2017-02-20 14:45:12'),
(16, 7, 'Du lịch châu Âu mùa nào đẹp nhất? - MixTourist', 'du-lich-chau-au-mua-nao-dep-nhat-mixtourist', '/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/mua-dong-chau-au-mixtourist.jpg', 4358, '<p style="text-align: justify;">\r\n	Bạn đang thắc mắc rằng <strong>m&ugrave;a n&agrave;o ở ch&acirc;u &Acirc;u l&agrave; th&iacute;ch hợp để đi du lịch nhất</strong>? <a href="http://dulichchauau.org/tin-tuc/du-lich-thuy-si/du-lich-chau-au-mua-nao-dep-nhat-mixtourist.html"><strong>N&ecirc;n đi du lịch ch&acirc;u &Acirc;u v&agrave;o th&aacute;ng mấy</strong></a> hay <strong>nhiệt độ ở ch&acirc;u &Acirc;u như thế n&agrave;o</strong>? B&agrave;i viết dưới đ&acirc;y sẽ giải đ&aacute;p c&aacute;c thắc mắc đ&oacute; của bạn.</p>\r\n<p style="text-align: justify;">\r\n	Nhắc đến ch&acirc;u &Acirc;u l&agrave; nhắc đến những c&ocirc;ng tr&igrave;nh kiễn tr&uacute;c cổ k&iacute;nh nổi tiếng như <strong>th&aacute;p Eiffel</strong> (Ph&aacute;p), <strong>đồng hồ Big Ben</strong> (Anh), <strong>th&aacute;p nghi&ecirc;ng Pisa</strong> (&Yacute;), những lễ hội độc đ&aacute;o như <strong>lễ hội h&oacute;a trang</strong> (<span style="color: rgb(102, 102, 102); font-family: Arial, helvetica, sans-serif; text-align: justify;">Brazil</span>), <strong>lễ hội n&eacute;m c&agrave; chua</strong> - lễ hội truyền thống của người d&acirc;n T&acirc;y Ban Nha hay s&ocirc;i động trong <strong>điệu nhảy Flamenco</strong>,... Tất cả đ&atilde; tạo n&ecirc;n một ch&acirc;u &Acirc;u v&ocirc; c&ugrave;ng kh&aacute;c biệt v&agrave; quyến rũ trong l&ograve;ng kh&aacute;ch du lịch.&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	Nếu bạn c&oacute; &yacute; định đi <a href="http://dulichchauau.org/"><strong>du lịch ch&acirc;u &Acirc;u</strong></a> trong năm 2017 n&agrave;y th&igrave; đừng bỏ qua những th&ocirc;ng tin cần thiết sau đ&acirc;y nh&eacute;!</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">1. Nhiệt độ ở ch&acirc;u &Acirc;u</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Ch&acirc;u &Acirc;u c&oacute; vị tr&iacute; địa l&yacute;&nbsp;ph&iacute;a bắc gi&aacute;p&nbsp;Bắc Băng Dương, ph&iacute;a t&acirc;y gi&aacute;p&nbsp;Đại T&acirc;y Dương, ph&iacute;a nam gi&aacute;p&nbsp;Địa Trung Hải&nbsp;v&agrave;&nbsp;biển Đen, ph&iacute;a Đ&ocirc;ng gi&aacute;p với ch&acirc;u &Aacute; được ph&acirc;n c&aacute;ch bởi d&atilde;y n&uacute;i Ural (nga).</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/vi-tri-chau-au-mixtourist.png" style="width: 300px; height: 300px;" /></p>\r\n<p style="text-align: justify;">\r\n	C&oacute; thể n&oacute;i rằng ch&acirc;u &Acirc;u được bao quanh hầu hết l&agrave; biển, lại nằm s&aacute;t với cực Bắc của Tr&aacute;i Đất n&ecirc;n ch&acirc;u &Acirc;u c&oacute; kh&iacute; hậu kh&aacute; đa dạng v&igrave; nằm trong khu vực &ocirc;n đới. Nhiệt độ cao nhất v&agrave;o m&ugrave;a h&egrave; khoảng 34 độ C chủ yếu ở khu vực trung t&acirc;m của ch&acirc;u lục v&agrave; thấp nhất v&agrave;o m&ugrave;a đ&ocirc;ng c&oacute; thể xuống tới -20 độ C.</p>\r\n<p style="text-align: center;">\r\n	<img alt="mua-dong-chau-au-mixtourist" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/mua-dong-chau-au-mixtourist.jpg" style="width: 600px; height: 375px;" /></p>\r\n<p style="text-align: justify;">\r\n	Thời tiết tại Ch&acirc;u &Acirc;u cũng được chia th&agrave;nh 4 m&ugrave;a trong năm v&agrave; thời gian mỗi m&ugrave;a kh&aacute; tương đồng với Việt nam:&nbsp;</p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		M&ugrave;a xu&acirc;n (từ th&aacute;ng 2 đến th&aacute;ng 4) nhiệt độ từ 6 - 20 độ</li>\r\n	<li style="text-align: justify;">\r\n		M&ugrave;a h&egrave; (từ th&aacute;ng 5 đến th&aacute;ng 7) nhiệt độ từ 25 - 34 độ</li>\r\n	<li style="text-align: justify;">\r\n		M&ugrave;a Thu (từ th&aacute;ng 8 đến th&aacute;ng 10) nhiệt độ từ 18 - 28 độ</li>\r\n	<li style="text-align: justify;">\r\n		M&ugrave;a đ&ocirc;ng (từ th&aacute;ng 11 đến th&aacute;ng 1) nhiệt độ từ -20 độ C&nbsp;đến 13 độ C</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	Nắng mưa ở đ&acirc;y kh&aacute; thất thường n&ecirc;n khi đi du lịch, bạn h&atilde;y mang theo 1 chiếc &ocirc; loại gọn nhỏ v&agrave; &aacute;o mưa (c&oacute; thể gấp được) đề ph&ograve;ng trường hợp cần d&ugrave;ng nh&eacute;!</p>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">2. Thời tiết ch&acirc;u &Acirc;u th&aacute;ng 2 - Du lịch ch&acirc;u &Acirc;u m&ugrave;a xu&acirc;n c&oacute; g&igrave; th&uacute; vị?</span></span></h2>\r\n<p style="text-align: justify;">\r\n	M&ugrave;a xu&acirc;n Ch&acirc;u &Acirc;u k&eacute;o d&agrave;i từ th&aacute;ng 2 đến th&aacute;ng 4, đ&acirc;y l&agrave; khoảng thời gian tuyệt vời để du kh&aacute;ch tham quan du ngoạn.&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/mua-xuan-chau-au-mixtourist.jpg" style="width: 600px; height: 421px;" /></p>\r\n<p>\r\n	Thời tiết ấm &aacute;p, c&acirc;y cối đ&acirc;m chồi nảy lộc những d&ograve;ng s&ocirc;ng băng bắt đầu tan chảy v&agrave; kh&ocirc;ng gian như trở n&ecirc;n trong l&agrave;nh tinh khiết hơn sau thời gian d&agrave;i ủ trong m&ugrave;a đ&ocirc;ng băng gi&aacute;.</p>\r\n<p style="text-align: justify;">\r\n	Du kh&aacute;ch đi <strong>du lịch ch&acirc;u &Acirc;u m&ugrave;a xu&acirc;n</strong> sẽ c&oacute; cơ hội tham gia rất nhiều c&aacute;c lễ hội đầu năm mới như:</p>\r\n<p style="text-align: justify;">\r\n	-&nbsp;&nbsp;Một loạt c&aacute;c sự kiện li&ecirc;n hoan nhạc Jazz, tuần lễ thời trang, lễ hội đường phố&hellip; được diễn ra khắp nơi ở Paris.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/tuan-le-thoi-trang-paris-le-hoi-mua-xuan-chau-au-mixtourist.jpg" style="width: 600px; height: 326px;" /></p>\r\n<p style="text-align: center;">\r\n	Tuần lễ thời trang Paris</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/le-hoi-duong-pho-tai-paris-mixtourist.JPG" style="width: 600px; height: 399px;" /></p>\r\n<p style="text-align: center;">\r\n	Lễ hội đường phố tại Paris</p>\r\n<p style="text-align: justify;">\r\n	Trong bầu kh&ocirc;ng kh&iacute; l&atilde;ng mạn, du kh&aacute;ch thong thả dạo bộ dọc theo bờ s&ocirc;ng Seine &ecirc;m đềm, để vui chơi, mua sắm những m&oacute;n đồ lưu niệm độc đ&aacute;o trong lễ hội m&ugrave;a xu&acirc;n &ldquo;La f&ecirc;te du printemps&rdquo; hoặc gh&eacute; thăm chợ hoa &Icirc;le de la Cit&eacute; (ở quảng trường L&eacute;pine, gần Nh&agrave; thờ Đức B&agrave;)</p>\r\n<p style="text-align: justify;">\r\n	-&nbsp;<strong>Carnival Venice</strong> -&nbsp;Lễ hội m&ugrave;a xu&acirc;n hấp dẫn nhất thế giới tại &Yacute; như đưa du kh&aacute;ch trở về thời ho&agrave;ng kim của giới qu&yacute; tộc v&agrave;o thế kỷ 15.&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/le-hoi-Carnival-Venice-mixtourist.jpg" style="width: 600px; height: 399px;" /></p>\r\n<p style="text-align: justify;">\r\n	Diễn ra v&agrave;o th&aacute;ng 2 h&agrave;ng năm, lễ hội đ&atilde; mang đến cho Venice vốn y&ecirc;n b&igrave;nh những khoảnh khắc huy&ecirc;n n&aacute;o.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/Carnival-Venice-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: center;">\r\n	<span style="text-align: justify;">Lễ hội Carnival Venice, &yacute;</span></p>\r\n<p style="text-align: justify;">\r\n	- Đến H&agrave; lan v&agrave;o đầu xu&acirc;n bạn sẽ được thấy một H&agrave; Lan rực rỡ sắc m&agrave;u hoa Tuylip. <strong>Khu vườn Keukenhof </strong>rộng hơn 30 ha l&agrave; nơi c&oacute; m&ugrave;a xu&acirc;n rực rỡ nhất H&agrave; Lan.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/mua-xuan-o-chau-au-mixtourist.JPG" style="width: 600px; height: 398px;" /></p>\r\n<p style="text-align: justify;">\r\n	Hoa được trồng xen giữa những khu rừng nhỏ hay ven những h&agrave;ng c&acirc;y (c&oacute; tới 2.500 c&acirc;y l&acirc;u năm), b&ecirc;n những lối đi v&ograve;ng v&egrave;o d&agrave;i tổng cộng tới 15km, ven những hồ nước uốn lượn c&oacute; đ&agrave;n thi&ecirc;n nga trắng nh&agrave;n tản bồng bềnh tr&ecirc;n mặt nước.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/le-hoi-hoa-tuy-lip-ha-lan-mixtourist.jpg" style="width: 600px; height: 399px;" /></p>\r\n<p style="text-align: center;">\r\n	Hoa tuylip nở tực rỡ trong&nbsp;khu vườn Keukenhof v&agrave;o m&ugrave;a xu&acirc;n ở H&agrave; Lan</p>\r\n<p style="text-align: justify;">\r\n	- <strong>Lễ hội Maslenitsa</strong> -&nbsp;Lễ hội tiễn đưa m&ugrave;a đ&ocirc;ng lạnh gi&aacute; v&agrave; đ&oacute;n ch&agrave;o m&ugrave;a xu&acirc;n vui tươi tại Nga.&nbsp;</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/le-hoi-Maslenitsa-le-hoi-mua-xuan-chau-au-mixtourist.jpg" style="width: 600px; height: 401px;" /></p>\r\n<p style="text-align: justify;">\r\n	Tr&ecirc;n c&aacute;c quảng trường, c&aacute;nh đồng ven rừng... người d&acirc;n dựng l&ecirc;n t&ograve;a th&agrave;nh bằng băng tuyết, h&igrave;nh nộm, đu quay, s&agrave;n m&uacute;a rối.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/thu-vi-le-hoi-maslenitsa-le-hoi-tien-mua-dong-o-nga-mixtourist.jpg" style="width: 600px; height: 496px;" /></p>\r\n<p style="text-align: center;">\r\n	Những con rối trong lễ hội được người d&acirc;n Nga l&agrave;m để ch&agrave;o đ&oacute;n&nbsp;ễ hội Maslenitsa</p>\r\n<p style="text-align: justify;">\r\n	&gt;&gt; Mời bạn tham khảo&nbsp;<a href="http://dulichchauau.org/du-lich-chau-au/"><strong>tour du lịch ch&acirc;u &acirc;u m&ugrave;a xu&acirc;n</strong></a>&nbsp;của&nbsp;<strong>MixTourist&nbsp;</strong></p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		<p>\r\n			<strong>Ch&ugrave;m tour du lịch ch&acirc;u &Acirc;u th&aacute;ng 2</strong></p>\r\n	</li>\r\n	<li style="text-align: justify;">\r\n		<p>\r\n			<strong>Ch&ugrave;m tour du lịch ch&acirc;u &Acirc;u&nbsp;th&aacute;ng 3</strong></p>\r\n	</li>\r\n	<li style="text-align: justify;">\r\n		<p>\r\n			<strong>Ch&ugrave;m tour du lịch ch&acirc;u &Acirc;u&nbsp;th&aacute;ng 4&nbsp;</strong></p>\r\n	</li>\r\n</ul>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">3. Thời tiết ch&acirc;u &Acirc;u th&aacute;ng 5 - Du lịch Ch&acirc;u &Acirc;u m&ugrave;a h&egrave; tận hưởng cảm gi&aacute;c m&aacute;t mẻ tại trời T&acirc;y</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>Du lịch ch&acirc;u &Acirc;u m&ugrave;a h&egrave;</strong> l&agrave; sở th&iacute;ch của đa số kh&aacute;ch du lịch. C&aacute;i nắng m&ugrave;a h&egrave; ở đ&acirc;y kh&ocirc;ng gay gắt như những nước ở khu vực nhiệt đới.&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	<strong>M&ugrave;a h&egrave; ở Ch&acirc;u &Acirc;u</strong> gần như ng&agrave;y n&agrave;o trời cũng nắng v&agrave; trong xanh, c&acirc;y cối ph&aacute;t triển mạnh, nhiệt độ cũng kh&ocirc;ng qu&aacute; n&oacute;ng, bạn c&oacute; thể thỏa th&iacute;ch đi bộ, đạp xe ngắm hồ nước, cảnh quan thi&ecirc;n nhi&ecirc;n.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/du-lich-chau-au-mua-he-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: justify;">\r\n	Tuy nhi&ecirc;n v&agrave;o dịp n&agrave;y du kh&aacute;ch đến với Ch&acirc;u &Acirc;u rất đ&ocirc;ng, do vậy nếu đến Ch&acirc;u &Acirc;u v&agrave;o dịp n&agrave;y bạn sẽ phải chấp nhận một chi ph&iacute; kh&aacute; cao so với thời điểm kh&aacute;c.</p>\r\n<p style="text-align: justify;">\r\n	Vậy tại sao m&ugrave;a h&egrave; l&agrave; thu h&uacute;t kh&aacute;ch du lịch v&agrave;o ch&acirc;u &Acirc;u? Kh&ocirc;ng chi bởi thời tiết đẹp, thuận lợi cho c&aacute;c hoạt động ngo&agrave;i trời như tắm biển, lướt s&oacute;ng, đua thuyền, leo n&uacute;i,...</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/du-lich-chau-au-mua-he-cung-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: justify;">\r\n	Ngo&agrave;i ra v&agrave;o thời gian n&agrave;y cũng diễn ra rất nhiều những sự kiện, lễ hội đặc sắc:&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	- <strong>Lễ hội ẩm thực m&ugrave;a h&egrave;</strong> lớn nhất ch&acirc;u &Acirc;u diễn ra&nbsp;11 - 12/7 tại Anh;&nbsp;23 - 26/7 tại &Yacute;,&nbsp;26/8 - 6/9 tại Đức hay&nbsp;5 - 6/9 tại Scotland</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/le-hoi-am-thuc-mua-he-o-chau-au.jpg" style="width: 600px; height: 338px;" /></p>\r\n<p style="text-align: justify;">\r\n	- <strong>Lễ hội&nbsp;Koninginnedag</strong> - <strong>&quot;Ng&agrave;y Nữ Ho&agrave;ng&quot;</strong> của người d&acirc;n H&agrave; Lan&nbsp;để ch&agrave;o mừng sự l&ecirc;n ng&ocirc;i của C&ocirc;ng Nương Wilhelmina. Nhưng sau khi Ho&agrave;ng Tử Willem-Alexander l&ecirc;n ng&ocirc;i v&agrave;o năm 2013 c&ugrave;ng với sự kiện tho&aacute;i ng&ocirc;i của Ho&agrave;ng Hậu Beatrix, ng&agrave;y hội <strong>Koninginnedag </strong>đ&atilde; ch&iacute;nh thức được đổi t&ecirc;n th&agrave;nh <strong>Koningsdag</strong>, hay c&ograve;n c&oacute; nghĩa l&agrave; <strong>&ldquo;Ng&agrave;y Của Vua&rdquo; </strong>diễn ra v&agrave;o ng&agrave;y&nbsp;27/04 h&agrave;ng năm.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/le-hoi-Koninginnedag-ha-lan-mixtourist.jpg" style="width: 600px; height: 404px;" /></p>\r\n<p style="text-align: justify;">\r\n	Được biết đến l&agrave; &quot;lễ hội đường phố lớn nhất trong năm&quot;, h&agrave;ng loạt c&aacute;c chương tr&igrave;nh nhạc sống, c&aacute;c bữa tiệc đường phố c&ugrave;ng sắc cam bao phủ khắp mọi nơi của đất nước H&agrave; Lan c&agrave;ng khiến kh&ocirc;ng kh&iacute; lễ hội trở n&ecirc;n nồng nhiệt hơn bao giờ hết.</p>\r\n<p style="text-align: justify;">\r\n	<strong>- Lễ hội &acirc;m nhạc Glastonbury (24-28/06).&nbsp;</strong>Mỗi năm, Lễ hội &acirc;m nhạc v&agrave; nghệ thuật thường ni&ecirc;n của nước Anh tại Worthy Farm ở Somerset vẫn thu h&uacute;t đến 200,000 người tham gia.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/le-hoi-am-nhac-Glastonbury-le-hoi-mua-he-o-anh-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: justify;">\r\n	Cho đến ng&agrave;y nay, lễ hội n&agrave;y vẫn được tổ chức bởi Michael Eavis v&agrave; diễn ra tr&ecirc;n đất tư của &ocirc;ng, Glastonbury l&agrave; lễ hội &acirc;m sắc lớn nhất thế giới v&agrave; l&agrave; sự kiện h&agrave;ng đầu đối với những fans &acirc;m nhạc trung th&agrave;nh.</p>\r\n<p style="text-align: justify;">\r\n	V&agrave; c&ograve;n rất nhiều những lễ hội hấp dẫn kh&aacute;c như&nbsp;<strong>Cannes Film Festival</strong> (13-24/05), lễ hội&nbsp;<strong>British Open</strong> (12-19/07) d&agrave;nh cho những người y&ecirc;u m&ocirc;n nghệ thuật đ&aacute;nh gofl hay <strong>lễ hội&nbsp;Venice Redentore Festival</strong> (18/07) với rất nhiều c&aacute;c hoạt động như thi đấu ph&aacute;o hoa hoặc trang tr&iacute; thuyền buồm,...</p>\r\n<p style="text-align: justify;">\r\n	&gt;&gt; Mời bạn tham khảo <a href="http://dulichchauau.org/du-lich-chau-au/"><strong>tour du lịch ch&acirc;u &acirc;u m&ugrave;a h&egrave;</strong></a> của <strong>MixTourist&nbsp;</strong></p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		<p>\r\n			<strong>Ch&ugrave;m tour du lịch ch&acirc;u &Acirc;u th&aacute;ng 5</strong></p>\r\n	</li>\r\n	<li style="text-align: justify;">\r\n		<p>\r\n			<strong>Ch&ugrave;m tour du lịch ch&acirc;u &Acirc;u&nbsp;th&aacute;ng 6</strong></p>\r\n	</li>\r\n	<li style="text-align: justify;">\r\n		<p>\r\n			<strong>Ch&ugrave;m tour du lịch ch&acirc;u &Acirc;u&nbsp;th&aacute;ng 7&nbsp;</strong></p>\r\n	</li>\r\n</ul>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">4. &nbsp;Du lịch ch&acirc;u &Acirc;u th&aacute;ng 9 - Du lịch Ch&acirc;u &Acirc;u m&ugrave;a thu rực rỡ m&ugrave;a l&aacute; đỏ&nbsp;</span></span></h2>\r\n<p style="text-align: justify;">\r\n	Từ th&aacute;ng 8 đến th&aacute;ng 10, bầu trời ch&acirc;u &Acirc;u như kho&aacute;c l&ecirc;n m&igrave;nh chiếc &aacute;o mới, từ nền xanh m&aacute;t chấm ph&aacute; v&agrave;i nốt v&agrave;ng, dần chuyển sang m&agrave;u v&agrave;ng, đỏ rực quyến rũ.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/du-lich-mua-thu-chau-au-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<div id="left_calculator">\r\n	<div>\r\n		<p style="text-align: justify;">\r\n			Ch&acirc;u &Acirc;u quyến rũ từ những gam m&agrave;u cổ k&iacute;nh của kiến tr&uacute;c gothic v&agrave; roman đặc trưng; lối đi l&aacute;t đ&aacute; ngang qua qu&aacute;n caf&eacute; ngay g&oacute;c phố đầy l&atilde;ng mạn.</p>\r\n		<p style="text-align: center;">\r\n			<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/du-lich-mua-thu-chau-au-mixtourist2(1).jpg" style="width: 600px; height: 399px;" /></p>\r\n		<p style="text-align: justify;">\r\n			Nơi đ&acirc;y c&ograve;n ấn tượng du kh&aacute;ch bởi những t&ograve;a l&acirc;u đ&agrave;i tr&aacute;ng lệ nằm cạnh d&ograve;ng s&ocirc;ng thơ mộng; hay nhiều con k&ecirc;nh y&ecirc;n b&igrave;nh len lỏi dưới b&oacute;ng r&acirc;m của c&aacute;c d&atilde;y nh&agrave; xinh xắn.</p>\r\n		<p style="text-align: center;">\r\n			<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/du-lich-mua-thu-chau-au-mixtourist2.jpg" style="width: 600px; height: 419px;" /></p>\r\n		<p style="text-align: justify;">\r\n			Biết bao h&agrave;ng c&acirc;y v&agrave; những chiếc cầu cổ bắc ngang cũng tạo n&ecirc;n bức tranh m&ugrave;a thu đầy sắc m&agrave;u v&agrave; thật ấn tượng.</p>\r\n	</div>\r\n</div>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/du-lich-mua-thu-chau-au-mixtourist3.jpg" style="width: 600px; height: 337px;" /></p>\r\n<p style="text-align: justify;">\r\n	Ch&acirc;u &Acirc;u trong l&ograve;ng du kh&aacute;ch l&uacute;c n&agrave;y thật b&igrave;nh y&ecirc;n đến lạ kỳ. Tuy nhi&ecirc;n, ho&agrave;n to&agrave;n tr&aacute;i ngược với cảnh sắc xung quanh l&agrave; những lễ hội v&ocirc; c&ugrave;ng n&aacute;o nhiệt v&agrave; s&ocirc;i động h&uacute;t h&agrave;ng triệu lượt kh&aacute;ch du lịch mỗi năm như:</p>\r\n<p style="text-align: justify;">\r\n	-&nbsp;Lễ hội bia lớn nhất thế giới ở Đức</p>\r\n<p style="text-align: justify;">\r\n	<strong>Lễ hội bia Oktoberfest </strong>hay c&ograve;n gọi l&agrave; &ldquo;<strong>lễ hội th&aacute;ng 10</strong>&rdquo; ra đời v&agrave;o năm 1810 tại Munich nhằm t&ocirc;n vinh đ&aacute;m cưới của Ho&agrave;ng th&aacute;i tử Ludwig (sau n&agrave;y l&agrave; vua Ludwig I) v&agrave; C&ocirc;ng ch&uacute;a Therese của Saxe-Hildburghausen.</p>\r\n<p style="text-align: center;">\r\n	<img alt="Lễ hội bia Oktoberfest" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/duc/le-hoi-bia-Oktoberfest-mixtourist.jpg" style="width: 600px; height: 386px;" /></p>\r\n<p style="text-align: justify;">\r\n	Do th&aacute;ng 10 thời tiết ở xứ Bavaria kh&aacute; lạnh n&ecirc;n lễ hội bia sẽ được tổ chức bắt đầu từ giữa th&aacute;ng 9 v&agrave; k&eacute;o d&agrave;i tới ng&agrave;y chủ nhật đầu ti&ecirc;n của th&aacute;ng 10.</p>\r\n<p style="text-align: justify;">\r\n	<strong>-&nbsp;Lễ hội s&ocirc; c&ocirc; la ở Perugia:&nbsp;</strong>Th&aacute;ng 10 l&agrave; thời điểm th&iacute;ch hợp nhất để du kh&aacute;ch h&ograve;a m&igrave;nh v&agrave;o d&ograve;ng người tham dự lễ hội &ldquo;s&ocirc; c&ocirc; la ch&acirc;u &Acirc;u&rdquo; lớn nhất lục địa gi&agrave;.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/le-hoi-socola-Perugia-du-lich-chau-au-mua-thu-mixtourist.jpg" style="width: 600px; height: 377px;" /></p>\r\n<p style="text-align: justify;">\r\n	Nếu l&agrave; t&iacute;n đồ của m&oacute;n ăn n&agrave;y, bạn sẽ kh&ocirc;ng thể bỏ qua v&igrave; to&agrave;n bộ c&aacute;c h&atilde;ng sản xuất s&ocirc; c&ocirc; la hảo hạng ở ch&acirc;u &Acirc;u đều c&oacute; mặt ở Perugia. Ngo&agrave;i việc được thưởng thức s&ocirc; c&ocirc; la, bạn c&ograve;n c&oacute; thể thăm th&uacute; th&agrave;nh phố cổ k&iacute;nh n&agrave;y.</p>\r\n<p style="text-align: justify;">\r\n	&gt;&gt; Mời bạn tham khảo&nbsp;<a href="http://dulichchauau.org/du-lich-chau-au/"><strong>tour du lịch ch&acirc;u &acirc;u m&ugrave;a thu</strong></a>&nbsp;của&nbsp;<strong>MixTourist&nbsp;</strong></p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		<p>\r\n			<strong><a href="http://dulichchauau.org/tin-tuc/du-lich-anh/du-lich-chau-au-thang-8-chum-tour-du-lich-chau-au-thang-8-mixtourist.html">Ch&ugrave;m tour du lịch ch&acirc;u &Acirc;u th&aacute;ng 8</a></strong></p>\r\n	</li>\r\n	<li style="text-align: justify;">\r\n		<p>\r\n			<a href="http://dulichchauau.org/tin-tuc/du-lich-phap/du-lich-chau-au-thang-9-chum-tour-du-lich-chau-au-thang-9-mixtourist.html"><strong>Ch&ugrave;m tour du lịch ch&acirc;u &Acirc;u&nbsp;th&aacute;ng 9</strong></a></p>\r\n	</li>\r\n	<li style="text-align: justify;">\r\n		<p>\r\n			<a href="http://dulichchauau.org/tin-tuc/du-lich-tay-ban-nha/du-lich-chau-au-thang-10-chum-tour-du-lich-chau-au-thang-10-mixtourist.html"><strong>Ch&ugrave;m tour du lịch ch&acirc;u &Acirc;u&nbsp;th&aacute;ng 10</strong></a></p>\r\n	</li>\r\n</ul>\r\n<h2 style="text-align: justify;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">5. Du lịch ch&acirc;u &Acirc;u th&aacute;ng 12 -Du lịch Ch&acirc;u &Acirc;u m&ugrave;a đ&ocirc;ng tưng bừng m&ugrave;a Gi&aacute;ng sinh v&agrave; đ&oacute;n năm mới</span></span></h2>\r\n<p style="text-align: justify;">\r\n	<strong>M&ugrave;a đ&ocirc;ng ch&acirc;u &Acirc;u</strong> nhiệt độ kh&aacute; lạnh gi&aacute;. Nhiệt độ thấp nhất c&oacute; thể xuống tới -20 độ C. Tuy nhi&ecirc;n kh&ocirc;ng v&igrave; thế m&agrave; ch&acirc;u &Acirc;u vắng kh&aacute;ch du lịch m&ugrave;a n&agrave;y.</p>\r\n<p style="text-align: justify;">\r\n	C&oacute; rất nhiều c&aacute;c tr&ograve; chơi thể thao m&ugrave;a đ&ocirc;ng như trượt tuyết, trượt băng, dựng lều tuyết hay thậm ch&iacute; l&agrave; tr&egrave;o thuyền kayak tr&ecirc;n s&ocirc;ng băng,...</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/truot-tuyet-mua-dong-o-chau-au-mixtourist.jpg" style="width: 600px; height: 338px;" /></p>\r\n<p style="text-align: center;">\r\n	Tr&ograve; chơi trượt tuyết khi đi du lịch ch&acirc;u &Acirc;u m&ugrave;a đ&ocirc;ng</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/truot-bang-mua-dong-o-chau-au-mixtourist.jpg" style="width: 600px; height: 450px;" /></p>\r\n<p style="text-align: center;">\r\n	Trượt băng - tr&ograve; chơi ưa th&iacute;ch của kh&aacute;ch du lịch ch&acirc;u &Acirc;u</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/dung-leu-tuyet-o-chau-au-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: center;">\r\n	Những ng&ocirc;i lều tuyết được du kh&aacute;ch dựng l&ecirc;n bằng ch&iacute;nh đ&ocirc;i tay trần của m&igrave;nh</p>\r\n<p style="text-align: justify;">\r\n	Cứ đến m&ugrave;a đ&ocirc;ng l&agrave; người d&acirc;n khắp c&aacute;c nước tại ch&acirc;u &Acirc;u lại tất bật chuẩn bị cho một <strong>m&ugrave;a Gi&aacute;ng sinh</strong> tưng bừng v&agrave; ch&agrave;o đ&oacute;n năm mới.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/du-lich-chau-au-mua-giang-sinh-mixtourist.jpg" style="width: 600px; height: 401px;" /></p>\r\n<p style="text-align: justify;">\r\n	Từ c&aacute;c trung t&acirc;m thương mại, c&aacute;c khu chợ, trong c&aacute;c nh&agrave; thờ v&agrave; từng gia đ&igrave;nh, đ&acirc;u đ&acirc;u cũng thấy những &aacute;nh đ&egrave;n v&agrave; rực rỡ c&ugrave;ng những b&agrave;i h&aacute;t gi&aacute;ng sinh vui nhộn.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/du-lich-chau-au-mua-giang-sinh-mixtourist2.jpg" style="width: 600px; height: 411px;" /></p>\r\n<p style="text-align: justify;">\r\n	B&ecirc;n cạnh đ&oacute; l&agrave; sự kiện ch&agrave;o mừng năm mới trong m&agrave;n ph&aacute;o hoa đặc sắc</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/du-lich-mua-dong-don-nam-moi-o-chau-au-mixtourist.jpg" style="width: 600px; height: 383px;" /></p>\r\n<p style="text-align: center;">\r\n	M&agrave;n ph&aacute;o hoa năm mới sống động tại th&aacute;p Eiffel, Ph&aacute;p</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/danhmucquocgia/chauau/du-lich-mua-dong-don-nam-moi-o-chau-au-mixtourist1.JPG" style="width: 600px; height: 399px;" /></p>\r\n<p style="text-align: center;">\r\n	Người d&acirc;n ch&acirc;u &Acirc;u tưng bừng trong khoảnh khắc giao thừa</p>\r\n<p style="text-align: justify;">\r\n	Ch&acirc;u &Acirc;u hay bất cứ đ&acirc;u cũng vậy. Mỗi m&ugrave;a lại c&oacute; một sức h&uacute;t v&agrave; những điều th&uacute; vị ri&ecirc;ng. Nếu bạn chọn đi du lịch ch&acirc;u &Acirc;u cho du l&agrave; v&agrave;o m&ugrave;a n&agrave;o th&igrave; chắc chắn bạn cũng đều c&oacute; những trải nghiệm v&ocirc; c&ugrave;ng tuyệt vời.&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	&gt;&gt; Mời bạn tham khảo&nbsp;<a href="http://dulichchauau.org/du-lich-chau-au/"><strong>tour du lịch ch&acirc;u &acirc;u m&ugrave;a đ&ocirc;ng</strong></a>&nbsp;của&nbsp;<strong>MixTourist&nbsp;</strong></p>\r\n<ul>\r\n	<li style="text-align: justify;">\r\n		<p>\r\n			<strong><a href="http://dulichchauau.org/tin-tuc/du-lich-duc/du-lich-chau-au-thang-11-chum-tour-du-lich-chau-au-thang-11-mixtourist.html">Ch&ugrave;m tour du lịch ch&acirc;u &Acirc;u th&aacute;ng 11</a></strong></p>\r\n	</li>\r\n	<li style="text-align: justify;">\r\n		<p>\r\n			<strong><a href="http://dulichchauau.org/tin-tuc/du-lich-y/du-lich-chau-au-thang-12-chum-tour-du-lich-chau-au-thang-12--mixtourist.html">Ch&ugrave;m tour du lịch ch&acirc;u &Acirc;u&nbsp;th&aacute;ng 12</a></strong></p>\r\n	</li>\r\n	<li style="text-align: justify;">\r\n		<p>\r\n			<strong>Ch&ugrave;m tour du lịch ch&acirc;u &Acirc;u&nbsp;th&aacute;ng 1</strong></p>\r\n	</li>\r\n</ul>\r\n<p style="text-align: justify;">\r\n	Để c&oacute; thể tận hưởng cuộc sống s&ocirc;i động tại trời T&acirc;y, bạn cần phải c&oacute; visa du lịch. Tham khảo ngay <strong><a href="http://dulichchauau.org/dich-vu/visa--ho-chieu/thu-tuc-xin-visa-du-lich-chau-au-visa-schengen.html">thủ tục xin visa du lịch ch&acirc;u &Acirc;u (Visa Schengen)</a>&nbsp;tại đ&acirc;y!</strong></p>\r\n<p style="text-align: justify;">\r\n	H&atilde;y chọn ngay cho m&igrave;nh một <strong><a href="http://dulichchauau.org/du-lich-chau-au/">tour du lịch ch&acirc;u &Acirc;u</a>&nbsp;</strong>c&ugrave;ng&nbsp;<strong>MixTourist</strong> ngay h&ocirc;m nay để nhận được khuyến m&atilde;i của ch&uacute;ng t&ocirc;i.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/dichvu/hotline-chau-au.png" style="width: 300px; height: 90px;" /></p>\r\n<p style="text-align: center;">\r\n	Hoặc</p>\r\n<p style="text-align: center;">\r\n	<span style="color:#ff0000;"><span style="font-size:16px;"><strong><span style="background-color:#ffff00;">CHAT ONLINE NGAY</span></strong></span></span></p>\r\n', 'Du lịch châu Âu mùa nào đẹp nhất? - MixTourist', 'du lịch châu âu mùa nào đẹp nhất, nên đi du lịch châu âu tháng mấy, du lịch châu âu mùa xuân, du lịch châu âu mùa hè, du lịch châu âu mùa thu, du lịch châu âu mùa đông', 'Bạn đang thắc mắc rằng mùa nào ở châu Âu là thích hợp để đi du lịch nhất? Nên đi du lịch châu Âu vào tháng mấy hay nhiệt độ ở châu Âu như thế nào? Bài viết dưới đây sẽ gi', '2017-09-29 09:40:28');
INSERT INTO `news` (`id`, `danhmuc_id`, `name`, `name_url`, `img`, `view`, `content`, `title`, `keyword`, `description`, `created`) VALUES
(17, 1, 'Du lịch châu âu cần bao nhiêu tiền?', 'du-lich-chau-au-can-bao-nhieu-tien', '/view/admin/Themes/kcfinder/upload/images/delt-ha-lan-mixtourist.jpg', 11972, '<p style="text-align: justify;">\r\n	Bạn đang c&oacute; kế hoạch cho một chuyến <strong>du lịch Ch&acirc;u &Acirc;u</strong> đầy l&atilde;ng mạn v&agrave;o thời gian sắp tới. Nhưng bạn c&oacute; biết đi <strong>du lịch Ch&acirc;u &Acirc;u</strong> hết <strong>bao nhi&ecirc;u tiền</strong>, bạn cần những điều kiện g&igrave; về t&agrave;i ch&iacute;nh để được đi du lịch Ch&acirc;u &Acirc;u?</p>\r\n<p style="text-align: justify;">\r\n	Sau đ&acirc;y, <strong>c&ocirc;ng ty</strong> <strong>MixTourist</strong> sẽ tư vấn chi tiết v&agrave; miễn ph&iacute; cho c&aacute;c bạn th&ocirc;ng tin cần thiết nhất về một tour du lịch kh&aacute;m ph&aacute;, trải nghiệm <strong>Ch&acirc;u &Acirc;u si&ecirc;u tiết kiệm</strong>.</p>\r\n<h2 style="text-align: justify;">\r\n	<strong><span style="color:#ff0000;"><span style="font-size:16px;">1.&nbsp;Đi du lịch Ch&acirc;u &Acirc;u hết bao nhi&ecirc;u tiền?</span></span></strong></h2>\r\n<p style="text-align: justify;">\r\n	Với từng thời điểm bạn mong muốn đi du lịch trong năm th&igrave; gi&aacute; tour cho mỗi chuyến <strong>du lịch Ch&acirc;u &Acirc;u</strong> l&agrave; kh&aacute;c nhau v&agrave; <strong>MixTourist </strong>sẽ cung cấp cho bạn mức g&iacute;a cho <strong>c</strong>huyến du lịch thấp nhất.</p>\r\n<p style="text-align: justify;">\r\n	Một chuyến du lịch qua c&aacute;c nước của Ch&acirc;u &Acirc;u cơ bản thường từ&nbsp;<strong>7 ng&agrave;y 6 đ&ecirc;m</strong> đến&nbsp;<strong>15 ng&agrave;y 14 đ&ecirc;m</strong> xuy&ecirc;n suốt khu vực Đ&ocirc;ng &Acirc;u hoặc T&acirc;y &Acirc;u sẽ c&oacute; gi&aacute; dao động từ <strong>45 triệu</strong> đến <strong>90 triệu</strong> hoặc t&ugrave;y theo y&ecirc;u cầu của du kh&aacute;ch, bạn c&oacute; thể <em><a href="http://dulichchauau.org/tour-ghep.html"><strong>tự&nbsp;thiết kế tour ri&ecirc;ng</strong></a> </em>cho m&igrave;nh&nbsp;với gi&aacute; chỉ&nbsp;<span style="box-sizing: border-box; font-weight: 700; color: rgb(0, 0, 0); font-family: Tahoma; font-size: 14px; background-color: rgb(250, 250, 250);"><span style="box-sizing: border-box; color: rgb(0, 0, 255);">&euro;</span></span><span style="box-sizing: border-box; font-family: Tahoma; font-size: 14px; background-color: rgb(250, 250, 250); color: rgb(0, 0, 255);"><span style="box-sizing: border-box; font-family: Helvetica, Arial, sans-serif;"><span style="box-sizing: border-box; font-weight: 700;">73&nbsp;</span></span></span><span style="box-sizing: border-box; font-family: Tahoma; font-size: 14px; background-color: rgb(250, 250, 250); color: rgb(0, 0, 255);"><span style="box-sizing: border-box; font-family: Helvetica, Arial, sans-serif;"><span style="box-sizing: border-box; font-weight: 700;">/ Ng&agrave;y</span></span></span>.</p>\r\n<blockquote>\r\n	<p style="text-align: justify;">\r\n		<span style="font-size:14px;"><b>===&gt;&gt;&gt; Tham khảo ngay: Danh s&aacute;ch c&aacute;c <a href="http://dulichchauau.org/du-lich-chau-au/">tour du lịch ch&acirc;u &Acirc;u 2017</a>&nbsp;được lựa chọn nhiều nhất của <a href="http://mixtourist.com.vn">MixTourist</a></b></span></p>\r\n</blockquote>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/du-lich-chau-au-can-bao-nhieu-tien-mixtourist.jpg" style="width: 600px; height: 450px;" /></p>\r\n<p style="text-align: justify;">\r\n	Một trong những c&aacute;ch gi&uacute;p bạn tiết kiệm chi ph&iacute; nhất cho chuyến đi đ&oacute; l&agrave; h&atilde;y <em><strong>đăng k&iacute; đi theo đo&agrave;n đ&ocirc;ng người c&ugrave;ng đồng nghiệp, bạn b&egrave; hay gia đ&igrave;nh.</strong></em></p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/du-lich-chau-au-mixtourist2.jpg" style="width: 600px; height: 399px;" /></p>\r\n<p style="text-align: justify;">\r\n	Tuy nhi&ecirc;n, khi <strong>Ch&acirc;u &Acirc;u</strong> qu&aacute; hấp dẫn m&agrave; bạn chưa kịp kh&aacute;m ph&aacute; hết th&igrave; bạn c&oacute; thể k&eacute;o d&agrave;i chuyến đi của m&igrave;nh; v&agrave; mọi chi ph&iacute; ph&aacute;t sinh th&ecirc;m đ&oacute; sẽ t&ugrave;y v&agrave;o gi&aacute; dịch vụ của c&ocirc;ng ty bạn đặt tour hoặc bạn c&oacute; thể ho&agrave;n to&agrave;n tự t&uacute;c chi ph&iacute; đ&oacute;.</p>\r\n<p style="text-align: justify;">\r\n	<em><u><strong>Lưu &yacute;: </strong></u>Th&ocirc;ng thường gi&aacute; c&aacute;c tour du lịch của c&aacute;c c&ocirc;ng ty du lịch sẽ kh&ocirc;ng bao gồm chi ph&iacute; c&aacute; nh&acirc;n như mua đồ, qu&agrave;, h&agrave;nh l&yacute; qu&aacute; quy định, gi&aacute; cước điện thoại hay tiền tips cho HDV hoặc l&aacute;i xe.</em></p>\r\n<h2 style="text-align: justify;">\r\n	<span style="font-size:16px;"><span style="color:#ff0000;"><strong>2.&nbsp;&nbsp;</strong><strong>Đi du lịch Ch&acirc;u &Acirc;u được mang theo bao nhi&ecirc;u tiền</strong></span></span></h2>\r\n<p style="text-align: justify;">\r\n	Bạn đang c&oacute; một list danh s&aacute;ch c&aacute;c m&oacute;n đồ muốn sở hữu bấy l&acirc;u: c&aacute;c nh&atilde;n h&agrave;ng thời trang Ph&aacute;p, nước hoa Ph&aacute;p, đồng hồ Thụy Sĩ.....c&ugrave;ng h&agrave;ng trăm m&oacute;n ăn nổi tiếng m&agrave; bạn muốn thử qua như: m&igrave; &Yacute;, gan ngỗng b&eacute;o Ph&aacute;p, b&aacute;nh &nbsp;Doner Kebab ở Thổ Nhĩ Kỳ, M&oacute;n G&agrave; T&acirc;y ở Anh...</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/du-lich-chau-au-mixtourist.jpg" style="width: 600px; height: 408px;" /></p>\r\n<p style="text-align: justify;">\r\n	Do đ&oacute;, một vali tiền đ&atilde; sẵn s&agrave;ng cho chuyến du lịch của bạn. Tuy nhi&ecirc;n bạn c&oacute; biết bạn được ph&eacute;p mang <strong>bao nhi&ecirc;u tiền</strong> ra nước ngo&agrave;i?</p>\r\n<p style="text-align: justify;">\r\n	<u><strong>Quy định về mang theo ngoại tệ khi xuất cảnh của NHNN Việt Nam</strong></u></p>\r\n<p style="text-align: justify;">\r\n	Theo quy định &nbsp;khi xuất cảnh, nếu mang theo số ngoại tệ lớn hơn 3.000 USD tiền mặt hoặc ngoại tệ tương đương, người d&acirc;n đều phải xin ph&eacute;p Ng&acirc;n h&agrave;ng Nh&agrave; nước v&agrave; để được cấp ph&eacute;p bạn phải chờ từ ba ng&agrave;y đến một tuần. Tuy nhi&ecirc;n, với quy định mới, c&aacute;c ng&acirc;n h&agrave;ng v&agrave; người d&acirc;n c&oacute; nhu cầu chuyển tiền ra nước ngo&agrave;i như du học, du lịch, chữa bệnh, thăm th&acirc;n nh&acirc;n th&igrave; sẽ dễ d&agrave;ng hơn.</p>\r\n<p style="text-align: justify;">\r\n	Theo đ&oacute;, Ng&acirc;n h&agrave;ng Nh&agrave; nước đ&atilde; ban h&agrave;nh quyết định số 921/QĐ-NHNN ng&agrave;y 27-6-2005 về sửa đổi quyết định 337/1998/QĐ-NHNN của thống đốc Ng&acirc;n h&agrave;ng Nh&agrave; nước về việc mang ngoại tệ tiền mặt v&agrave; đồng Việt Nam bằng tiền mặt khi xuất nhập cảnh. Theo quyết định n&agrave;y, hạn mức phải khai b&aacute;o với hải quan cửa khẩu khi xuất nhập cảnh đối với ngoại tệ tiền mặt l&agrave; 7.000 USD v&agrave; đối với đồng Việt Nam l&agrave; 15 triệu.</p>\r\n<p style="text-align: justify;">\r\n	<u><strong>Cụ thể:</strong></u></p>\r\n<p style="text-align: justify;">\r\n	- C&ocirc;ng d&acirc;n Việt Nam c&oacute; nhu cầu mang ngoại tệ dưới mức 7.000 USD để phục vụ c&aacute;c mục đ&iacute;ch hợp ph&aacute;p th&igrave; kh&ocirc;ng cần phải c&oacute; giấy ph&eacute;p của Ng&acirc;n h&agrave;ng Nh&agrave; nước, chi nh&aacute;nh tỉnh v&agrave; th&agrave;nh phố.</p>\r\n<p style="text-align: justify;">\r\n	- C&ocirc;ng d&acirc;n Việt Nam c&oacute; nhu cầu mang ngoại tệ để phục vụ mục đ&iacute;ch trợ cấp kh&oacute; khăn cho th&acirc;n nh&acirc;n theo quy đinh th&igrave; kh&ocirc;ng cần phải c&oacute; giấy ph&eacute;p của Ng&acirc;n h&agrave;ng Nh&agrave; nước chi nh&aacute;nh tỉnh, th&agrave;nh phố.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/money-mixtouist1..jpg" style="width: 80%;" /></p>\r\n<p style="text-align: center;">\r\n	&nbsp;</p>\r\n<p style="text-align: justify;">\r\n	- Trường hợp c&ocirc;ng d&acirc;n Việt Nam c&oacute; nhu cầu mang ngoại tệ từ mức 7.000 USD trở l&ecirc;n hoặc c&oacute; nhu cầu mua, chuyển ngoại tệ cho c&aacute;c mục đ&iacute;ch hợp ph&aacute;p th&igrave; vẫn thực hiện theo đ&uacute;ng thủ tục, điều kiện cấp giấy ph&eacute;p mang ngoại tệ quy định.</p>\r\n<p style="text-align: justify;">\r\n	Trong thực tế, bạn c&oacute; thể mang theo cả vali tiền đi du nhưng nhất định phải khai b&aacute;o với quản l&yacute; tại s&acirc;n bay nếu số tiền đ&oacute; lớn hơn $10,000 v&agrave; đ&acirc;y mới l&agrave; l&agrave; điều quan trọng nhất! Họ sẽ kh&ocirc;ng giữ số tiền n&agrave;y nếu bạn khai b&aacute;o với mục đ&iacute;ch thống k&ecirc;. C&ograve;n nếu bạn kh&ocirc;ng khai b&aacute;o m&agrave; để bị ph&aacute;t gi&aacute;c ra th&igrave; nhất định bạn sẽ bị tịch thu thậm ch&iacute; bị giam giữ ngay tại s&acirc;n bay.</p>\r\n<p style="text-align: justify;">\r\n	Quy tr&igrave;nh khai b&aacute;o, bạn chỉ phải điền v&agrave;o mẫu đơn 105 c&oacute; t&ecirc;n &ldquo;Report of International Transportation of Currency or Nonetary Instruments,&rdquo; do nh&acirc;n vi&ecirc;n quan thuế cung cấp. Sau khi khai b&aacute;o xong, bạn c&oacute; thể đi với số tiền mang theo m&agrave; kh&ocirc;ng hề bị thu giữ.</p>\r\n<p style="text-align: justify;">\r\n	Tuy nhi&ecirc;n, nếu bạn chỉ muốn tận hưởng kh&ocirc;ng kh&iacute; y&ecirc;n b&igrave;nh, l&atilde;ng mạn trong <strong>chuyến du lịch Ch&acirc;u &Acirc;u</strong> của m&igrave;nh th&igrave; bạn kh&ocirc;ng cần mang theo qu&aacute; $10,000 để tr&aacute;nh những thủ tục v&agrave; thời gian khai b&aacute;o tại s&acirc;n bay, bởi tất cả c&aacute;c dịch vụ đ&atilde; c&oacute; <strong>Mix Tourist </strong>chuẩn bị cho bạn.</p>\r\n<p style="text-align: justify;">\r\n	<em>====&gt;&gt;&gt; Mọi thắc về <strong><a href="http://dulichchauau.org/tin-tuc/du-lich-thuy-si/du-lich-chau-au-mua-nao-dep-nhat-mixtourist.html">du lịch ch&acirc;u &Acirc;u m&ugrave;a n&agrave;o đẹp nhất</a>&nbsp;</strong>hoặc <a href="http://dulichchauau.org/dich-vu/visa--ho-chieu/"><strong>thủ tục xin visa đi Ch&acirc;u &Acirc;u</strong></a> h&atilde;y li&ecirc;n hệ ngay tới văn ph&ograve;ng của <strong>MixTourist</strong>&nbsp;nh&eacute;</em></p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/icon/hotline-chau-au.png" style="width: 300px; height: 88px;" /></p>\r\n<p style="text-align: center;">\r\n	<strong style="font-size: 14px; text-align: center;">Hoặc</strong></p>\r\n<p style="text-align: center;">\r\n	<strong><span style="font-size:16px;"><span style="color:#ff0000;"><span style="background-color:#ffff00;">CHAT ONLINE NGAY</span></span></span></strong></p>\r\n', 'Du lịch châu âu cần bao nhiêu tiền? - MixTourist', 'du lịch châu âu cần bao nhiêu tiền, du lịch châu âu, du lịch châu âu giá rẻ, mixtourist, thủ tục xin visa châu âu, tiền đi du lịch', 'Bạn đang có kế hoạch cho một chuyến du lịch Châu Âu đầy lãng mạn vào thời gian sắp tới. Nhưng bạn có biết đi du lịch Châu Âu hết bao nhiêu tiền, bạn cần những điều kiện g', '2017-10-10 10:24:41'),
(18, 12, '6 cái NHẤT đầy thú vị của Phần Lan', '6-cai-nhat-day-thu-vi-cua-phan-lan', '/view/admin/Themes/kcfinder/upload/images/du-lich-phan-lan-mixtourist.jpg', 460, '<p>\r\n	Phần Lan l&agrave; một trong những quốc gia đ&aacute;ng sống NHẤT thể giới với bầu kh&ocirc;ng tr&iacute; trong l&agrave;nh, y&ecirc;n b&igrave;nh; an ninh trật tự tốt, người d&acirc;n trung thực, mức thu nhập cao.</p>\r\n<p>\r\n	Tuy nhi&ecirc;n, bạn c&oacute; biết quốc gia nhỏ b&eacute; n&agrave;y c&ograve;n những c&aacute;i NHẤT kh&aacute;c cũng kh&ocirc;ng k&eacute;m phần th&uacute; vị? H&atilde;y cũng <strong>Mix Tourist</strong> kh&aacute;m ph&aacute; những c&aacute;i nhất đ&oacute; l&agrave; g&igrave; nh&eacute;!!!</p>\r\n<h2>\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">1. Xứ sở ngh&igrave;n hồ</span></span></h2>\r\n<p>\r\n	187,888 l&agrave; con số ch&iacute;nh x&aacute;c về số lượng hồ nước (rộng hơn 500m2) v&agrave; 179,584 đảo lớn nhỏ trong v&ugrave;ng l&atilde;nh thổ Phần Lan v&agrave; được coi l&agrave; một trong những quốc gia c&oacute; nhiều hồ, đảo nhất thế giới.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/ho-phan-lan.jpg" style="width: 600px; height: 450px;" /></p>\r\n<p style="text-align: center;">\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="font-size:16px;"><span style="color:#ff0000;">2. Nền gi&aacute;o dục k&igrave; lạ nhất thế giới</span></span></h2>\r\n<p>\r\n	Hệ thống gi&aacute;o dục Phần Lan được đ&aacute;nh gi&aacute; l&agrave; tốt nhất thế giới với những điều &ldquo;k&igrave; lạ nhất&rdquo; đ&oacute; l&agrave;: kh&ocirc;ng c&oacute; c&aacute;c k&igrave; thi, kh&ocirc;ng dạy qu&aacute; nhiều kiến thức, kh&ocirc;ng cần học thuộc, kh&ocirc;ng cần trả ph&iacute;, kh&ocirc;ng cần nh&agrave; gi&aacute;.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/giao-duc-phan-lan-mix-tourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: center;">\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">3. Uống nhiều c&agrave; ph&ecirc; nhất</span></span></h2>\r\n<p>\r\n	Ngo&agrave;i rượu, người Phần Lan c&ograve;n rất th&iacute;ch uống c&agrave; ph&ecirc;. Đ&acirc;y l&agrave; một trong những quốc gia ti&ecirc;u thụ c&agrave; ph&ecirc; nhiều nhất thế giới. Trung b&igrave;nh mỗi người d&ugrave;ng hết 12 kg c&agrave; ph&ecirc; nguy&ecirc;n chất một năm, trong khi con số nay ở Italy l&agrave; 5,7 kg v&agrave; ở T&acirc;y Ban Nha l&agrave; 4,5 kg.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/cafe-phan-lan-mixtourist.jpg" style="width: 600px; height: 398px;" /></p>\r\n<p style="text-align: center;">\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">4. C&oacute; nhiều nh&agrave; tắm hơi nhất</span></span></h2>\r\n<p>\r\n	Tắm hơi l&agrave; một truyền thống văn h&oacute;a của v&ugrave;ng đất n&agrave;y. Bạn c&oacute; thể t&igrave;m thấy ph&ograve;ng tắm hơi ở hầu hết kh&aacute;ch sạn. Ph&ograve;ng tắm thường được l&agrave;m n&oacute;ng tới 70-100 độ C, với độ ẩm kh&ocirc;</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/tam-hoi-phan-lan-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="text-align: center;">\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">5. &nbsp;Nhiều người đi du lịch nhất</span></span></h2>\r\n<p>\r\n	Phần Lan được mệnh danh như thủ đ&ocirc; <strong>du lịch của ch&acirc;u &Acirc;u</strong>. C&oacute; đến 88,5 % d&acirc;n Phần Lan đi du lịch trong năm vừa qua. Đ&acirc;y chắc hẳn l&agrave; con số kỷ lục rất đ&aacute;ng ghi nhớ. V&agrave; kh&ocirc;ng c&oacute; l&yacute; do g&igrave; để bạn từ chối một chuyến du lịch tới thủ đo du lịch n&agrave;y.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/du-lich-phan-lan-mix-tourist.jpg" style="width: 600px; height: 338px;" /></p>\r\n<p style="text-align: center;">\r\n	&nbsp;</p>\r\n<p>\r\n	&gt;&gt;&gt;&gt;&gt; Mọi thắc về c&aacute;c <a href="http://dulichchauau.org/du-lich-chau-au/"><strong>tour du lịch Phần Lan gi&aacute; rẻ 2017</strong></a> hoặc <a href="http://dulichchauau.org/dich-vu/visa--ho-chieu/"><strong>thủ tục xin visa đi Phần Lan v&agrave; Ch&acirc;u &Acirc;u</strong></a> h&atilde;y li&ecirc;n hệ ngay tới văn ph&ograve;ng của <strong>MixTourist </strong>qua <strong>hotline 04.6281 4340 </strong>để được tư vấn miễn ph&iacute; nh&eacute;!</p>\r\n', '6 cái NHẤT đầy thú vị của Phần Lan', 'Phần Lan, du lịch Châu Âu, Mix Tourist', 'Phần Lan là một trong những quốc gia đáng sống NHẤT thể giới với bầu không trí trong lành, yên bình; an ninh trật tự tốt, người dân trung thực. Tuy nhiên, bạn có biết quố', '2017-04-23 18:18:02'),
(19, 4, 'Các món ăn KHÔNG nên thử khi đến Ý', 'cac-mon-an-khong-nen-thu-khi-den-y', '/view/admin/Themes/kcfinder/upload/images/my-y-mixtourist.jpg', 448, '<p style="margin-left:-7.1pt;">\r\n	Bạn sắp c&oacute; chuyến du lịch đến &Yacute;- qu&ecirc; hương của h&agrave;ng chục m&oacute;n ăn nổi tiếng khắp thế giới. Tuy nhi&ecirc;n, bạn c&oacute; biết những m&oacute;n ăn cực độc v&agrave; rất dễ g&acirc;y nguy hiểm cho dạ d&agrave;y nếu bạn ăn thử kh&ocirc;ng?</p>\r\n<p style="margin-left:-7.1pt;">\r\n	Bạn h&atilde;y y&ecirc;n t&acirc;m, Mix Tourist sẽ gi&uacute;p bạn liệt k&ecirc; đầy đủ c&aacute;c m&oacute;n ăn c&oacute; hại nhất tại &Yacute; để bạn c&oacute; thể khước từ ch&uacute;ng khi bạn đi du lịch &Yacute;.</p>\r\n<h2>\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">1. Spaghetti</span></span></h2>\r\n<p style="margin-left:-7.1pt;">\r\n	Spaghetti l&agrave; m&oacute;n ăn được nhắc đến đầu ti&ecirc;n khi n&oacute;i đến ẩm thực &Yacute;. N&oacute; thường được ăn với xốt c&agrave; chua, c&aacute;c loại xốt n&agrave;y c&oacute; thể c&oacute; nhiều loại rau gia vị như oregano, h&uacute;ng, dầu &ocirc; liu, hoặc thịt. Người ta cũng thường rắc th&ecirc;m một số loại pho m&aacute;t xay như Pecorino Romano, Parmedan, v&agrave; Asiago. Spaghetti l&agrave; một trong những m&oacute;n ăn kh&ocirc;ng thể thiếu trong thực đơn của Người &Yacute;.</p>\r\n<p style="margin-left: -7.1pt; text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/m%E1%BB%B3-%C3%BD-Mixtourist.jpg" style="width: 600px; height: 338px;" /></p>\r\n<p style="margin-left: -7.1pt; text-align: center;">\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">2. Pizza &ndash; &Yacute;</span></span></h2>\r\n<p style="margin-left:-7.1pt;">\r\n	Pizza l&agrave; m&oacute;n ăn phổ biến tr&ecirc;n to&agrave;n thế giới, v&agrave; n&oacute;i đến Pizza l&agrave; người ta nghĩ ngay tới quốc gia &Yacute;. M&oacute;n ăn ngon nổi tiếng ở &Yacute; n&agrave;y được l&agrave;m từ bột sau đ&oacute; được nướng l&ecirc;n c&ugrave;ng với nhiều gia vị, nước sốt c&agrave;, pho m&aacute;t. Tuy nhi&ecirc;n, pizza ban đầu giống như một loại b&aacute;nh ngọt.</p>\r\n<p style="margin-left: -7.1pt; text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/pizza-mixtourist.jpg" style="width: 600px; height: 402px;" /></p>\r\n<p style="margin-left: -7.1pt; text-align: center;">\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">3. Gelato</span></span></h2>\r\n<p style="margin-left:-7.1pt;">\r\n	Đ&acirc;y l&agrave; một m&oacute;n kem đ&ocirc;ng lạnh nổi tiếng của nước&nbsp; &Yacute; v&agrave; Gelato c&oacute; nghĩa l&agrave; sự ngọt ng&agrave;o. M&oacute;n ăn ngon ở &Yacute; n&agrave;y được l&agrave;m bằng kem, sữa v&agrave; đường sau đ&oacute; pha th&ecirc;m hương vị tr&aacute;i c&acirc;y. N&oacute; kh&aacute;c kem thường ở chỗ n&oacute; chứa &iacute;t chất b&eacute;o nhưng nhiều đường. &Yacute; l&agrave; một trong những quốc gia duy nhất m&agrave; getlato giữ thị th&agrave;nh xuất khẩu đ&aacute;ng kể.</p>\r\n<p style="margin-left: -7.1pt; text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/gentalo-mixtourist.jpg" style="width: 600px; height: 312px;" /></p>\r\n<p style="margin-left: -7.1pt; text-align: center;">\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">4. Risotto</span></span></h2>\r\n<p style="margin-left:-7.1pt;">\r\n	Cơm Risotto c&oacute; th&agrave;nh phần ch&iacute;nh l&agrave; gạo được nấu ch&iacute;n. Nước d&ugrave;ng c&oacute; thể l&agrave; thịt, c&aacute; hoặc rau v&agrave; cuối c&ugrave;ng l&agrave; cơm nấu ch&iacute;n. Đ&acirc;y l&agrave; một trong những c&aacute;ch nấu cơm phổ biến nhất ở &Yacute;. Đi du lịch &Yacute; du kh&aacute;ch n&ecirc;n thưởng thức m&oacute;n Risotto đặc biệt n&agrave;y một lần trong đời nh&eacute;.</p>\r\n<p style="margin-left: -7.1pt; text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/Risotto-mixtourist.jpg" style="width: 600px; height: 381px;" /></p>\r\n<p style="margin-left: -7.1pt; text-align: center;">\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">5. Calzone</span></span></h2>\r\n<p>\r\n	Ch&uacute;ng được l&agrave;m từ bột b&aacute;nh m&igrave; (tương tự như pizza) nhưng c&oacute; nhiều c&aacute;c th&agrave;nh phần kh&aacute;c nhau. Theo truyền thống Calzone ch&uacute;ng được b&aacute;n tr&ecirc;n c&aacute;c xe ven đường phố ở &Yacute; như l&agrave; một bữa ăn nhẹ c&oacute; k&iacute;ch thước giống như b&aacute;nh sandwich v&agrave; dễ d&agrave;ng ăn l&uacute;c đi tr&ecirc;n đường.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/Calzone-mixtourist%281%29.jpg" style="width: 600px; height: 450px;" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center;">\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">6. Tortellini</span></span></h2>\r\n<p style="margin-left:-7.1pt;">\r\n	M&igrave; ống nhỏ truyền thống được trọn c&ugrave;ng với c&aacute;c loại thịt kh&aacute;c nhau c&oacute; thể l&agrave; mực ống, nhưng bạn c&oacute; thể cảm nhận được hơn thế bởi ch&uacute;ng c&ograve;n c&oacute; c&aacute;c loại nấm hoang d&atilde; c&aacute;c loại pho m&aacute;t kh&aacute;c nhau c&ugrave;ng với thịt b&ograve; hoặc nước luộc g&agrave;. Ngo&agrave;i ra c&ograve;n c&oacute; một biến thể kh&aacute;c gọi tortelloni, được d&ugrave;ng rau thay v&igrave; thịt.</p>\r\n<p style="margin-left: -7.1pt; text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/Tortellini-mixtourist.png" style="width: 600px; height: 450px;" /></p>\r\n<p style="margin-left: -7.1pt; text-align: center;">\r\n	&nbsp;</p>\r\n<h2>\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">7. Panna Cotta</span></span></h2>\r\n<p style="margin-left:-7.1pt;">\r\n	Panna cotta chỉ đơn giản c&oacute; nghĩa l&agrave; kem nấu ch&iacute;n. C&oacute; rất nhiều biến thể v&agrave; hương vị nhưng một panna cotta đ&uacute;ng nghĩa l&agrave; kem đun n&oacute;ng, kết hợp với sữa v&agrave; đường sau đ&oacute; trộn với gelatine. N&oacute; c&oacute; thể d&ugrave;ng k&egrave;m tr&aacute;i c&acirc;y, caramel hoặc s&ocirc;-c&ocirc;-la. Hỗn hợp ch&iacute;nh n&oacute; c&oacute; thể l&agrave; hương vị với vỏ quả vani, dừa hoặc thậm ch&iacute; sả. Bạn c&oacute; thể thưởng thức m&oacute;n ăn ngon nổi tiếng ở &Yacute; n&agrave;y với nhiều hương vị kh&aacute;ch nhau, chắc chắn sẽ mang đến cho du kh&aacute;ch sự h&agrave;i l&ograve;ng nhất.</p>\r\n<p style="margin-left: -7.1pt; text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/panna-cotta-mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p style="margin-left: -7.1pt; text-align: center;">\r\n	&nbsp;</p>\r\n<p style="margin-left:-7.1pt;">\r\n	C&aacute;c m&oacute;n ăn n&agrave;y sẽ giết chết dạ d&agrave;y của ch&uacute;ng ta đ&uacute;ng kh&ocirc;ng c&aacute;c bạn? V&igrave; vậy nếu bạn sắp đi du lịch &Yacute; th&igrave; cần phải đặc biệt d&egrave; chừng với c&aacute;c m&oacute;n ăn đ&atilde; được liệt k&ecirc; b&ecirc;n tr&ecirc;n nh&eacute;.</p>\r\n<p style="margin-left:-7.1pt;">\r\n	&gt;&gt; Mọi thắc về c&aacute;c t<a href="http://dulichchauau.org/">our du lịch Ch&acirc;u &Acirc;u gi&aacute; rẻ 2017</a> hoặc thủ t<a href="http://mixtourist.com.vn/dich-vu/visa-ho-chieu/">ục xin visa đi Ch&acirc;u &Acirc;u </a>h&atilde;y li&ecirc;n hệ ngay tới văn ph&ograve;ng của MixTourist qua hotline 04.6281 4340 để được tư vấn miễn ph&iacute; nh&eacute;!</p>\r\n<p style="margin-left:-7.1pt;">\r\n	&nbsp;</p>\r\n', 'Các món ăn KHÔNG nên thử khi đến Ý', 'du lịch Ý, du lịch châu Âu, món ăn Ý, mixtourist, xin visa đi châu âu', 'Bạn sắp có chuyến du lịch đến Ý- quê hương của hàng chục món ăn nổi tiếng khắp thế giới. Tuy nhiên, bạn có biết những món ăn cực độc và rất dễ gây nguy hiểm cho dạ dày nế', '2017-04-23 18:05:57'),
(20, 2, 'khám phá 4 khu phố đèn đỏ nổi tiếng nhất của châu Âu', 'kham-pha-4-khu-pho-den-do-noi-tieng-nhat-cua-chau-au', '/view/admin/Themes/kcfinder/upload/images/pho-den-do-mixtourist.jpg', 757, '<p>\r\n	Ch&acirc;u &Acirc;u kh&ocirc;ng chỉ nổi tiếng với những cảnh đẹp đầy l&atilde;ng mạn, tuyệt hảo c&ugrave;ng với h&agrave;ng trăm m&oacute;n ăn hấp dẫn m&agrave; c&ograve;n c&oacute; một loại h&igrave;nh rất h&uacute;t kh&aacute;ch khi đi <strong>du lịch Ch&acirc;u &Acirc;u</strong> đ&oacute; ch&iacute;nh l&agrave; Sex Tour.</p>\r\n<p>\r\n	&gt;&gt;&gt; bạn c&oacute; thể tham khảo c&aacute;c <strong>tour du lịch ch&acirc;u &Acirc;u gi&aacute; rẻ 2017</strong> <a href="http://dulichchauau.org/">Tại đ&acirc;y</a></p>\r\n<p>\r\n	Bạn đ&atilde; sẵn s&agrave;ng c&ugrave;ng Mix Tourist kh&aacute;m ph&aacute; 4 khu phố đ&egrave;n đỏ nức danh của Ch&acirc;u &Acirc;u chưa?</p>\r\n<h2>\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">1. De Wallen, Amsterdam &ndash; H&agrave; Lan</span></span></h2>\r\n<p>\r\n	Amsterdam l&agrave; th&agrave;nh phố du lịch &ldquo;kh&eacute;t tiếng&rdquo; h&uacute;t kh&aacute;ch ở ch&acirc;u &Acirc;u kh&ocirc;ng chỉ bởi vẻ đẹp thơ mộng m&agrave; phần lớn l&agrave; do sức h&uacute;t của khu đ&egrave;n đỏ&nbsp; De Wallen, tọa lạc ngay giữa trung t&acirc;m cổ k&iacute;nh của th&agrave;nh phố.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/De-Wallen-ha-lan-mixtourist.jpg" style="width: 600px; height: 338px;" /></p>\r\n<p>\r\n	Tại đ&acirc;y bạn c&oacute; thể thấy da thịt tự nhi&ecirc;n ph&ocirc; diễn tr&ecirc;n từng con phố đồng thời bạn c&oacute; thể t&igrave;m kiếm bất k&igrave; thứ g&igrave; li&ecirc;n quan đến t&igrave;nh dục như sex toy, bao cao su hay thuốc phiện. C&oacute; rất nhiều du kh&aacute;ch khi đi <strong>du lịch H&agrave; Lan</strong> l&agrave; x&aacute;ch va li đến thẳng De Wallen.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/pho-den-do-H%C3%A0-lan-mixtourist.jpg" style="width: 600px; height: 384px;" /></p>\r\n<p>\r\n	Amsterdam l&agrave; nơi duy nhất tr&ecirc;n thế giới đ&uacute;c tượng đồng để t&ocirc;n vinh nghề mại d&acirc;m; &quot;H&atilde;y t&ocirc;n trọng những người h&agrave;nh nghề mại d&acirc;m tr&ecirc;n to&agrave;n thế giới&quot; l&agrave; d&ograve;ng chữ được khắc tr&ecirc;n bệ tượng.</p>\r\n<p>\r\n	Ở th&agrave;nh phố cổ k&iacute;nh ch&acirc;u &Acirc;u n&agrave;y, mại d&acirc;m được xem l&agrave; một nghề hợp ph&aacute;p.</p>\r\n<h2>\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">2. Reeperbahn, Hamburg - Đức</span></span></h2>\r\n<p>\r\n	Người Đức kh&aacute; cởi mở trong vấn đề t&igrave;nh dục, v&agrave; chuyện nam giới &ldquo;vui vẻ&rdquo; ở c&aacute;c khu đ&egrave;n đỏ l&agrave; điều b&igrave;nh thường v&igrave; vậy Reeperbahn l&agrave; một trong bốn khu đ&egrave;n đỏ nổi tiếng nhất tại Ch&acirc;u &Acirc;u v&agrave; được mệnh danh l&agrave; &ldquo;th&agrave;nh phố tội lỗi&rdquo;.</p>\r\n<p style="text-align: center;">\r\n	<img alt="" src="/view/admin/Themes/kcfinder/upload/images/Reeperbahn-Mixtourist.jpg" style="width: 600px; height: 400px;" /></p>\r\n<p>\r\n	V&agrave;o năm 2002, Đức đ&atilde; hợp ph&aacute;p h&oacute;a ng&agrave;nh mại d&acirc;m v&agrave; &ldquo;quy hoạch&rdquo; một khu ri&ecirc;ng ở Hamburg, l&agrave; Reeperbahn để du kh&aacute;ch gh&eacute; thăm. Đến &ldquo;th&agrave;nh phố tội lỗi&rdquo; n&agrave;y, du kh&aacute;ch c&oacute; thể t&igrave;m thấy nhiều thứ để phục vụ &ldquo;nhu cầu&rdquo; nhạy cảm của m&igrave;nh, từ những cửa h&agrave;ng sex toy, c&acirc;u lạc bộ tho&aacute;t y, đến nh&agrave; h&aacute;t t&igrave;nh dục&hellip;</p>\r\n<h2>\r\n	<span style="color:#ff0000;"><span style="font-size:16px;">3. Pigalle, Paris - Ph&aacute;p</span></span></h2>\r\n<p>\r\n	Paris, th&agrave;nh phố hoa lệ v&agrave; l&atilde;ng mạn nhất nước Ph&aacute;p. Th&agrave;nh phố n&agrave;y c&ograve;n được mệnh danh l&agrave; th&agrave;nh phố của 